import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";

const FinTrans = () => {
  const [formData, setFormData] = useState({
    PropertyCode: "",
    TenancyContractNumber: "",
    ReceiptPayment: "Receipt",
    ReceiptPaymentReason: "",
    ModeOfPayment: "",
    TrDate: "",
    TrAmount: "",
    ReferenceNumber: "",
    ChequeDate: "",
  });

  const [nextAction, setNextAction] = useState(null);
  const location = useLocation();

  // ‚úÖ Detect if FinTrans was opened from AddContract
  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const next = params.get("next");
    if (next === "add_contract") {
      setNextAction("add_contract");
      alert("üí∞ Please record the rent/deposit transaction before contract creation.");
    }
  }, [location]);

  // ‚úÖ Handle form input
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // ‚úÖ Submit Financial Transaction
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch("http://localhost:5000/add_fintrans", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      const result = await res.json();

      if (result.success) {
        alert("‚úÖ Financial Transaction recorded successfully!");

        // If called from AddContract ‚Üí automatically continue
        if (nextAction === "add_contract") {
          await createContractAfterFinTrans();
        } else {
          // Normal flow
          setFormData({
            PropertyCode: "",
            TenancyContractNumber: "",
            ReceiptPayment: "Receipt",
            ReceiptPaymentReason: "",
            ModeOfPayment: "",
            TrDate: "",
            TrAmount: "",
            ReferenceNumber: "",
            ChequeDate: "",
          });
        }
      } else {
        alert(`‚ùå ${result.message || "Failed to record transaction"}`);
      }
    } catch (err) {
      console.error("Error submitting FinTrans:", err);
      alert("‚ùå Error submitting financial transaction");
    }
  };

  // ‚úÖ Function to create contract automatically after FinTrans
  const createContractAfterFinTrans = async () => {
    const pendingContract = localStorage.getItem("pendingContract");
    if (!pendingContract) {
      alert("‚ö†Ô∏è No pending contract data found.");
      return;
    }

    const contractData = JSON.parse(pendingContract);
    const data = new FormData();
    Object.keys(contractData).forEach((key) => data.append(key, contractData[key]));

    try {
      const res = await fetch("http://localhost:5000/add_contract", {
        method: "POST",
        credentials: "include",
        body: data,
      });

      const result = await res.json();

      if (result.success) {
        alert(`‚úÖ Contract created successfully!\nüìß ${result.email_status || "Email not sent"}`);
        localStorage.removeItem("pendingContract");
        window.location.href = "/add_contract"; // redirect back
      } else {
        alert(`‚ùå Failed to create contract: ${result.message}`);
      }
    } catch (err) {
      console.error("Error creating contract:", err);
      alert("‚ùå Error creating contract after FinTrans");
    }
  };

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: "20px" }}>
      <h2 style={{ textAlign: "center", background: "#2f3640", color: "white", padding: "15px 0" }}>
        Financial Transaction Entry
      </h2>

      <form onSubmit={handleSubmit} style={{ maxWidth: "800px", margin: "auto" }}>
        <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: "15px" }}>
          <div>
            <label>Property Code</label>
            <input
              type="text"
              name="PropertyCode"
              value={formData.PropertyCode}
              onChange={handleChange}
              required
            />
          </div>

          <div>
            <label>Tenancy Contract Number</label>
            <input
              type="text"
              name="TenancyContractNumber"
              value={formData.TenancyContractNumber}
              onChange={handleChange}
            />
          </div>

          <div>
            <label>Receipt or Payment</label>
            <select
              name="ReceiptPayment"
              value={formData.ReceiptPayment}
              onChange={handleChange}
            >
              <option value="Receipt">Receipt</option>
              <option value="Payment">Payment</option>
            </select>
          </div>

          <div>
            <label>Reason</label>
            <input
              type="text"
              name="ReceiptPaymentReason"
              value={formData.ReceiptPaymentReason}
              onChange={handleChange}
              required
            />
          </div>

          <div>
            <label>Mode of Payment</label>
            <select
              name="ModeOfPayment"
              value={formData.ModeOfPayment}
              onChange={handleChange}
              required
            >
              <option value="">Select</option>
              <option value="Cash">Cash</option>
              <option value="Cheque">Cheque</option>
              <option value="Online">Online</option>
            </select>
          </div>

          <div>
            <label>Transaction Date</label>
            <input
              type="date"
              name="TrDate"
              value={formData.TrDate}
              onChange={handleChange}
              required
            />
          </div>

          <div>
            <label>Transaction Amount</label>
            <input
              type="number"
              name="TrAmount"
              value={formData.TrAmount}
              onChange={handleChange}
              required
            />
          </div>

          <div>
            <label>Reference Number</label>
            <input
              type="text"
              name="ReferenceNumber"
              value={formData.ReferenceNumber}
              onChange={handleChange}
            />
          </div>

          <div>
            <label>Cheque Date</label>
            <input
              type="date"
              name="ChequeDate"
              value={formData.ChequeDate}
              onChange={handleChange}
            />
          </div>
        </div>

        <div style={{ textAlign: "center", marginTop: "30px" }}>
          <button
            type="submit"
            style={{
              backgroundColor: "#27ae60",
              color: "white",
              border: "none",
              borderRadius: "6px",
              padding: "10px 20px",
              fontSize: "16px",
              cursor: "pointer",
            }}
          >
            Save Transaction
          </button>
        </div>
      </form>
    </div>
  );
};

export default FinTrans;
