import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

const EditServTrans = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [form, setForm] = useState({});

  useEffect(() => {
    fetch(`http://localhost:5000/get_servtrans/${id}`)
      .then((res) => res.json())
      .then((data) => {
        if (data.success) setForm(data.data);
        else alert(data.message);
      })
      .catch((err) => console.error("Error fetching record:", err));
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    Object.keys(form).forEach((key) => formData.append(key, form[key]));

    try {
      const res = await fetch(`http://localhost:5000/update_servtrans/${id}`, {
        method: "POST",
        body: formData,
      });
      const result = await res.json();
      if (result.success) {
        alert(result.message);
        navigate("/list-servtrans");
      } else {
        alert("❌ " + result.message);
      }
    } catch (err) {
      console.error("Error updating record:", err);
    }
  };

  const serviceTypes = ["PL", "PA", "CL", "DA", "EL", "OT"];
  const statuses = ["OP", "IP", "CL"];

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h2 style={{ textAlign: "center", background: "#273c75", color: "white", padding: "15px" }}>
        Edit Service Transaction #{id}
      </h2>

      <form onSubmit={handleSubmit}>
        <div style={{ display: "grid", gridTemplateColumns: "repeat(2, 1fr)", gap: "10px" }}>
          <div>
            <label>Service Type:</label>
            <select name="ServiceRequestType" value={form.ServiceRequestType || ""} onChange={handleChange}>
              {serviceTypes.map((t) => (
                <option key={t} value={t}>
                  {t}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label>Description:</label>
            <input name="ServiceRequestDescription" value={form.ServiceRequestDescription || ""} onChange={handleChange} />
          </div>

          <div>
            <label>Attended By:</label>
            <input name="ServiceRequestAttendedBy" value={form.ServiceRequestAttendedBy || ""} onChange={handleChange} />
          </div>

          <div>
            <label>Estimated Cost:</label>
            <input type="number" name="ServiceRequestEstimatedCost" value={form.ServiceRequestEstimatedCost || ""} onChange={handleChange} />
          </div>

          <div>
            <label>Actual Cost:</label>
            <input type="number" name="ServiceRequestActualCost" value={form.ServiceRequestActualCost || ""} onChange={handleChange} />
          </div>

          <div>
            <label>Status:</label>
            <select name="StatusOfServiceRequest" value={form.StatusOfServiceRequest || ""} onChange={handleChange}>
              {statuses.map((s) => (
                <option key={s} value={s}>
                  {s}
                </option>
              ))}
            </select>
          </div>
        </div>

        <div style={{ textAlign: "center", marginTop: "30px" }}>
          <button
            type="button"
            onClick={() => navigate("/list-servtrans")}
            style={{
              width: 150,
              padding: "10px",
              backgroundColor: "#718093",
              color: "white",
              border: "none",
              borderRadius: "5px",
              marginRight: "20px",
            }}
          >
            Cancel
          </button>
          <button
            type="submit"
            style={{
              width: 150,
              padding: "10px",
              backgroundColor: "#27ae60",
              color: "white",
              border: "none",
              borderRadius: "5px",
            }}
          >
            Update
          </button>
        </div>
      </form>
    </div>
  );
};

export default EditServTrans;
