import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

const ViewProperty = () => {
  const { property_code } = useParams();
  const navigate = useNavigate();
  const [property, setProperty] = useState(null);
  const [tenant, setTenant] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchPropertyDetails();
  }, [property_code]);

  const fetchPropertyDetails = async () => {
    try {
      const res = await fetch(`http://localhost:5000/view_property/${property_code}`, {
        credentials: "include",
      });
      const data = await res.json();

      if (res.ok && data.success) {
        setProperty(data.property);
        setTenant(data.tenant);
      } else {
        alert(data.message || "Failed to load property");
      }
    } catch (err) {
      console.error(err);
      alert("Error fetching property details");
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <p>Loading property details...</p>;
  if (!property) return <p>Property not found</p>;

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: 20 }}>
      <h2
        style={{
          textAlign: "center",
          background: "#2f3640",
          color: "white",
          padding: 15,
          borderRadius: 6,
        }}
      >
        Property Details
      </h2>

      <div
        style={{
          background: "#fff",
          borderRadius: 8,
          padding: 20,
          marginTop: 20,
          boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
        }}
      >
        <button
          onClick={() => navigate(-1)}
          style={{
            backgroundColor: "#7f8c8d",
            color: "white",
            border: "none",
            borderRadius: 6,
            padding: "8px 12px",
            marginBottom: 20,
            cursor: "pointer",
          }}
        >
          ← Back
        </button>

        <div style={{ display: "flex", gap: 30 }}>
          {property.property_photo && (
            <img
              src={`data:image/jpeg;base64,${property.property_photo}`}
              alt="Property"
              style={{ width: 250, height: 180, borderRadius: 8, objectFit: "cover" }}
            />
          )}

          <div style={{ flex: 1 }}>
            <h3 style={{ color: "#273c75" }}>
              {property.name || "Unnamed Property"} ({property.property_type})
            </h3>
            <p>
              <strong>Developer:</strong> {property.developer_name}
            </p>
            <p>
              <strong>Building:</strong> {property.building_name}
            </p>
            <p>
              <strong>Address:</strong> {property.address1}, {property.city},{" "}
              {property.state}, {property.country}
            </p>
            <p>
              <strong>Bedrooms:</strong> {property.bedrooms} |{" "}
              <strong>Bathrooms:</strong> {property.bathrooms} |{" "}
              <strong>Car Parkings:</strong> {property.car_parkings}
            </p>
            <p>
              <strong>Ownership:</strong> {property.ownership_type} |{" "}
              <strong>Furnishing:</strong> {property.furnishing_status}
            </p>
          </div>
        </div>

        <hr style={{ margin: "20px 0" }} />

        {tenant ? (
          <>
            <h3 style={{ color: "#27ae60" }}>Current Tenant</h3>
            <p>
              <strong>Name:</strong> {tenant.TenantName}
            </p>
            <p>
              <strong>Email:</strong> {tenant.TenantEmailID}
            </p>
            <p>
              <strong>Mobile:</strong> {tenant.TenantMobileNumber}
            </p>
            <p>
              <strong>Employer:</strong> {tenant.TenantEmployer}
            </p>
            <p>
              <strong>Lease:</strong> {tenant.LeaseStartDate?.slice(0, 10)} →{" "}
              {tenant.LeaseEndDate?.slice(0, 10)}
            </p>
            <p>
              <strong>Rent:</strong> {tenant.RentAmount}
            </p>
          </>
        ) : (
          <p style={{ color: "gray" }}>No tenant currently linked.</p>
        )}
      </div>
    </div>
  );
};

export default ViewProperty;
