import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const AddFinTrans = () => {
  const [contractId, setContractId] = useState("");
  const [contractDetails, setContractDetails] = useState(null);
  const [formData, setFormData] = useState({
    contract_id: "",
    property_code: "",
    tenant_id: "",
    tenant_name: "",
    receipt_payment: "Receipt",
    receipt_payment_reason: "",
    mode_of_payment: "Cash",
    tr_date: "",
    tr_amount: "",
    reference_number: "",
    cheque_number: "",
    cheque_date: "",
    bank_name: "",
  });

  const navigate = useNavigate();

  // üîπ Fetch contract details by Contract ID
  const handleSearch = async () => {
    if (!contractId) {
      alert("Please enter a Contract ID");
      return;
    }

    try {
      const res = await fetch(`http://localhost:5000/api/contract/${contractId}`);
      if (!res.ok) throw new Error("Contract not found");
      const data = await res.json();

      setContractDetails(data);
      setFormData((prev) => ({
        ...prev,
        contract_id: data.contract_id,
        property_code: data.property_id,
        tenant_id: data.tenant_id,
        tenant_name: data.tenant_name,
      }));
    } catch (error) {
      console.error("Error fetching contract:", error);
      alert("‚ùå Contract not found. Please check the Contract ID.");
    }
  };

  // üîπ Handle field updates
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // üîπ Save Financial Transaction
  const handleSubmit = async (e) => {
    e.preventDefault();

    const {
      mode_of_payment,
      tr_date,
      tr_amount,
      cheque_number,
      cheque_date,
      bank_name,
      reference_number,
    } = formData;

    // ‚úÖ Conditional validation
    if (!tr_date || !tr_amount) {
      alert("Please fill transaction date and amount.");
      return;
    }

    let payload = {
      contract_id: formData.contract_id,
      property_code: formData.property_code,
      tenant_id: formData.tenant_id,
      tenant_name: formData.tenant_name,
      receipt_payment: formData.receipt_payment,
      receipt_payment_reason: formData.receipt_payment_reason,
      mode_of_payment,
      tr_date,
      tr_amount,
    };

    // üü¢ Mode-based conditional data
    if (mode_of_payment === "Cheque") {
      payload = {
        ...payload,
        cheque_number,
        cheque_date,
        bank_name,
      };
    } else if (mode_of_payment === "Online") {
      payload = {
        ...payload,
        reference_number,
      };
    }

    try {
      const res = await fetch("http://localhost:5000/api/fintrans", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });

      if (res.ok) {
        alert("‚úÖ Financial transaction saved successfully!");
        navigate("/addcontract");
      } else {
        alert("‚ùå Failed to save financial transaction.");
      }
    } catch (err) {
      console.error("Error saving FinTrans:", err);
      alert("‚ùå Error saving data.");
    }
  };

  return (
    <div className="fintrans-container" style={{ padding: "20px" }}>
      <h2>üßæ Financial Transaction Entry</h2>

      {/* Search Section */}
      <div style={{ marginBottom: "20px" }}>
        <label>Contract ID:</label>
        <input
          type="text"
          value={contractId}
          onChange={(e) => setContractId(e.target.value)}
          placeholder="Enter Contract ID"
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      {/* Form Section */}
      {contractDetails && (
        <form onSubmit={handleSubmit} style={{ display: "grid", gap: "10px" }}>
          <label>Property Code:</label>
          <input name="property_code" value={formData.property_code} readOnly />

          <label>Tenant ID:</label>
          <input name="tenant_id" value={formData.tenant_id} readOnly />

          <label>Tenant Name:</label>
          <input name="tenant_name" value={formData.tenant_name} readOnly />

          <label>Receipt / Payment:</label>
          <select name="receipt_payment" value={formData.receipt_payment} onChange={handleChange}>
            <option value="Receipt">Receipt</option>
            <option value="Payment">Payment</option>
          </select>

          <label>Reason:</label>
          <input
            type="text"
            name="receipt_payment_reason"
            value={formData.receipt_payment_reason}
            onChange={handleChange}
          />

          <label>Mode of Payment:</label>
          <select
            name="mode_of_payment"
            value={formData.mode_of_payment}
            onChange={handleChange}
          >
            <option value="Cash">Cash</option>
            <option value="Cheque">Cheque</option>
            <option value="Online">Online</option>
          </select>

          <label>Transaction Date:</label>
          <input type="date" name="tr_date" value={formData.tr_date} onChange={handleChange} />

          <label>Transaction Amount:</label>
          <input
            type="number"
            name="tr_amount"
            value={formData.tr_amount}
            onChange={handleChange}
          />

          {/* Conditional Fields */}
          {formData.mode_of_payment === "Cheque" && (
            <>
              <label>Cheque Number:</label>
              <input
                type="text"
                name="cheque_number"
                value={formData.cheque_number}
                onChange={handleChange}
              />
              <label>Cheque Date:</label>
              <input
                type="date"
                name="cheque_date"
                value={formData.cheque_date}
                onChange={handleChange}
              />
              <label>Bank Name:</label>
              <input
                type="text"
                name="bank_name"
                value={formData.bank_name}
                onChange={handleChange}
              />
            </>
          )}

          {formData.mode_of_payment === "Online" && (
            <>
              <label>Reference Number:</label>
              <input
                type="text"
                name="reference_number"
                value={formData.reference_number}
                onChange={handleChange}
              />
            </>
          )}

          <div style={{ marginTop: "10px" }}>
            <button type="submit">üíæ Save</button>
            <button
              type="button"
              onClick={() => navigate("/addtrans")}
              style={{ marginLeft: "10px" }}
            >
              ‚¨ÖÔ∏è Back
            </button>
          </div>
        </form>
      )}
    </div>
  );
};

export default AddFinTrans;
