import React, { useEffect, useState } from "react";

const ListProperty = () => {
  const [properties, setProperties] = useState([]);
  const [currentDate, setCurrentDate] = useState("");

  useEffect(() => {
    fetchProperties();
    setCurrentDate(new Date().toLocaleDateString());
  }, []);

  const fetchProperties = async () => {
    try {
      const response = await fetch("http://localhost:5000/get_properties"); // <-- change this to your backend endpoint
      const data = await response.json();
      setProperties(data);
    } catch (error) {
      console.error("Error fetching properties:", error);
    }
  };

  const sortTable = (n) => {
    const sorted = [...properties].sort((a, b) => {
      const valA = Object.values(a)[n] || "";
      const valB = Object.values(b)[n] || "";
      return valA > valB ? 1 : -1;
    });
    setProperties(sorted);
  };

  return (
    <div style={{ fontFamily: "Arial, sans-serif", background: "#f5f6fa", padding: "20px" }}>
      <h2 style={{ textAlign: "center", background: "#2f3640", color: "white", padding: "15px 0" }}>
        All Properties
      </h2>

      <div className="header-row" style={{ display: "flex", justifyContent: "space-between", margin: "15px" }}>
        <div>
          <span>{currentDate}</span> | <span>Total Properties: {properties.length}</span>
        </div>
        <div>
          <a href="/index" style={{ textDecoration: "none", color: "#333" }}>‚Üê Go Back Home</a>
        </div>
      </div>

      <div className="table-container" style={{ overflowX: "auto", background: "white", padding: "20px", borderRadius: "10px" }}>
        <table style={{ width: "100%", borderCollapse: "collapse" }}>
          <thead>
            <tr>
              {[
                "Property Code", "Primary Owner", "Developer Name", "Building Name", "Property Type",
                "Name", "Address 1", "Address 2", "City", "State", "Country", "Zip Code",
                "Municipality No", "Electricity Bill No", "Water Bill No", "Ownership Type",
                "Furnishing Status", "Land Area", "Carpet Area", "Built-up Area", "Bedrooms",
                "Bathrooms", "Washrooms", "Facing", "Car Parkings", "Servant Rooms", "Balconies",
                "Has Dishwasher", "Has Washing Machine", "Property Value", "Year of Construction",
                "Second Owner", "Expected Rent Value", "Status", "MTD Income", "YTD Income",
                "MTD Expense", "YTD Expense", "Purchase Value", "Construction Cost", "Renovation Cost",
                "Registration", "Transfer Fees", "Misc Expense", "Created By", "Creation Date", "Photo"
              ].map((col, index) => (
                <th
                  key={index}
                  onClick={() => sortTable(index)}
                  style={{ cursor: "pointer", backgroundColor: "#2f3640", color: "white", padding: "8px" }}
                >
                  {col}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {properties.map((property, idx) => (
              <tr key={idx} style={{ backgroundColor: idx % 2 === 0 ? "#f0f0f0" : "white" }}>
                {Object.keys(property).map((key, i) => (
                  key === "property_photo" ? (
                    <td key={i}>
                      {property[key] ? (
                        <img src={`data:image/jpeg;base64,${property[key]}`} style={{ maxWidth: "30px", borderRadius: "6px" }} alt="property" />
                      ) : "No Photo"}
                    </td>
                  ) : (
                    <td key={i}>{property[key]}</td>
                  )
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ListProperty;
