import React, { useState, useEffect } from "react";

const OwnerList = ({ apiUrl, deleteUrl }) => {
  const [owners, setOwners] = useState([]);
  const [sortField, setSortField] = useState("");
  const [sortOrder, setSortOrder] = useState("asc");
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState("");

  useEffect(() => {
    fetchOwners();
  }, [sortField, sortOrder]);

  const fetchOwners = async () => {
    try {
      setLoading(true);
      const url = `${apiUrl}?sort=${sortField}&order=${sortOrder}`;
      const res = await fetch(url, { credentials: "include" });
      const data = await res.json();
      setOwners(data.owners || []);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleSort = (field) => {
    if (sortField === field) {
      setSortOrder(sortOrder === "asc" ? "desc" : "asc");
    } else {
      setSortField(field);
      setSortOrder("asc");
    }
  };

  const handleDelete = async (owner_code) => {
    if (!window.confirm(`Are you sure you want to delete owner ${owner_code}?`)) return;

    try {
      const res = await fetch(deleteUrl, {
        method: "POST",
        credentials: "include",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ owner_code }),
      });
      const data = await res.json();
      if (res.ok && data.success) {
        setMessage(data.message);
        fetchOwners(); // refresh the list
      } else {
        setMessage(data.message || "❌ Error deleting owner");
      }
    } catch (err) {
      console.error(err);
      setMessage("❌ Error deleting owner");
    }
  };

  const currentDate = new Date().toLocaleDateString();

  return (
    <div className="container" style={{ maxWidth: "1600px", margin: "auto", overflowX: "auto" }}>
      <div className="header-row" style={{ display: "flex", alignItems: "center", justifyContent: "space-between", marginBottom: "15px", flexWrap: "wrap" }}>
        <div className="header-left" style={{ display: "flex", gap: "10px", fontWeight: "bold" }}>
          <span>{currentDate}</span> | <span>Total Owners: {owners.length}</span>
        </div>
        <h2 style={{ margin: "0 auto", textAlign: "center", color: "black" }}>List of Owners</h2>
        <div className="header-right" style={{ fontWeight: "bold" }}>
          <a href="/index" style={{ textDecoration: "none", color: "#333" }}>← Go Back Home</a>
        </div>
      </div>

      {message && <p style={{ textAlign: "center", fontWeight: "bold", color: message.includes("✅") ? "green" : "red" }}>{message}</p>}

      {loading ? (
        <p style={{ textAlign: "center" }}>Loading...</p>
      ) : (
        <table style={{ width: "100%", borderCollapse: "collapse", background: "white", borderRadius: "8px", overflow: "hidden" }}>
          <thead>
            <tr>
              {owners.length > 0 &&
                Object.keys(owners[0]).map((col) => (
                  <th
                    key={col}
                    style={{ padding: "10px 12px", borderBottom: "1px solid #ddd", textAlign: "left", fontSize: "0.9rem", background: "#007BFF", color: "white", cursor: "pointer" }}
                    onClick={() => handleSort(col)}
                  >
                    {col.replace("_", " ").replace(/\b\w/g, (c) => c.toUpperCase())}{" "}
                    {sortField === col ? `(${sortOrder})` : ""}
                  </th>
                ))}
              <th style={{ padding: "10px 12px", background: "#007BFF", color: "white" }}>Actions</th>
            </tr>
          </thead>
          <tbody>
            {owners.map((owner) => (
              <tr key={owner.owner_code} style={{ background: "#fff" }}>
                {Object.entries(owner).map(([key, val]) => {
                  if (["eid_image", "passport_copy", "res_visa"].includes(key)) {
                    return (
                      <td key={key} style={{ padding: "10px 12px" }}>
                        {val ? (
                          <img
                            src={`data:image/jpeg;base64,${val}`}
                            alt={key}
                            style={{ width: "30px", height: "30px", objectFit: "cover", borderRadius: "4px", border: "1px solid #ccc" }}
                          />
                        ) : "N/A"}
                      </td>
                    );
                  } else {
                    return <td key={key} style={{ padding: "10px 12px" }}>{val}</td>;
                  }
                })}
                <td style={{ padding: "10px 12px" }}>
                  <button
                    onClick={() => handleDelete(owner.owner_code)}
                    style={{ padding: "4px 8px", background: "#c0392b", color: "#fff", border: "none", borderRadius: "4px", cursor: "pointer" }}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default OwnerList;
