import React, { useEffect, useState } from "react";

const ListProperty = () => {
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");

  // Preview modal
  const [previewSrc, setPreviewSrc] = useState(null);

  // Edit modal
  const [editingProperty, setEditingProperty] = useState(null);
  const [editForm, setEditForm] = useState({});
  const [editFiles, setEditFiles] = useState({});
  const [editLoading, setEditLoading] = useState(false);

  // MySQL column order
  const columnOrder = [
    "property_code",
    "primary_owner_code",
    "developer_name",
    "building_name",
    "property_type",
    "name",
    "address1",
    "address2",
    "city",
    "state",
    "country",
    "zip_code",
    "municipality_number",
    "electricity_bill_number",
    "water_bill_number",
    "ownership_type",
    "furnishing_status",
    "land_area",
    "carpet_area",
    "builtup_area",
    "bedrooms",
    "bathrooms",
    "washrooms",
    "facing",
    "car_parkings",
    "servant_rooms",
    "balconies",
    "has_dishwasher",
    "has_washing_machine",
    "property_value",
    "year_of_construction",
    "second_owner_code",
    "expected_rent_value",
    "status",
    "mtd_income",
    "ytd_income",
    "mtd_expense",
    "ytd_expense",
    "purchase_value",
    "construction_cost",
    "renovation_cost",
    "registration",
    "transfer_fees",
    "misc_expense",
    "created_by",
    "creation_date",
    "property_photo",
  ];

  const photoFields = ["property_photo"];
  const dateFields = ["creation_date"];

  // Map DB columns -> backend form keys (example)
  const dbToFormMap = {
    property_code: "property_code",
    name: "name",
    address1: "address1",
    address2: "address2",
    city: "city",
    state: "state",
    country: "country",
    zip_code: "zip_code",
    created_by: "created_by",
    creation_date: "creation_date",
  };

  useEffect(() => {
    fetchProperties();
  }, []);

  async function fetchProperties() {
    try {
      setLoading(true);
      const res = await fetch("http://localhost:5000/list_property", {
        method: "GET",
        credentials: "include",
      });
      const data = await res.json();
      if (res.ok && data.success) {
        setProperties(data.properties || []);
      } else {
        alert("Error fetching properties");
      }
    } catch (err) {
      console.error(err);
      alert("Error fetching properties");
    } finally {
      setLoading(false);
    }
  }

  const filtered = properties.filter((p) => {
    const q = searchTerm.trim().toLowerCase();
    if (!q) return true;
    return (
      (p.property_code || "").toString().toLowerCase().includes(q) ||
      (p.name || "").toString().toLowerCase().includes(q)
    );
  });

  const openEditModal = (prop) => {
    setEditingProperty(prop);
    const form = {};
    Object.entries(dbToFormMap).forEach(([dbCol, formKey]) => {
      form[formKey] = prop[dbCol] ? (prop[dbCol].slice ? prop[dbCol].slice(0, 10) : prop[dbCol]) : "";
    });
    setEditForm(form);
    setEditFiles({});
  };

  const closeEditModal = () => {
    setEditingProperty(null);
    setEditForm({});
    setEditFiles({});
  };

  const onEditChange = (e) => {
    const { name, value } = e.target;
    setEditForm((p) => ({ ...p, [name]: value }));
  };

  const onEditFileChange = (e, key) => {
    const file = e.target.files[0];
    if (!file) return;
    setEditFiles((p) => ({ ...p, [key]: file }));
  };

  const submitEdit = async (e) => {
    e.preventDefault();
    if (!editingProperty) return;
    try {
      setEditLoading(true);
      const data = new FormData();
      Object.entries(editForm).forEach(([k, v]) => data.append(k, v || ""));
      photoFields.forEach((pf) => {
        if (editFiles[pf]) data.append(pf, editFiles[pf], editFiles[pf].name);
      });

      const res = await fetch("http://localhost:5000/property_update", {
        method: "POST",
        credentials: "include",
        body: data,
      });
      const result = await res.json();
      if (res.ok && result.success) {
        alert(result.message || "Property updated");
        await fetchProperties();
        closeEditModal();
      } else {
        alert(result.message || "Failed to update property");
      }
    } catch (err) {
      console.error(err);
      alert("Error updating property");
    } finally {
      setEditLoading(false);
    }
  };

  const handleDeleteApi = async (property_code) => {
    if (!window.confirm(`Confirm deletion of property ${property_code}?`)) return;
    try {
      setLoading(true);
      const res = await fetch(`http://localhost:5000/property_delete_api/${property_code}`, {
        method: "DELETE",
        credentials: "include",
      });
      const result = await res.json();
      if (res.ok && result.success) {
        alert(result.message || "Deleted successfully");
        setProperties((prev) => prev.filter((p) => p.property_code !== property_code));
      } else {
        alert(result.message || "Failed to delete");
      }
    } catch (err) {
      console.error(err);
      alert("Error deleting property");
    } finally {
      setLoading(false);
    }
  };

  const renderThumb = (p, key) => {
    const val = p[key];
    if (!val) return <div style={{ fontSize: 12, color: "#666" }}>‚Äî</div>;
    const src = val.startsWith("data:") ? val : `data:image/jpeg;base64,${val}`;
    return (
      <img
        src={src}
        alt={key}
        style={{ width: 48, height: 48, objectFit: "cover", borderRadius: 4, border: "1px solid #ccc", cursor: "pointer" }}
        onClick={() => setPreviewSrc(src)}
      />
    );
  };

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: 20, background: "#f0f2f5" }}>
      <h2 style={{ textAlign: "center", background: "#2f3640", color: "white", padding: 15, margin: 0 }}>Property List</h2>

      <div style={{ maxWidth: 1500, margin: "20px auto", background: "white", padding: 16, borderRadius: 8, boxShadow: "0 0 12px rgba(0,0,0,0.12)", overflowX: "auto" }}>
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: 12 }}>
          <div style={{ fontWeight: "bold" }}>{new Date().toLocaleDateString()} | Total Properties: {properties.length}</div>
          <div style={{ display: "flex", gap: 8, alignItems: "center" }}>
            <input
              placeholder="Search by code or name"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              style={{ padding: 8, borderRadius: 6, border: "1px solid #ccc", width: 320 }}
            />
            <button onClick={() => fetchProperties()} style={{ padding: "8px 12px", borderRadius: 6, cursor: "pointer" }}>Refresh</button>
          </div>
        </div>

        {loading ? (
          <p>Loading...</p>
        ) : (
          <table style={{ width: "100%", borderCollapse: "collapse" }}>
            <thead>
              <tr>
                {columnOrder.map((col) => (
                  <th key={col} style={{ padding: 8, border: "1px solid #ddd", background: "#273c75", color: "#fff", fontSize: 12 }}>
                    {col}
                  </th>
                ))}
                <th style={{ padding: 8, border: "1px solid #ddd", background: "#273c75", color: "#fff", fontSize: 12 }}>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filtered.map((p, idx) => (
                <tr key={p.property_code || idx} style={{ background: idx % 2 === 0 ? "#fafafa" : "#fff" }}>
                  {columnOrder.map((key) => (
                    <td key={key} style={{ padding: 6, border: "1px solid #eee", textAlign: "center", fontSize: 13 }}>
                      {photoFields.includes(key) ? renderThumb(p, key) : dateFields.includes(key) && p[key] ? p[key].slice(0, 10) : p[key] ?? ""}
                    </td>
                  ))}
                  <td style={{ padding: 8, border: "1px solid #eee", display: "flex", gap: 8, justifyContent: "center" }}>
                    <button onClick={() => openEditModal(p)} style={{ padding: "6px 10px", borderRadius: 6, cursor: "pointer" }}>‚úèÔ∏è Edit</button>
                    <button onClick={() => handleDeleteApi(p.property_code)} style={{ padding: "6px 10px", borderRadius: 6, background: "#c0392b", color: "#fff", cursor: "pointer" }}>üóëÔ∏è Delete</button>
                  </td>
                </tr>
              ))}
              {filtered.length === 0 && (
                <tr>
                  <td colSpan={columnOrder.length + 1} style={{ padding: 16, textAlign: "center" }}>No properties found</td>
                </tr>
              )}
            </tbody>
          </table>
        )}
      </div>

      {/* Preview modal */}
      {previewSrc && (
        <div onClick={() => setPreviewSrc(null)} style={{ position: "fixed", inset: 0, backgroundColor: "rgba(0,0,0,0.8)", display: "flex", alignItems: "center", justifyContent: "center", zIndex: 2000 }}>
          <img src={previewSrc} alt="preview" style={{ maxWidth: "90%", maxHeight: "90%", borderRadius: 6 }} />
        </div>
      )}

      {/* Edit modal */}
      {editingProperty && (
        <div style={{ position: "fixed", inset: 0, backgroundColor: "rgba(0,0,0,0.5)", display: "flex", alignItems: "center", justifyContent: "center", zIndex: 2000 }}>
          <div style={{ width: 900, maxHeight: "90vh", overflowY: "auto", background: "#fff", borderRadius: 8, padding: 16 }}>
            <h3 style={{ marginTop: 0 }}>Edit Property ‚Äî {editingProperty.property_code}</h3>
            <form onSubmit={submitEdit}>
              <div style={{ display: "grid", gridTemplateColumns: "repeat(3, 1fr)", gap: 12 }}>
                {Object.entries(dbToFormMap).map(([dbCol, formKey]) => (
                  <div key={formKey}>
                    <label style={{ display: "block", fontWeight: "bold", marginBottom: 6 }}>{dbCol.replace(/_/g, " ").toUpperCase()}</label>
                    <input type="text" name={formKey} value={editForm[formKey] || ""} onChange={onEditChange} style={{ width: "100%", padding: 8, borderRadius: 6, border: "1px solid #ccc" }} />
                  </div>
                ))}
              </div>
              <hr style={{ margin: "16px 0" }} />
              <div style={{ display: "grid", gridTemplateColumns: "repeat(4,1fr)", gap: 12 }}>
                {photoFields.map((pf) => (
                  <div key={pf} style={{ textAlign: "center" }}>
                    <label style={{ display: "block", fontWeight: "bold", marginBottom: 6 }}>{pf}</label>
                    {editingProperty[pf] ? (
                      <img src={editingProperty[pf].startsWith("data:") ? editingProperty[pf] : `data:image/jpeg;base64,${editingProperty[pf]}`} alt={pf} style={{ width: 80, height: 80, objectFit: "cover", borderRadius: 6, border: "1px solid #ccc", display: "block", margin: "0 auto 8px" }} />
                    ) : (
                      <div style={{ width: 80, height: 80, background: "#f3f3f3", borderRadius: 6, display: "inline-block", marginBottom: 8 }} />
                    )}
                    <input type="file" name={pf} onChange={(e) => onEditFileChange(e, pf)} />
                  </div>
                ))}
              </div>
              <div style={{ display: "flex", gap: 8, justifyContent: "flex-end", marginTop: 16 }}>
                <button type="button" onClick={closeEditModal} style={{ padding: "8px 12px", borderRadius: 6 }}>Cancel</button>
                <button type="submit" disabled={editLoading} style={{ padding: "8px 12px", borderRadius: 6, background: "#2ecc71", color: "#fff" }}>{editLoading ? "Saving..." : "Save Changes"}</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default ListProperty;
