import React, { useEffect, useState } from "react";

const ListProperty = () => {
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    fetchProperties();
  }, []);

  const fetchProperties = async () => {
    try {
      setLoading(true);
      const res = await fetch("http://localhost:5000/list_property", {
        method: "GET",
        credentials: "include",
      });
      const data = await res.json();
      if (res.ok && data.success) {
        setProperties(data.properties);
      } else {
        alert(data.message || "Failed to fetch properties");
      }
    } catch (err) {
      console.error(err);
      alert("Error fetching properties");
    } finally {
      setLoading(false);
    }
  };

  const handleSort = (colIndex) => {
    const sorted = [...properties].sort((a, b) => {
      const key = Object.keys(a)[colIndex];
      const valA = a[key] ?? "";
      const valB = b[key] ?? "";
      if (!isNaN(valA) && !isNaN(valB)) return valA - valB;
      return valA.toString().localeCompare(valB.toString());
    });
    setProperties(sorted);
  };

  const handleDelete = async (propertyCode) => {
    if (!window.confirm(`Are you sure you want to delete Property: ${propertyCode}?`)) return;

    try {
      const res = await fetch("http://localhost:5000/delete_property", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ property_code: propertyCode }),
      });
      const data = await res.json();
      if (res.ok && data.success) {
        alert(data.message);
        fetchProperties();
      } else {
        alert(data.message || "Failed to delete property");
      }
    } catch (err) {
      console.error(err);
      alert("Error deleting property");
    }
  };

const filteredProperties = properties.filter(
  (p) =>
    String(p.property_code ?? "").toLowerCase().includes(searchTerm.toLowerCase()) ||
    String(p.primary_owner_code ?? "").toLowerCase().includes(searchTerm.toLowerCase())
);

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: 20, background: "#f5f6fa" }}>
      <h2 style={{ textAlign: "center", background: "#2f3640", color: "white", padding: 15, margin: 0 }}>
        Property List
      </h2>

      <div style={{ display: "flex", justifyContent: "space-between", margin: "15px 0" }}>
        <div>
          <span>{new Date().toLocaleDateString()}</span> | <span>Total Properties: {filteredProperties.length}</span>
        </div>
        <div>
          <a href="/index" style={{ textDecoration: "none", fontWeight: "bold", color: "#333" }}>‚Üê Go Back Home</a>
        </div>
      </div>

      {/* Search */}
      <div style={{ marginBottom: 15 }}>
        <input
          type="text"
          placeholder="Search by Property Code or Owner"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ padding: 6, borderRadius: 5, border: "1px solid #aaa", width: 250 }}
        />
      </div>

      {loading ? (
        <p>Loading properties...</p>
      ) : (
        <div style={{ overflowX: "auto", background: "white", padding: 15, borderRadius: 10, boxShadow: "0 0 10px #ccc" }}>
          <table style={{ width: "100%", borderCollapse: "collapse" }}>
            <thead>
              <tr>
                {properties[0] && Object.keys(properties[0]).map((col, idx) => (
                  <th
                    key={idx}
                    onClick={() => handleSort(idx)}
                    style={{ padding: 5, border: "1px solid #ccc", background: "#273c75", color: "white", cursor: "pointer" }}
                  >
                    {col.replace(/_/g, " ").replace(/\b\w/g, (ch) => ch.toUpperCase())}
                  </th>
                ))}
                <th style={{ padding: 8, border: "1px solid #ccc", background: "#273c75", color: "white" }}>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredProperties.map((p, idx) => (
                <tr key={idx} style={{ backgroundColor: idx % 2 === 0 ? "#f2f2f2" : "#fff" }}>
                  {Object.keys(p).map((key) => (
                    <td key={key} style={{ padding: 8, border: "1px solid #ccc", textAlign: "center" }}>
                      {key === "property_photo" && p[key] ? (
                        <img src={`data:image/jpeg;base64,${p[key]}`} alt={key} style={{ width: 60, height: 60, borderRadius: 4 }} />
                      ) : (
                        p[key]
                      )}
                    </td>
                  ))}
                  <td style={{ textAlign: "center" }}>
                    <button
                      onClick={() => window.location.href = `/edit_property/${p.property_code}`}
                      style={{ marginRight: 5, padding: "5px 10px", background: "#3498db", color: "white", border: "none", borderRadius: 5, cursor: "pointer" }}
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDelete(p.property_code)}
                      style={{ padding: "5px 10px", background: "#c0392b", color: "white", border: "none", borderRadius: 5, cursor: "pointer" }}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default ListProperty;
