import React, { useState } from "react";

const ModifyTenant = ({ sessionUser }) => {
  const [tenancyId, setTenancyId] = useState("");
  const [tenant, setTenant] = useState(null);
  const [formData, setFormData] = useState({});
  const [previews, setPreviews] = useState({}); // for selected files or existing base64 previews
  const [loading, setLoading] = useState(false);

  // Photo fields from your template
  const photoFields = [
    "PassportCopyPath",
    "EIDCopyPath",
    "ResidenceVisaCopyPath",
    "BankStatementCopyPath",
    "DepositChequeCopyPath",
    "SecurityChequeCopyPath",
    "EjariMunicipalRegistrationCopyPath",
  ];

  // ------------------- Search Tenant -------------------
  const handleSearch = async (e) => {
    e && e.preventDefault();
    if (!tenancyId) return alert("Please enter a tenancy ID");

    try {
      setLoading(true);
      const res = await fetch(`http://localhost:5000/tenants/${encodeURIComponent(tenancyId)}`, {
        method: "GET",
        credentials: "include",
      });
      const data = await res.json();
      if (res.ok && data.success) {
        setTenant(data.tenant);

        // Initialize formData with fetched tenant values (non-file fields)
        const initial = {};
        Object.keys(data.tenant).forEach((k) => {
          // we won't store raw base64 big blobs in formData; keep file inputs separate
          if (!photoFields.includes(k)) {
            initial[k] = data.tenant[k];
          }
        });

        // Provide readable date values (YYYY-MM-DD) for date inputs if present
        ["TenantDOB","TenantPassportExpiryDate","TenantEIDExpiryDate","LeaseStartDate","LeaseEndDate","MoveInDate","MoveOutDate","CreationDate"]
          .forEach((dfield) => {
            if (data.tenant[dfield]) {
              // try to coerce to yyyy-mm-dd if it's ISO-like
              initial[dfield] = data.tenant[dfield].slice(0,10);
            }
          });

        setFormData(initial);

        // Setup previews from base64 tenant fields if available
        const p = {};
        photoFields.forEach((field) => {
          if (data.tenant[field]) {
            // assume tenant[field] is base64 JPEG/PNG (template used data:image/jpeg;base64,{{ ... }})
            // If backend returns raw base64 without prefix, add an image mime guess.
            // If already prefixed with 'data:image', use it as-is.
            const val = data.tenant[field];
            p[field] = val.startsWith("data:") ? val : `data:image/jpeg;base64,${val}`;
          } else {
            p[field] = null;
          }
        });
        setPreviews(p);
      } else {
        alert(data.message || "Tenant not found");
        setTenant(null);
        setFormData({});
        setPreviews({});
      }
    } catch (err) {
      console.error(err);
      alert("Error fetching tenant");
    } finally {
      setLoading(false);
    }
  };

  // ------------------- Handle regular input change -------------------
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // ------------------- Handle file selection & previews -------------------
  const handleFileChange = (e, key) => {
    const file = e.target.files[0];
    if (!file) return;

    // store File object for upload
    setFormData((prev) => ({ ...prev, [key]: file }));

    // preview image if image
    if (file.type && file.type.startsWith("image/")) {
      const reader = new FileReader();
      reader.onload = (ev) => {
        setPreviews((prev) => ({ ...prev, [key]: ev.target.result }));
      };
      reader.readAsDataURL(file);
    } else {
      setPreviews((prev) => ({ ...prev, [key]: null }));
    }
  };

  // ------------------- Submit Update -------------------
  const handleUpdate = async (e) => {
    e.preventDefault();
    if (!tenant) return alert("Please search and load a tenant first");

    const data = new FormData();
    // always include tenancy contract number (readonly in UI)
    data.append("tenancy_contract_number", tenant.TenancyContractNumber || formData.tenancy_contract_number || tenancyId);

    // append all fields from formData (text/date) and uploaded files
    Object.keys(formData).forEach((key) => {
      const val = formData[key];
      if (val instanceof File) {
        data.append(key, val, val.name);
      } else if (val !== undefined && val !== null) {
        data.append(key, val);
      }
    });

    // ensure created_by / creation_date if absent (mirrors your add example)
    if (!formData.created_by) data.append("created_by", sessionUser || "admin");
    if (!formData.creation_date) data.append("creation_date", new Date().toISOString().split("T")[0]);

    try {
      setLoading(true);
      const res = await fetch("http://localhost:5000/update_tenant", {
        method: "POST",
        credentials: "include",
        body: data,
      });
      const result = await res.json();
      if (res.ok && result.success) {
        alert(`✅ ${result.message}`);
        // refresh tenant data from server to reflect changes (best-effort)
        await handleSearch();
      } else {
        alert(`❌ ${result.message || "Failed to update tenant"}`);
      }
    } catch (err) {
      console.error(err);
      alert("❌ Error updating tenant");
    } finally {
      setLoading(false);
    }
  };

  // Converts a field name to a friendly label
  const labelize = (s) => {
    return s.replace(/([A-Z])/g, " $1").replace(/_/g, " ").replace(/\b\w/g, (ch) => ch.toUpperCase()).trim();
  };

  // Fields layout mirroring the template structure (5 columns rows)
  const rows = [
    // row 1
    ["tenancy_contract_number", "tenant_name", "tenant_dob", "tenant_nationality", "tenant_passport"],
    // row 2
    ["tenant_eid", "passport_expiry", "eid_expiry", "tenant_employer", "tenant_mobile"],
    // row 3
    ["tenant_email", "lease_start", "lease_end", "move_in", "move_out"],
    // row 4
    ["rent_amount", "deposit_amount", "number_of_payments", "rent_received", "rent_outstanding"],
    // row 5
    ["created_by", "creation_date"]
  ];

  // Helper to get controlled value: prefer formData, fall back to tenant fields (keys as in template)
  const getValue = (key) => {
    if (formData[key] !== undefined) return formData[key];
    if (!tenant) return "";
    // Map UI name to tenant object properties where names differ
    const map = {
      tenancy_contract_number: "TenancyContractNumber",
      tenant_name: "TenantName",
      tenant_dob: "TenantDOB",
      tenant_nationality: "TenantNationality",
      tenant_passport: "TenantPassportNumber",
      tenant_eid: "TenantEmiratesID",
      passport_expiry: "TenantPassportExpiryDate",
      eid_expiry: "TenantEIDExpiryDate",
      tenant_employer: "TenantEmployer",
      tenant_mobile: "TenantMobileNumber",
      tenant_email: "TenantEmailID",
      lease_start: "LeaseStartDate",
      lease_end: "LeaseEndDate",
      move_in: "MoveInDate",
      move_out: "MoveOutDate",
      rent_amount: "RentAmount",
      deposit_amount: "DepositAmountReceived",
      number_of_payments: "NumberOfPayments",
      rent_received: "RentAmountReceived",
      rent_outstanding: "RentAmountOutstanding",
      created_by: "CreatedBy",
      creation_date: "CreationDate",
    };
    const tkey = map[key] || key;
    const val = tenant[tkey];
    if (!val && (key === "created_by" || key === "creation_date") && sessionUser) {
      return key === "created_by" ? sessionUser : new Date().toISOString().slice(0,10);
    }
    // coerce dates to yyyy-mm-dd if ISO-like
    if (val && typeof val === "string" && /\d{4}-\d{2}-\d{2}/.test(val)) return val.slice(0,10);
    return val ?? "";
  };

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: "20px" }}>
      <h2 style={{ textAlign: "center", background: "#2f3640", color: "white", padding: "15px 0", margin: 0 }}>
        Modify Tenant
      </h2>

      {/* Search Tenancy */}
      <form onSubmit={handleSearch} style={{ textAlign: "center", margin: "20px 0" }}>
        <label style={{ fontWeight: "bold", marginRight: 8 }}>Enter Tenant ID:</label>
        <input
          type="text"
          value={tenancyId}
          onChange={(e) => setTenancyId(e.target.value)}
          placeholder="Ex: 1"
          required
          style={{ width: 150, padding: 6, borderRadius: 5, border: "1px solid #aaa" }}
        />
        <button type="submit" style={{ marginLeft: 8, padding: "6px 12px", background: "#273c75", color: "white", border: "none", borderRadius: 5, cursor: "pointer" }}>
          {loading ? "Searching..." : "Search"}
        </button>
      </form>

      {tenant && (
        <form onSubmit={handleUpdate} encType="multipart/form-data" style={{ maxWidth: 1200, margin: "0 auto", background: "white", padding: 20, borderRadius: 10, boxShadow: "0 0 10px #ccc" }}>
          {/* Rows of inputs */}
          {rows.map((cols, idx) => (
            <div key={idx} style={{ display: "grid", gridTemplateColumns: `repeat(${cols.length}, 1fr)`, gap: 15, marginBottom: 15 }}>
              {cols.map((field) => {
                const isDate = ["tenant_dob","passport_expiry","eid_expiry","lease_start","lease_end","move_in","move_out","creation_date"].includes(field);
                const readOnly = field === "tenancy_contract_number" || field === "rent_received" || field === "rent_outstanding";
                const value = getValue(field);
                return (
                  <div key={field}>
                    <label style={{ fontWeight: "bold", display: "block", marginBottom: 6 }}>{labelize(field)}</label>
                    {isDate ? (
                      <input
                        type="date"
                        name={field}
                        value={value || ""}
                        onChange={handleChange}
                        style={{ width: "100%", padding: 7, borderRadius: 6, border: "1px solid #ccc" }}
                      />
                    ) : (
                      <input
                        type="text"
                        name={field}
                        value={value || ""}
                        onChange={handleChange}
                        readOnly={readOnly}
                        style={{ width: "100%", padding: 7, borderRadius: 6, border: "1px solid #ccc", background: readOnly ? "#f3f3f3" : "white" }}
                      />
                    )}
                  </div>
                );
              })}
            </div>
          ))}

          {/* Photo Section */}
          <div style={{ display: "grid", gridTemplateColumns: "repeat(7, 1fr)", gap: 10, marginTop: 20, textAlign: "center" }}>
            {photoFields.map((field) => (
              <div key={field} style={{ textAlign: "center" }}>
                <label style={{ fontWeight: "bold", display: "block", marginBottom: 6 }}>{field}</label>
                {previews[field] ? (
                  <img src={previews[field]} alt={field} style={{ width: 60, height: 60, objectFit: "cover", borderRadius: 4, border: "1px solid #ccc", display: "block", margin: "0 auto 8px" }} />
                ) : (
                  <p style={{ margin: "6px 0" }}>N/A</p>
                )}
                <input type="file" name={field} onChange={(e) => handleFileChange(e, field)} />
              </div>
            ))}
          </div>

          {/* Submit */}
          <div style={{ textAlign: "center", marginTop: 20 }}>
            <button
              type="submit"
              style={{
                padding: "10px 20px",
                backgroundColor: "#0097e6",
                color: "white",
                border: "none",
                borderRadius: 6,
                cursor: "pointer",
                fontSize: 16
              }}
            >
              {loading ? "Updating..." : "Update Tenant"}
            </button>
          </div>
        </form>
      )}

      <a href="/index" style={{ display: "block", textAlign: "center", marginTop: 20, fontWeight: "bold", color: "#333" }}>
        ← Go Back Home
      </a>
    </div>
  );
};

export default ModifyTenant;
