import React, { useState } from "react";

const AddFinTrans = ({ sessionUser }) => {
  const [propertyCode, setPropertyCode] = useState("");
  const [property, setProperty] = useState(null);
  const [formData, setFormData] = useState({});
  const [previews, setPreviews] = useState({ document: null });

  // ------------------- Search Property -------------------
  const handleSearchProperty = async (e) => {
    e.preventDefault();
    if (!propertyCode) return;

    try {
      const res = await fetch(`http://localhost:5000/properties/${propertyCode}`, {
        method: "GET",
        credentials: "include",
      });
      const data = await res.json();
      if (res.ok && data.success) setProperty(data.property);
      else alert(data.message || "Property not found");
    } catch (err) {
      console.error(err);
      alert("Error fetching property");
    }
  };

  // ------------------- Input Change -------------------
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e, key) => {
    const file = e.target.files[0];
    if (!file) return;

    setFormData((prev) => ({ ...prev, [key]: file }));

    if (file.type.startsWith("image/")) {
      const reader = new FileReader();
      reader.onload = (ev) =>
        setPreviews((prev) => ({ ...prev, [key]: ev.target.result }));
      reader.readAsDataURL(file);
    } else {
      setPreviews((prev) => ({ ...prev, [key]: null }));
    }
  };

  // ------------------- Submit Form -------------------
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!property) return alert("Please search and select a property first");

    const data = new FormData();
    data.append("property_code", property.property_code);

    Object.keys(formData).forEach((key) => {
      data.append(key, formData[key]);
    });

    if (!formData.created_by) data.append("created_by", sessionUser || "admin");
    if (!formData.creation_date)
      data.append("creation_date", new Date().toISOString().split("T")[0]);

    try {
      const res = await fetch("http://localhost:5000/add_fintrans", {
        method: "POST",
        credentials: "include",
        body: data,
      });

      const result = await res.json();
      if (res.ok && result.success) {
        alert(`✅ ${result.message}`);
      } else {
        alert(`❌ ${result.message || "Failed to add transaction"}`);
      }
    } catch (err) {
      console.error("FETCH FAILED:", err);
      alert("❌ Error submitting form");
    }
  };

  // ------------------- Fields -------------------
  const transFields = [
    ["transaction_type", "amount", "transaction_date", "payment_mode", "reference_number"],
    ["remarks", "document", "created_by", "creation_date"]
  ];

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: "20px" }}>
      <h2 style={{ textAlign: "center", background: "#2f3640", color: "white", padding: "15px 0" }}>
        Add Financial Transaction
      </h2>

      {/* Property Search */}
      <form onSubmit={handleSearchProperty} style={{ textAlign: "center", margin: "20px 0" }}>
        <label>Enter Property Code:</label>
        <input
          type="text"
          value={propertyCode}
          onChange={(e) => setPropertyCode(e.target.value)}
          placeholder="Ex: P001"
          required
          style={{ margin: "0 10px", padding: "5px", width: 100 }}
        />
        <button type="submit" style={{ padding: "5px 10px", cursor: "pointer", width: 100 }}>Search</button>
      </form>

      {property && (
        <>
          <p style={{ textAlign: "center", fontWeight: "bold", marginBottom: "20px" }}>
            Property: {property.name} | Address: {property.address1}, {property.city}, {property.state}
          </p>

          <form onSubmit={handleSubmit} encType="multipart/form-data">
            {transFields.map((row, i) => (
              <div key={i} style={{ display: "grid", gridTemplateColumns: "repeat(5, 1fr)", gap: "10px", marginBottom: "10px" }}>
                {row.map((field) => {
                  const isFile = field === "document";
                  const value =
                    field === "created_by" ? (sessionUser || "admin") :
                    field === "creation_date" ? new Date().toISOString().split("T")[0] :
                    formData[field] || "";

                  return (
                    <div key={field}>
                      <label style={{ fontWeight: "bold" }}>{field.replace(/_/g, " ").toUpperCase()}</label>
                      {isFile ? (
                        <>
                          <input type="file" name={field} onChange={(e) => handleFileChange(e, field)} />
                          {previews[field] && (
                            <img
                              src={previews[field]}
                              alt={field}
                              style={{ maxWidth: "200px", borderRadius: "10px", marginTop: "10px", border: "1px solid #ccc" }}
                            />
                          )}
                        </>
                      ) : field === "transaction_date" || field === "creation_date" ? (
                        <input type="date" name={field} value={value} onChange={handleChange} />
                      ) : (
                        <input type="text" name={field} value={value} onChange={handleChange} />
                      )}
                    </div>
                  );
                })}
              </div>
            ))}

            <div style={{ textAlign: "center", marginTop: "30px" }}>
              <a href="/index" style={{ ...styles.button, backgroundColor: "#492bc0ff", marginRight: "20px", textDecoration: "none", display: "inline-block" }}>← Go Back Home</a>
              <button type="submit" style={styles.submitButton}>Add Transaction</button>
            </div>
          </form>
        </>
      )}
    </div>
  );
};

const styles = {
  button: { width: 200, padding: "10px 20px", backgroundColor: "#0097e6", color: "white", border: "none", borderRadius: "6px", cursor: "pointer", fontSize: "16px" },
  submitButton: { width: 200, padding: "10px 20px", backgroundColor: "#27ae60", color: "white", border: "none", borderRadius: "6px", cursor: "pointer", fontSize: "16px" },
};

export default AddFinTrans;
