import React, { useState } from "react";

const DeleteOwner = () => {
  const [ownerCode, setOwnerCode] = useState("");
  const [selectedOwner, setSelectedOwner] = useState(null);
  const [message, setMessage] = useState("");

  // Search owner by code
  const handleSearch = async (e) => {
    e.preventDefault();
    setMessage("");
    setSelectedOwner(null);

    try {
      const res = await fetch(`http://localhost:5000/owners/${ownerCode}`, {
        method: "GET",
        credentials: "include",
      });
      const data = await res.json();

      if (res.ok && data.success) {
        setSelectedOwner(data.owner);
      } else {
        setMessage(data.message || "Owner not found");
      }
    } catch (err) {
      console.error(err);
      setMessage("Error fetching owner");
    }
  };

  // Delete owner confirmation
  const handleDelete = async (e) => {
    e.preventDefault();
    if (!window.confirm("Are you sure you want to delete this owner?")) return;

    try {
      const formData = new FormData();
      formData.append("action", "delete");
      formData.append("owner_code", selectedOwner.owner_code);

      const res = await fetch("http://localhost:5000/delete_owner", {
        method: "POST",
        credentials: "include",
        body: formData,
      });
      const data = await res.json();

      if (res.ok) {
        setMessage("✅ Owner deleted successfully");
        setSelectedOwner(null);
        setOwnerCode("");
      } else {
        setMessage(data.message || "❌ Error deleting owner");
      }
    } catch (err) {
      console.error(err);
      setMessage("❌ Error deleting owner");
    }
  };

  return (
    <div style={{ maxWidth: "1500px", margin: "auto", padding: "20px", background: "#fff", borderRadius: "8px", boxShadow: "0 0 10px rgba(0,0,0,0.1)" }}>
      <h2 style={{ textAlign: "center", marginBottom: "20px" }}>Delete Owner</h2>

      {message && (
        <p style={{ textAlign: "center", fontWeight: "bold", marginBottom: "15px", color: message.includes("✅") ? "green" : "red" }}>
          {message}
        </p>
      )}

      {/* Search Form */}
      <form onSubmit={handleSearch} style={{ textAlign: "center", marginBottom: "20px" }}>
        <input
          type="text"
          value={ownerCode}
          onChange={(e) => setOwnerCode(e.target.value)}
          placeholder="Owner Code"
          required
          style={{ width: "120px", padding: "6px", fontSize: "0.85rem", marginRight: "5px" }}
        />
        <button type="submit" style={{ background: "#c0392b", color: "white", padding: "8px 16px", border: "none", borderRadius: "4px" }}>
          Search
        </button>
      </form>

      <a href="/" style={{ display: "block", textAlign: "center", marginBottom: "20px", color: "#333", fontWeight: "bold", textDecoration: "none" }}>
        ← Go Back Home
      </a>

      {/* Owner Details */}
      {selectedOwner && (
        <form onSubmit={handleDelete}>
          {/* Personal Details */}
          <div style={{ display: "flex", flexWrap: "wrap", marginBottom: "15px" }}>
            {[
              ["Salutation", selectedOwner.salutation],
              ["First Name", selectedOwner.first_name],
              ["Last Name", selectedOwner.last_name],
              ["Nationality", selectedOwner.nationality],
            ].map(([label, value], i) => (
              <div key={i} style={{ width: "25%", padding: "0 10px", marginBottom: "15px" }}>
                <label>{label}</label>
                <input type="text" value={value} readOnly style={{ width: "100%", padding: "8px", border: "1px solid #ccc", borderRadius: "4px" }} />
              </div>
            ))}
          </div>

          {/* Address & Contact */}
          <div style={{ display: "flex", flexWrap: "wrap", marginBottom: "15px" }}>
            {[
              ["Address 1", selectedOwner.address1],
              ["Address 2", selectedOwner.address2],
              ["City", selectedOwner.city],
              ["State", selectedOwner.state],
            ].map(([label, value], i) => (
              <div key={i} style={{ width: "25%", padding: "0 10px", marginBottom: "15px" }}>
                <label>{label}</label>
                <input type="text" value={value} readOnly style={{ width: "100%", padding: "8px", border: "1px solid #ccc", borderRadius: "4px" }} />
              </div>
            ))}
          </div>

          <div style={{ display: "flex", flexWrap: "wrap", marginBottom: "15px" }}>
            {[
              ["Country", selectedOwner.country],
              ["Zip Code", selectedOwner.zip_code],
              ["Mobile Number", selectedOwner.mobile_number],
              ["Email", selectedOwner.email],
            ].map(([label, value], i) => (
              <div key={i} style={{ width: "25%", padding: "0 10px", marginBottom: "15px" }}>
                <label>{label}</label>
                <input type="text" value={value} readOnly style={{ width: "100%", padding: "8px", border: "1px solid #ccc", borderRadius: "4px" }} />
              </div>
            ))}
          </div>

          {/* Passport & Dates */}
          <div style={{ display: "flex", flexWrap: "wrap", marginBottom: "15px" }}>
            {[
              ["Passport Number", selectedOwner.passport_number],
              ["Date of Birth", selectedOwner.date_of_birth],
              ["Visa Expiry", selectedOwner.visa_expiry_date],
              ["Passport Expiry", selectedOwner.passport_expiry_date],
            ].map(([label, value], i) => (
              <div key={i} style={{ width: "25%", padding: "0 10px", marginBottom: "15px" }}>
                <label>{label}</label>
                <input type={label.includes("Date") || label.includes("Expiry") ? "date" : "text"} value={value} readOnly style={{ width: "100%", padding: "8px", border: "1px solid #ccc", borderRadius: "4px" }} />
              </div>
            ))}
          </div>

          {/* Files */}
          <div style={{ display: "flex", flexWrap: "wrap", marginBottom: "15px" }}>
            {[
              ["Emirates ID", selectedOwner.eid_image, "eid_image"],
              ["Passport Copy", selectedOwner.passport_copy, "passport_copy"],
              ["Residence Visa", selectedOwner.res_visa, "res_visa"],
            ].map(([label, exists, type], i) => (
              <div key={i} style={{ width: "25%", padding: "0 10px", marginBottom: "15px" }}>
                <label>{label}</label>
                {exists && (
                  <img
                    src={`http://localhost:5000/owner_image/${selectedOwner.owner_code}/${type}`}
                    alt={label}
                    style={{ display: "block", marginTop: "5px", maxWidth: "100px", maxHeight: "80px", border: "1px solid #ccc", borderRadius: "4px" }}
                  />
                )}
              </div>
            ))}
          </div>

          <div style={{ textAlign: "center" }}>
            <button type="submit" style={{ background: "#c0392b", color: "white", padding: "8px 16px", border: "none", borderRadius: "4px" }}>
              Delete Owner
            </button>
          </div>
        </form>
      )}
    </div>
  );
};

export default DeleteOwner;
