import React, { useState, useEffect } from "react";

const OwnerList = ({ apiUrl }) => {
  const [owners, setOwners] = useState([]);
  const [sortField, setSortField] = useState("owner_code");
  const [sortOrder, setSortOrder] = useState("asc");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchOwners();
  }, [sortField, sortOrder]);

  const fetchOwners = async () => {
    try {
      setLoading(true);
      const url = `${apiUrl}?sort=${sortField}&order=${sortOrder.toUpperCase()}`;
      const res = await fetch(url, { credentials: "include" });
      const data = await res.json();
      if (data.success) setOwners(data.owners || []);
      else setOwners([]);
    } catch (err) {
      console.error(err);
      setOwners([]);
    } finally {
      setLoading(false);
    }
  };

  const handleSort = (field) => {
    if (sortField === field) {
      setSortOrder(sortOrder === "asc" ? "desc" : "asc");
    } else {
      setSortField(field);
      setSortOrder("asc");
    }
  };

  const currentDate = new Date().toLocaleDateString();

  const renderSortArrow = (field) => {
    if (sortField !== field) return null;
    return sortOrder === "asc" ? " ▲" : " ▼";
  };

  return (
    <div className="container" style={{ maxWidth: "1600px", margin: "auto", overflowX: "auto", padding: "20px" }}>
      <div className="header-row" style={{ display: "flex", alignItems: "center", justifyContent: "space-between", flexWrap: "wrap", marginBottom: "15px" }}>
        <div className="header-left" style={{ display: "flex", gap: "10px", fontWeight: "bold" }}>
          <span>{currentDate}</span> | <span>Total Owners: {owners.length}</span>
        </div>
        <h2 style={{ margin: "0 auto", textAlign: "center", color: "black" }}>List of Owners</h2>
        <div className="header-right" style={{ fontWeight: "bold" }}>
          <a href="/index" style={{ textDecoration: "none", color: "#333" }}>← Go Back Home</a>
        </div>
      </div>

      {loading ? (
        <p style={{ textAlign: "center" }}>Loading...</p>
      ) : (
        <table style={{ width: "100%", borderCollapse: "collapse", background: "white", borderRadius: "8px", overflow: "hidden" }}>
          <thead>
            <tr>
              {owners.length > 0 ? (
                Object.keys(owners[0]).map((col) => (
                  <th
                    key={col}
                    onClick={() => handleSort(col)}
                    style={{ padding: "10px", borderBottom: "1px solid #ddd", textAlign: "left", background: "#007BFF", color: "white", cursor: "pointer" }}
                  >
                    {col === "owner_code" ? "Owner Code" : col.replace("_", " ").replace(/\b\w/g, c => c.toUpperCase())}
                    {renderSortArrow(col)}
                  </th>
                ))
              ) : (
                <th>No data</th>
              )}
            </tr>
          </thead>
          <tbody>
            {owners.length > 0 ? (
              owners.map((owner) => (
                <tr key={owner.owner_code}>
                  {Object.entries(owner).map(([key, val]) => {
                    if (["eid_image", "passport_copy", "res_visa"].includes(key)) {
                      return (
                        <td key={key} style={{ padding: "8px" }}>
                          {val ? (
                            <img
                              src={`http://localhost:5000/owner_image/${owner.owner_code}/${key}`}
                              alt={key}
                              style={{ width: "30px", height: "30px", objectFit: "cover", borderRadius: "4px", border: "1px solid #ccc" }}
                            />
                          ) : (
                            "N/A"
                          )}
                        </td>
                      );
                    } else if (key === "owner_code") {
                      return <td key={key} style={{ padding: "8px" }}>{`OW${String(val).padStart(5, "0")}`}</td>;
                    } else {
                      return <td key={key} style={{ padding: "8px" }}>{val}</td>;
                    }
                  })}
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="100%" style={{ textAlign: "center", padding: "10px" }}>No owners found</td>
              </tr>
            )}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default OwnerList;
