import React, { useEffect, useState } from "react";

const ListProperty = () => {
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");

  // Preview modal
  const [previewSrc, setPreviewSrc] = useState(null);

  // Edit modal
  const [editingProperty, setEditingProperty] = useState(null);
  const [editForm, setEditForm] = useState({});
  const [editFile, setEditFile] = useState(null);
  const [editLoading, setEditLoading] = useState(false);

  const columnOrder = [
    "property_code","primary_owner_code","developer_name","building_name","property_type",
    "name","address1","address2","city","state","country","zip_code","municipality_number",
    "electricity_bill_number","water_bill_number","ownership_type","furnishing_status",
    "land_area","carpet_area","builtup_area","bedrooms","bathrooms","washrooms","facing",
    "car_parkings","servant_rooms","balconies","has_dishwasher","has_washing_machine",
    "property_value","year_of_construction","second_owner_code","expected_rent_value",
    "status","mtd_income","ytd_income","mtd_expense","ytd_expense","purchase_value",
    "construction_cost","renovation_cost","registration","transfer_fees","misc_expense",
    "created_by","creation_date","property_photo"
  ];

  const photoFields = ["property_photo"];
  const dateFields = ["year_of_construction","creation_date"];

  useEffect(() => {
    fetchProperties();
  }, []);

  async function fetchProperties() {
    try {
      setLoading(true);
      const res = await fetch("http://localhost:5000/list_property", {
        method: "GET",
        credentials: "include",
      });
      const data = await res.json();
      if (res.ok && data.success) setProperties(data.properties || []);
      else alert(data.message || "Error fetching properties");
    } catch (err) {
      console.error(err);
      alert("Error fetching properties");
    } finally {
      setLoading(false);
    }
  }

  const filtered = properties.filter(p => {
    const q = searchTerm.trim().toLowerCase();
    if (!q) return true;
    return (
      (p.property_code || "").toString().toLowerCase().includes(q) ||
      (p.name || "").toString().toLowerCase().includes(q)
    );
  });

  const openEditModal = (property) => {
    setEditingProperty(property);
    setEditForm({...property});
    setEditFile(null);
  };

  const closeEditModal = () => {
    setEditingProperty(null);
    setEditForm({});
    setEditFile(null);
  };

  const onEditChange = (e) => {
    const { name, value } = e.target;
    setEditForm(prev => ({...prev, [name]: value}));
  };

  const onEditFileChange = (e) => {
    setEditFile(e.target.files[0]);
  };

  const submitEdit = async (e) => {
    e.preventDefault();
    if (!editingProperty) return;
    try {
      setEditLoading(true);
      const data = new FormData();
      data.append("property_code", editForm.property_code);
      Object.entries(editForm).forEach(([k, v]) => {
        if (k !== "property_photo") data.append(k, v ?? "");
      });
      if (editFile) data.append("property_photo", editFile, editFile.name);

      const res = await fetch("http://localhost:5000/property_update", {
        method: "POST",
        credentials: "include",
        body: data
      });
      const result = await res.json();
      if (res.ok && result.success) {
        alert(result.message);
        fetchProperties();
        closeEditModal();
      } else {
        alert(result.message || "Failed to update property");
      }
    } catch (err) {
      console.error(err);
      alert("Error updating property");
    } finally {
      setEditLoading(false);
    }
  };

  const handleDelete = async (code) => {
    if (!window.confirm(`Delete property ${code}?`)) return;
    try {
      setLoading(true);
      const res = await fetch(`http://localhost:5000/property_delete_api/${code}`, {
        method: "DELETE",
        credentials: "include",
      });
      const data = await res.json();
      if (res.ok && data.success) {
        alert(data.message);
        setProperties(prev => prev.filter(p => p.property_code !== code));
      } else alert(data.message || "Failed to delete property");
    } catch (err) {
      console.error(err);
      alert("Error deleting property");
    } finally {
      setLoading(false);
    }
  };

  const renderThumb = (p, key) => {
    if (!p[key]) return <div style={{ fontSize: 12, color: "#666" }}>‚Äî</div>;
    const src = p[key].startsWith("data:") ? p[key] : `data:image/jpeg;base64,${p[key]}`;
    return <img src={src} alt={key} style={{ width: 48, height: 48, objectFit: "cover", borderRadius: 4, border: "1px solid #ccc", cursor: "pointer" }} onClick={() => setPreviewSrc(src)} />;
  };

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: 20, background: "#f0f2f5" }}>
      <h2 style={{ textAlign: "center", background: "#2f3640", color: "white", padding: 15, margin: 0 }}>Property List</h2>

      <div style={{ maxWidth: 1600, margin: "20px auto", background: "#fff", padding: 16, borderRadius: 8, overflowX: "auto" }}>
        <div style={{ display: "flex", justifyContent: "space-between", marginBottom: 12 }}>
          <div>Total Properties: {properties.length}</div>
          <input placeholder="Search by code or name" value={searchTerm} onChange={e => setSearchTerm(e.target.value)} style={{ padding: 8, borderRadius: 6, border: "1px solid #ccc", width: 300 }} />
          
        </div>

        {loading ? <p>Loading...</p> : (
          <table style={{ width: "100%", borderCollapse: "collapse" }}>
            <thead>
              <tr>
                {columnOrder.map(col => <th key={col} style={{ padding: 6, border: "1px solid #ddd", background: "#273c75", color: "#fff" }}>{col}</th>)}
                <th style={{ padding: 6, border: "1px solid #ddd", background: "#273c75", color: "#fff" }}>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filtered.map((p, idx) => (
                <tr key={p.property_code || idx} style={{ background: idx % 2 === 0 ? "#fafafa" : "#fff" }}>
                  {columnOrder.map(key => (
                    <td key={key} style={{ padding: 6, border: "1px solid #eee", textAlign: "center" }}>
                      {photoFields.includes(key) ? renderThumb(p, key) : dateFields.includes(key) && p[key] ? p[key].slice(0, 10) : p[key] ?? ""}
                    </td>
                  ))}
                  <td style={{ display: "flex", gap: 6, justifyContent: "center", padding: 6 }}>
                    <button onClick={() => openEditModal(p)} style={{ padding: "6px 10px", borderRadius: 6 }}>‚úèÔ∏è Edit</button>
                    <button onClick={() => handleDelete(p.property_code)} style={{ padding: "6px 10px", borderRadius: 6, background: "#c0392b", color: "#fff" }}>üóëÔ∏è Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>

      {/* Preview modal */}
      {previewSrc && <div onClick={() => setPreviewSrc(null)} style={{ position: "fixed", inset: 0, background: "rgba(0,0,0,0.8)", display: "flex", justifyContent: "center", alignItems: "center", zIndex: 2000 }}>
        <img src={previewSrc} alt="preview" style={{ maxWidth: "90%", maxHeight: "90%", borderRadius: 6 }} />
      </div>}

      {/* Edit modal */}
      {editingProperty && <div style={{ position: "fixed", inset: 0, background: "rgba(0,0,0,0.5)", display: "flex", justifyContent: "center", alignItems: "center", zIndex: 2000 }}>
        <div style={{ width: 900, maxHeight: "90vh", overflowY: "auto", background: "#fff", borderRadius: 8, padding: 16 }}>
          <h3>Edit Property ‚Äî {editingProperty.property_code}</h3>
          <form onSubmit={submitEdit}>
            <div style={{ display: "grid", gridTemplateColumns: "repeat(3,1fr)", gap: 12 }}>
              {columnOrder.filter(k => k !== "property_photo").map(k => (
                <div key={k}>
                  <label style={{ display: "block", fontWeight: "bold", marginBottom: 4 }}>{k}</label>
                  <input type={dateFields.includes(k) ? "date" : "text"} name={k} value={editForm[k] ?? ""} onChange={onEditChange} style={{ width: "100%", padding: 6, borderRadius: 4, border: "1px solid #ccc" }} />
                </div>
              ))}
            </div>

            <div style={{ marginTop: 16 }}>
              <label style={{ display: "block", fontWeight: "bold", marginBottom: 4 }}>Property Photo</label>
              {editingProperty.property_photo && <img src={editingProperty.property_photo.startsWith("data:") ? editingProperty.property_photo : `data:image/jpeg;base64,${editingProperty.property_photo}`} alt="property" style={{ width: 120, height: 120, objectFit: "cover", borderRadius: 6, border: "1px solid #ccc", marginBottom: 8 }} />}
              <input type="file" name="property_photo" onChange={onEditFileChange} />
            </div>

            <div style={{ marginTop: 16, display: "flex", gap: 8, justifyContent: "flex-end" }}>
              <button type="button" onClick={closeEditModal} style={{ padding: "6px 12px", borderRadius: 6 }}>Cancel</button>
              <button type="submit" disabled={editLoading} style={{ padding: "6px 12px", borderRadius: 6, background: "#2ecc71", color: "#fff" }}>{editLoading ? "Saving..." : "Save Changes"}</button>
            </div>
          </form>
        </div>
      </div>}
    </div>
  );
};

export default ListProperty;
