import React, { useState, useEffect } from "react";

const ListOwners = () => {
  const [owners, setOwners] = useState([]);
  const [sortField, setSortField] = useState("");
  const [sortOrder, setSortOrder] = useState("asc"); // asc or desc
  const [currentDate, setCurrentDate] = useState("");

  useEffect(() => {
    fetchOwners();
    setCurrentDate(new Date().toLocaleDateString());
  }, [sortField, sortOrder]);

  const fetchOwners = async () => {
    try {
      let url = `http://localhost:5000/owners?sort=${sortField}&order=${sortOrder}`;
      const res = await fetch(url, { credentials: "include" });
      const data = await res.json();
      setOwners(data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleSort = (field) => {
    if (sortField === field) {
      setSortOrder(sortOrder === "asc" ? "desc" : "asc");
    } else {
      setSortField(field);
      setSortOrder("asc");
    }
  };

  return (
    <div className="container" style={{ maxWidth: "1600px", margin: "auto", overflowX: "auto", padding: "20px" }}>
      {/* Header */}
      <div className="header-row" style={{ display: "flex", alignItems: "center", justifyContent: "space-between", marginBottom: "15px", flexWrap: "wrap" }}>
        <div className="header-left" style={{ display: "flex", gap: "10px", fontWeight: "bold" }}>
          <span>{currentDate}</span> | <span>Total Owners: {owners.length}</span>
        </div>
        <h2 style={{ margin: "0 auto", textAlign: "center", color: "black" }}>List of Owners</h2>
        <div className="header-right" style={{ fontWeight: "bold" }}>
          <a href="/" className="back-link" style={{ textDecoration: "none", color: "#333" }}>‚Üê Go Back Home</a>
        </div>
      </div>

      {/* Table */}
      <table style={{ width: "100%", borderCollapse: "collapse", background: "white", borderRadius: "8px", overflow: "hidden" }}>
        <thead>
          <tr>
            {owners.length > 0 && Object.keys(owners[0]).map((col) => {
              const isImageCol = ["eid_image", "passport_copy", "res_visa"].includes(col);
              return (
                <th
                  key={col}
                  style={{ padding: "10px 12px", borderBottom: "1px solid #ddd", textAlign: "left", fontSize: "0.9rem", background: "#007BFF", color: "white", cursor: isImageCol ? "default" : "pointer" }}
                  onClick={() => !isImageCol && handleSort(col)}
                >
                  {col.replace("_", " ").toUpperCase()} {!isImageCol && sortField === col && `(${sortOrder})`}
                </th>
              );
            })}
          </tr>
        </thead>
        <tbody>
          {owners.map((owner) => (
            <tr key={owner.owner_code} style={{ backgroundColor: "#fff" }}>
              {Object.entries(owner).map(([key, val]) => {
                if (["eid_image", "passport_copy", "res_visa"].includes(key)) {
                  return (
                    <td key={key} style={{ padding: "10px 12px", borderBottom: "1px solid #ddd" }}>
                      {val ? (
                        <img
                          src={`http://localhost:5000/owner_image/${owner.owner_code}/${key === "eid_image" ? "eid" : key === "passport_copy" ? "passport" : "visa"}`}
                          alt={key}
                          style={{ width: "30px", height: "30px", objectFit: "cover", borderRadius: "4px", border: "1px solid #ccc" }}
                        />
                      ) : "N/A"}
                    </td>
                  );
                } else {
                  return (
                    <td key={key} style={{ padding: "10px 12px", borderBottom: "1px solid #ddd", fontSize: "0.9rem" }}>
                      {val}
                    </td>
                  );
                }
              })}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ListOwners;
