import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const AddFinTrans = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState(null); // start with null for proper check

  // üß† Load contract data from localStorage
  useEffect(() => {
    const savedContract = localStorage.getItem("pending_contract");

    if (!savedContract) {
      alert("No contract found. Redirecting back...");
      navigate("/addcontract");
      return;
    }

    const contract = JSON.parse(savedContract);

    setFormData({
      contract_id: contract.contract_id || "",
      property_code: contract.property_code || "",
      tenant_id: contract.tenant_id || "",
      tenant_name: contract.tenant_name || "",
      tenant_email: contract.tenant_email || "",
      receipt_payment: "Receipt",
      receipt_payment_reason: "",
      mode_of_payment: "",
      tr_date: "",
      tr_amount: "",
      reference_number: "",
      cheque_number: "",
      cheque_date: "",
    });
  }, [navigate]);

  // üß© Update form values
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // üíæ Save Transaction
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData) return;

    try {
      const response = await fetch("http://localhost:5000/add_fintrans", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      const result = await response.json();

      if (result.success) {
        alert("‚úÖ Financial Transaction saved successfully!");

        // üì® Send email confirmation
        await fetch("http://localhost:5000/send_registration_email", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            to_email: formData.tenant_email,
            tenant_name: formData.tenant_name,
            property_code: formData.property_code,
          }),
        });

        localStorage.removeItem("pending_contract");
        navigate("/addcontract");
      } else {
        alert("‚ùå Failed to save transaction!");
      }
    } catch (error) {
      console.error("Error saving transaction:", error);
      alert("Error saving transaction!");
    }
  };

  if (!formData) return <p style={{ textAlign: "center" }}>‚è≥ Loading contract details...</p>;

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: "20px" }}>
      <h2
        style={{
          textAlign: "center",
          background: "#2f3640",
          color: "white",
          padding: "12px 0",
          borderRadius: "8px",
        }}
      >
        Add Financial Transaction
      </h2>

      <form onSubmit={handleSubmit} style={{ marginTop: "20px" }}>
        {/* üîπ Read-only Info Section */}
        <fieldset style={{ border: "1px solid #ccc", padding: "15px", borderRadius: "8px" }}>
          <legend>Contract Details</legend>
          <div style={{ display: "grid", gridTemplateColumns: "repeat(4, 1fr)", gap: "10px" }}>
            <input name="contract_id" value={formData.contract_id} readOnly placeholder="Contract ID" />
            <input name="property_code" value={formData.property_code} readOnly placeholder="Property Code" />
            <input name="tenant_id" value={formData.tenant_id} readOnly placeholder="Tenant ID" />
            <input name="tenant_name" value={formData.tenant_name} readOnly placeholder="Tenant Name" />
          </div>
        </fieldset>

        <br />

        {/* üîπ Payment Info Section */}
        <fieldset style={{ border: "1px solid #ccc", padding: "15px", borderRadius: "8px" }}>
          <legend>Transaction Details</legend>

          <div style={{ display: "grid", gridTemplateColumns: "repeat(3, 1fr)", gap: "10px" }}>
            <select
              name="mode_of_payment"
              value={formData.mode_of_payment}
              onChange={handleChange}
              required
            >
              <option value="">Select Mode of Payment</option>
              <option value="Cash">Cash</option>
              <option value="Cheque">Cheque</option>
              <option value="Online">Online</option>
            </select>

            <input
              type="date"
              name="tr_date"
              value={formData.tr_date}
              onChange={handleChange}
              required
            />

            <input
              type="number"
              name="tr_amount"
              value={formData.tr_amount}
              onChange={handleChange}
              placeholder="Transaction Amount"
              required
            />
          </div>

          {/* üí∞ Conditional Fields */}
          {formData.mode_of_payment === "Cheque" && (
            <div
              style={{
                display: "grid",
                gridTemplateColumns: "repeat(2, 1fr)",
                gap: "10px",
                marginTop: "10px",
              }}
            >
              <input
                type="text"
                name="cheque_number"
                value={formData.cheque_number}
                onChange={handleChange}
                placeholder="Cheque Number"
              />
              <input
                type="date"
                name="cheque_date"
                value={formData.cheque_date}
                onChange={handleChange}
                placeholder="Cheque Date"
              />
            </div>
          )}

          {formData.mode_of_payment === "Online" && (
            <div style={{ marginTop: "10px" }}>
              <input
                type="text"
                name="reference_number"
                value={formData.reference_number}
                onChange={handleChange}
                placeholder="Online Reference Number"
              />
            </div>
          )}
        </fieldset>

        {/* üîò Buttons */}
        <div style={{ textAlign: "center", marginTop: "25px" }}>
          <button
            type="button"
            onClick={() => navigate("/addcontract")}
            style={{
              backgroundColor: "#555",
              color: "white",
              border: "none",
              borderRadius: "6px",
              padding: "10px 20px",
              marginRight: "15px",
              cursor: "pointer",
            }}
          >
            ‚Üê Back to Contract
          </button>

          <button
            type="submit"
            style={{
              backgroundColor: "#27ae60",
              color: "white",
              border: "none",
              borderRadius: "6px",
              padding: "10px 20px",
              cursor: "pointer",
            }}
          >
            üíæ Save Transaction
          </button>
        </div>
      </form>
    </div>
  );
};

export default AddFinTrans;
