import React, { useState, useEffect } from "react";

const EditProperty = ({ propertyCode, onClose, onUpdate }) => {
  const [editingProperty, setEditingProperty] = useState(null);
  const [editForm, setEditForm] = useState({});
  const [editFiles, setEditFiles] = useState({});
  const [editLoading, setEditLoading] = useState(false);

  // List of photo fields
  const photoFields = ["property_photo"];

  // Mapping DB columns to form keys
  const dbToFormMap = {
    property_code: "property_code",
    primary_owner_code: "primary_owner_code",
    developer_name: "developer_name",
    building_name: "building_name",
    property_type: "property_type",
    name: "name",
    address1: "address1",
    address2: "address2",
    city: "city",
    state: "state",
    country: "country",
    zip_code: "zip_code",
    municipality_number: "municipality_number",
    electricity_bill_number: "electricity_bill_number",
    water_bill_number: "water_bill_number",
    ownership_type: "ownership_type",
    furnishing_status: "furnishing_status",
    land_area: "land_area",
    carpet_area: "carpet_area",
    builtup_area: "builtup_area",
    bedrooms: "bedrooms",
    bathrooms: "bathrooms",
    washrooms: "washrooms",
    facing: "facing",
    car_parkings: "car_parkings",
    servant_rooms: "servant_rooms",
    balconies: "balconies",
    has_dishwasher: "has_dishwasher",
    has_washing_machine: "has_washing_machine",
    property_value: "property_value",
    year_of_construction: "year_of_construction",
    second_owner_code: "second_owner_code",
    expected_rent_value: "expected_rent_value",
    status: "status",
    mtd_income: "mtd_income",
    ytd_income: "ytd_income",
    mtd_expense: "mtd_expense",
    ytd_expense: "ytd_expense",
    purchase_value: "purchase_value",
    construction_cost: "construction_cost",
    renovation_cost: "renovation_cost",
    registration: "registration",
    transfer_fees: "transfer_fees",
    misc_expense: "misc_expense",
    created_by: "created_by",
    creation_date: "creation_date"
  };

  // Fetch property details
  useEffect(() => {
    const fetchProperty = async () => {
      try {
        const res = await fetch(`http://localhost:5000/edit_property/${propertyCode}`);
        const data = await res.json();
        if (data.success) {
          setEditingProperty(data.property);
          setEditForm(data.property);
        } else {
          alert(data.message);
          onClose();
        }
      } catch (err) {
        console.error(err);
        alert("Error fetching property");
        onClose();
      }
    };
    fetchProperty();
  }, [propertyCode, onClose]);

  const onEditChange = (e) => {
    const { name, value } = e.target;
    setEditForm({ ...editForm, [name]: value });
  };

  const onEditFileChange = (e, field) => {
    const file = e.target.files[0];
    if (file) {
      setEditFiles({ ...editFiles, [field]: file });
      const reader = new FileReader();
      reader.onload = () => {
        setEditingProperty({ ...editingProperty, [field]: reader.result });
      };
      reader.readAsDataURL(file);
    }
  };

  const submitEdit = async (e) => {
    e.preventDefault();
    setEditLoading(true);
    try {
      const formData = new FormData();
      Object.entries(editForm).forEach(([key, value]) => formData.append(key, value));
      Object.entries(editFiles).forEach(([key, file]) => formData.append(key, file));

      const res = await fetch(`http://localhost:5000/edit_property/${propertyCode}`, {
        method: "PUT",
        body: formData
      });
      const data = await res.json();
      if (data.success) {
        alert("Property updated successfully");
        onUpdate();
        onClose();
      } else {
        alert(data.message || "Failed to update property");
      }
    } catch (err) {
      console.error(err);
      alert("Error updating property");
    } finally {
      setEditLoading(false);
    }
  };

  const closeEditModal = () => onClose();

  if (!editingProperty) return null;

  return (
    <div style={{
      position: "fixed", inset: 0, backgroundColor: "rgba(0,0,0,0.5)",
      display: "flex", alignItems: "center", justifyContent: "center", zIndex: 2000
    }}>
      <div style={{ width: 900, maxHeight: "90vh", overflowY: "auto", background: "#fff", borderRadius: 8, padding: 16 }}>
        <h3 style={{ marginTop: 0 }}>Edit Property â€” {editingProperty.property_code}</h3>

        <form onSubmit={submitEdit}>
          <div style={{ display: "grid", gridTemplateColumns: "repeat(3, 1fr)", gap: 12 }}>
            {Object.entries(dbToFormMap).map(([dbCol, formKey]) => (
              <div key={formKey}>
                <label style={{ display: "block", fontWeight: "bold", marginBottom: 6 }}>
                  {dbCol.replace(/([A-Z])/g, " $1")}
                </label>
                {["creation_date", "year_of_construction"].includes(formKey) ? (
                  <input
                    type="date"
                    name={formKey}
                    value={editForm[formKey]?.slice(0, 10) || ""}
                    onChange={onEditChange}
                    style={{ width: "100%", padding: 8, borderRadius: 6, border: "1px solid #ccc" }}
                  />
                ) : (
                  <input
                    type="text"
                    name={formKey}
                    value={editForm[formKey] || ""}
                    onChange={onEditChange}
                    style={{ width: "100%", padding: 8, borderRadius: 6, border: "1px solid #ccc" }}
                  />
                )}
              </div>
            ))}
          </div>

          <hr style={{ margin: "16px 0" }} />

          <div style={{ display: "grid", gridTemplateColumns: "repeat(4,1fr)", gap: 12 }}>
            {photoFields.map((pf) => (
              <div key={pf} style={{ textAlign: "center" }}>
                <label style={{ display: "block", fontWeight: "bold", marginBottom: 6 }}>{pf}</label>
                {editingProperty[pf] ? (
                  <img
                    src={editingProperty[pf].startsWith("data:") ? editingProperty[pf] : `data:image/jpeg;base64,${editingProperty[pf]}`}
                    alt={pf}
                    style={{ width: 80, height: 80, objectFit: "cover", borderRadius: 6, border: "1px solid #ccc", display: "block", margin: "0 auto 8px" }}
                  />
                ) : (
                  <div style={{ width: 80, height: 80, background: "#f3f3f3", borderRadius: 6, display: "inline-block", marginBottom: 8 }} />
                )}
                <input type="file" name={pf} onChange={(e) => onEditFileChange(e, pf)} />
              </div>
            ))}
          </div>

          <div style={{ display: "flex", gap: 8, justifyContent: "flex-end", marginTop: 16 }}>
            <button type="button" onClick={closeEditModal} style={{ padding: "8px 12px", borderRadius: 6 }}>Cancel</button>
            <button type="submit" disabled={editLoading} style={{ padding: "8px 12px", borderRadius: 6, background: "#2ecc71", color: "#fff" }}>
              {editLoading ? "Saving..." : "Save Changes"}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default EditProperty;
