import React, { useState } from "react";

const ModifyContract = ({ sessionUser }) => {
  const [contractNumber, setContractNumber] = useState("");
  const [contract, setContract] = useState(null);
  const [formData, setFormData] = useState({});
  const [loading, setLoading] = useState(false);

  const parseDate = (val) => {
    if (!val) return "";
    const d = new Date(val);
    if (isNaN(d)) return "";
    const month = (d.getMonth() + 1).toString().padStart(2, "0");
    const day = d.getDate().toString().padStart(2, "0");
    return `${d.getFullYear()}-${month}-${day}`;
  };

  const labelize = (s) => s.replace(/_/g, " ").replace(/\b\w/g, (ch) => ch.toUpperCase());

  const fields = [
    "contract_id",
    "property_id",
    "tenant_id",
    "tenant_name",
    "contract_start_date",
    "contract_end_date",
    "contract_termination_date",
    "move_in_date",
    "move_out_date",
    "rent_amount",
    "deposit_amount",
    "notice_period",
    "rent_payment_mode",
    "rent_payment_type",
    "deposit_payment_type",
    "new_or_old_tenant",
    "created_by",
    "creation_date"
  ];

  // ---------------- SEARCH ----------------
  const handleSearch = async (e) => {
    e?.preventDefault();
    if (!contractNumber) return alert("Please enter Contract Number");

    try {
      setLoading(true);
      const res = await fetch(`http://localhost:5000/contract/${contractNumber}`);
      const data = await res.json();

      if (res.ok && data.success && data.contract) {
        setContract(data.contract);
        const initial = {};
        Object.keys(data.contract).forEach((k) => {
          initial[k] = k.includes("date") ? parseDate(data.contract[k]) : data.contract[k] ?? "";
        });
        setFormData(initial);
      } else {
        alert(data.message || "Contract not found");
        setContract(null);
        setFormData({});
      }
    } catch (err) {
      console.error(err);
      alert("Error fetching contract");
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // ---------------- UPDATE ----------------
  const handleUpdate = async (e) => {
    e.preventDefault();
    if (!contract) return alert("Search a contract first");

    const data = new FormData();
    data.append("contract_id", contract.contract_id || contractNumber);
    Object.entries(formData).forEach(([k, v]) => {
      if (v != null) data.append(k, v);
    });

    if (!formData.created_by) data.append("created_by", sessionUser || "admin");
    if (!formData.creation_date)
      data.append("creation_date", new Date().toISOString().slice(0, 10));

    try {
      const res = await fetch("http://localhost:5000/modify_contract", {
        method: "POST",
        credentials: "include",
        body: data,
      });
      const result = await res.json();
      if (res.ok && result.success) {
        alert(`✅ ${result.message}`);
        await handleSearch(); // refresh
      } else alert(`❌ ${result.message || "Failed to update contract"}`);
    } catch (err) {
      console.error(err);
      alert("❌ Error updating contract");
    }
  };

  const getValue = (key) => formData[key] ?? contract?.[key] ?? "";
  const readOnlyFields = ["contract_id","property_id","tenant_id","tenant_name"];

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: 20 }}>
      <h2 style={{ textAlign: "center", background: "#2f3640", color: "white", padding: 15 }}>Modify Contract</h2>

      {/* Search */}
      <form onSubmit={handleSearch} style={{ textAlign: "center", margin: "20px 0" }}>
        <input
          type="text"
          value={contractNumber}
          onChange={(e) => setContractNumber(e.target.value)}
          placeholder="Contract Number"
          required
          style={{ width: 150, padding: 6, borderRadius: 5, border: "1px solid #aaa" }}
        />
        <button type="submit" style={{ marginLeft: 8, padding: "6px 12px", background: "#273c75", color: "white", border: "none", borderRadius: 5, width: 200 }}>
          {loading ? "Searching..." : "Search"}
        </button>
      </form>

      {/* Form */}
      {contract && (
        <form onSubmit={handleUpdate} style={{ maxWidth: 1200, margin: "0 auto", padding: 20, background: "white", borderRadius: 10, boxShadow: "0 0 10px #ccc" }}>
          {fields.reduce((rows, field, i) => {
            if (i % 4 === 0) rows.push([]);
            rows[rows.length - 1].push(field);
            return rows;
          }, []).map((cols, i) => (
            <div key={i} style={{ display: "grid", gridTemplateColumns: `repeat(${cols.length}, 1fr)`, gap: 15, marginBottom: 15 }}>
              {cols.map((field) => {
                const isDate = field.includes("date");
                const readOnly = readOnlyFields.includes(field);
                return (
                  <div key={field}>
                    <label style={{ fontWeight: "bold", display: "block", marginBottom: 6 }}>{labelize(field)}</label>
                    <input
                      type={isDate ? "date" : "text"}
                      name={field}
                      value={getValue(field)}
                      onChange={handleChange}
                      readOnly={readOnly}
                      style={{ width: "100%", padding: 7, borderRadius: 6, border: "1px solid #ccc", backgroundColor: readOnly ? "#f0f0f0" : "white" }}
                    />
                  </div>
                );
              })}
            </div>
          ))}

          <div style={{ textAlign: "center", marginTop: 30 }}>
            <a href="/index" style={{ width: 200, padding: "10px 20px", backgroundColor: "#492bc0ff", color: "white", borderRadius: 6, marginRight: 20, display: "inline-block", textDecoration: "none" }}>← Go Back Home</a>
            <button type="submit" style={{ width: 200, padding: "10px 20px", backgroundColor: "#27ae60", color: "white", border: "none", borderRadius: 6 }}>Modify Contract</button>
          </div>
        </form>
      )}
    </div>
  );
};

export default ModifyContract;
