import React, { useState, useEffect } from "react";

const OwnersList = () => {
  const [owners, setOwners] = useState([]);
  const [sortField, setSortField] = useState("owner_code");
  const [sortOrder, setSortOrder] = useState("ASC");
  const [loading, setLoading] = useState(true);

  // Database column order
  const columns = [
    "owner_code", "salutation", "first_name", "last_name", "nationality",
    "address1", "address2", "city", "state", "country", "zip_code",
    "mobile_number", "email", "passport_number", "date_of_birth",
    "visa_expiry_date", "passport_expiry_date", "eid_image",
    "passport_copy", "res_visa", "created_by", "approved_by"
  ];

  const fetchOwners = async () => {
    try {
      setLoading(true);
      const res = await fetch(`http://localhost:5000/owners_list?sort=${sortField}&order=${sortOrder}`, {
        credentials: "include",
      });
      const data = await res.json();
      if (data.success) {
        setOwners(data.owners);
      } else {
        setOwners([]);
        console.error(data.message);
      }
    } catch (err) {
      console.error(err);
      setOwners([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchOwners();
  }, [sortField, sortOrder]);

  const handleSort = (field) => {
    if (sortField === field) {
      setSortOrder(sortOrder === "ASC" ? "DESC" : "ASC");
    } else {
      setSortField(field);
      setSortOrder("ASC");
    }
  };

  const arrow = (field) => (sortField === field ? (sortOrder === "ASC" ? " ↑" : " ↓") : null);
  const currentDate = new Date().toLocaleDateString();

  return (
    <div style={{ maxWidth: "1600px", margin: "auto", overflowX: "auto", padding: "20px" }}>
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "15px", flexWrap: "wrap" }}>
        <div style={{ fontWeight: "bold", gap: "10px", display: "flex" }}>
          <span>{currentDate}</span>
          <span>Total Owners: {owners.length}</span>
        </div>
        <h2 style={{ margin: "0 auto", color: "black" }}>List of Owners</h2>
        <div style={{ fontWeight: "bold" }}>
          <a href="/index" style={{ textDecoration: "none", color: "#333" }}>← Go Back Home</a>
        </div>
      </div>

      {loading ? (
        <p style={{ textAlign: "center" }}>Loading...</p>
      ) : (
        <table style={{ width: "100%", borderCollapse: "collapse", background: "white", borderRadius: "8px", overflow: "hidden" }}>
          <thead>
            <tr>
              {columns.map((col) => (
                <th
                  key={col}
                  onClick={() => handleSort(col)}
                  style={{
                    padding: "10px 12px",
                    borderBottom: "1px solid #ddd",
                    textAlign: "left",
                    fontSize: "0.9rem",
                    background: "#007BFF",
                    color: "white",
                    cursor: "pointer",
                  }}
                >
                  {col.replace("_", " ").replace(/\b\w/g, (c) => c.toUpperCase())}
                  {arrow(col)}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {owners.map((owner) => (
              <tr key={owner.owner_code} style={{ background: "#fff", borderBottom: "1px solid #ddd" }}>
                {columns.map((key) => {
                  if (["eid_image", "passport_copy", "res_visa"].includes(key)) {
                    return (
                      <td key={key} style={{ padding: "10px 12px" }}>
                        {owner[key] ? (
                          <img
                            src={owner[key]}
                            alt={key}
                            style={{ width: "30px", height: "30px", objectFit: "cover", borderRadius: "4px", border: "1px solid #ccc" }}
                          />
                        ) : (
                          "N/A"
                        )}
                      </td>
                    );
                  } else if (key === "owner_code") {
                    return <td key={key} style={{ padding: "10px 12px" }}>OW{owner.owner_code.toString().padStart(5, "0")}</td>;
                  } else {
                    return <td key={key} style={{ padding: "10px 12px" }}>{owner[key]}</td>;
                  }
                })}
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default OwnersList;
