import React, { useState } from "react";

const DeleteOwner = () => {
  const [ownerCode, setOwnerCode] = useState("");
  const [selectedOwner, setSelectedOwner] = useState(null);
  const [message, setMessage] = useState("");

  // Search owner by code
  const handleSearch = async (e) => {
    e.preventDefault();
    setMessage("");
    setSelectedOwner(null);

    try {
      const res = await fetch(`http://localhost:5000/owners/${ownerCode}`, {
        method: "GET",
        credentials: "include",
      });
      const data = await res.json();
      if (res.ok) {
        setSelectedOwner(data);
      } else {
        setMessage(data.error || "Owner not found");
      }
    } catch (err) {
      console.error(err);
      setMessage("Error fetching owner");
    }
  };

  // Delete owner confirmation
  const handleDelete = async (e) => {
    e.preventDefault();
    if (!window.confirm("Are you sure you want to delete this owner?")) return;

    try {
      const res = await fetch("http://localhost:5000/delete_owner", {
        method: "POST",
        credentials: "include",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ owner_code: selectedOwner.owner_code }),
      });
      const data = await res.json();
      if (res.ok) {
        setMessage("✅ Owner deleted successfully");
        setSelectedOwner(null);
        setOwnerCode("");
      } else {
        setMessage(data.error || "❌ Error deleting owner");
      }
    } catch (err) {
      console.error(err);
      setMessage("❌ Error deleting owner");
    }
  };

  const infoFields = (fields) =>
    fields.map(([label, value, type], i) => (
      <div
        key={i}
        className="col"
        style={{
          flex: "1 1 200px",
          minWidth: "200px",
          marginBottom: "15px",
          padding: "0 10px",
        }}
      >
        <label style={{ display: "block", marginBottom: "5px", fontWeight: "bold" }}>{label}</label>
        {type === "img" ? (
          value && (
            <img
              src={value}
              alt={label}
              style={{ display: "block", marginTop: "5px", maxWidth: "100%", maxHeight: "100px", border: "1px solid #ccc", borderRadius: "4px" }}
            />
          )
        ) : (
          <input
            type={label.toLowerCase().includes("date") ? "date" : "text"}
            value={value || ""}
            readOnly
            style={{ width: "100%", padding: "8px", border: "1px solid #ccc", borderRadius: "4px" }}
          />
        )}
      </div>
    ));

  return (
    <div
      className="container"
      style={{
        maxWidth: "1400px",
        margin: "auto",
        padding: "20px",
        background: "#fff",
        borderRadius: "8px",
        boxShadow: "0 0 15px rgba(0,0,0,0.1)",
      }}
    >
      <h2 style={{ textAlign: "center", marginBottom: "20px", color: "#333" }}>Delete Owner</h2>

      {message && (
        <p style={{ textAlign: "center", fontWeight: "bold", marginBottom: "15px", color: message.includes("✅") ? "green" : "red" }}>
          {message}
        </p>
      )}

      {/* Search Form */}
      <form onSubmit={handleSearch} style={{ textAlign: "center", marginBottom: "20px" }}>
        <input
          type="text"
          value={ownerCode}
          onChange={(e) => setOwnerCode(e.target.value)}
          placeholder="Owner Code"
          required
          style={{ width: "150px", padding: "6px", marginRight: "8px", borderRadius: "4px", border: "1px solid #ccc" }}
        />
        <button type="submit" style={{ padding: "6px 12px", background: "#007BFF", color: "#fff", border: "none", borderRadius: "4px", cursor: "pointer" }}>
          Search
        </button>
      </form>

      <a href="/" style={{ display: "block", textAlign: "center", marginBottom: "20px", color: "#333", fontWeight: "bold", textDecoration: "none" }}>
        ← Go Back Home
      </a>

      {/* Owner Details */}
      {selectedOwner && (
        <form onSubmit={handleDelete}>
          <div className="row" style={{ display: "flex", flexWrap: "wrap" }}>
            {infoFields([
              ["Salutation", selectedOwner.salutation],
              ["First Name", selectedOwner.first_name],
              ["Last Name", selectedOwner.last_name],
              ["Nationality", selectedOwner.nationality],
            ])}
          </div>

          <div className="row" style={{ display: "flex", flexWrap: "wrap" }}>
            {infoFields([
              ["Address 1", selectedOwner.address1],
              ["Address 2", selectedOwner.address2],
              ["City", selectedOwner.city],
              ["State", selectedOwner.state],
            ])}
          </div>

          <div className="row" style={{ display: "flex", flexWrap: "wrap" }}>
            {infoFields([
              ["Country", selectedOwner.country],
              ["Zip Code", selectedOwner.zip_code],
              ["Mobile Number", selectedOwner.mobile_number],
              ["Email", selectedOwner.email],
            ])}
          </div>

          <div className="row" style={{ display: "flex", flexWrap: "wrap" }}>
            {infoFields([
              ["Passport Number", selectedOwner.passport_number],
              ["Emirates ID", selectedOwner.emirates_id],
              ["Date of Birth", selectedOwner.date_of_birth],
              ["Visa Expiry", selectedOwner.visa_expiry_date],
            ])}
          </div>

          {/* Files */}
          <div className="row" style={{ display: "flex", flexWrap: "wrap" }}>
            {infoFields([
              ["Emirates ID File", selectedOwner.eid_image ? `http://localhost:5000/owner_image/${selectedOwner.owner_code}/eid` : null, "img"],
              ["Passport Copy File", selectedOwner.passport_copy ? `http://localhost:5000/owner_image/${selectedOwner.owner_code}/passport` : null, "img"],
              ["Residence Visa File", selectedOwner.res_visa ? `http://localhost:5000/owner_image/${selectedOwner.owner_code}/visa` : null, "img"],
            ])}
          </div>

          <div style={{ textAlign: "center", marginTop: "20px" }}>
            <button type="submit" style={{ padding: "8px 16px", background: "#c0392b", color: "#fff", border: "none", borderRadius: "4px", cursor: "pointer" }}>
              Delete Owner
            </button>
          </div>
        </form>
      )}
    </div>
  );
};

export default DeleteOwner;
