import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

const AddOwner = () => {
  const [modalMessage, setModalMessage] = useState("");
  const [showModal, setShowModal] = useState(false);

  const [imagePreviews, setImagePreviews] = useState({
    OwnerEID: null,
    OwnerPassportCopy: null,
    OwnerResVisa: null,
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);

    try {
      const response = await fetch("http://localhost:5000/add_owner", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();

      setModalMessage(data.success ? `✅ ${data.message}` : `❌ ${data.message}`);
      setShowModal(true);

      if (data.success) {
        e.target.reset();
        setImagePreviews({ OwnerEID: null, OwnerPassportCopy: null, OwnerResVisa: null });
      }
    } catch (error) {
      console.error("Full Fetch Error:", error);
      setModalMessage("❌ Error submitting form! Check console for details.");
      setShowModal(true);
    }
  };

  const closeModal = () => setShowModal(false);

  const handleImageChange = (e) => {
    const { name, files } = e.target;
    if (files && files[0]) {
      setImagePreviews((prev) => ({
        ...prev,
        [name]: URL.createObjectURL(files[0]),
      }));
    }
  };

  return (
    <div className="container mt-4">
      <form className="row g-3" onSubmit={handleSubmit} encType="multipart/form-data">
        {/* --- Personal & Address Info --- */}
        <div className="col-md-3">
          <label className="form-label">Salutation</label>
          <select name="OwnerSalutation" className="form-control" required>
            <option value="">-- Select --</option>
            <option value="Mr.">Mr.</option>
            <option value="Mrs.">Mrs.</option>
            <option value="Ms.">Ms.</option>
          </select>
        </div>
        <div className="col-md-3">
          <label className="form-label">First Name</label>
          <input type="text" name="OwnerFirstName" className="form-control" required />
        </div>
        <div className="col-md-3">
          <label className="form-label">Last Name</label>
          <input type="text" name="OwnerLastName" className="form-control" required />
        </div>
        <div className="col-md-3">
          <label className="form-label">Nationality</label>
          <input type="text" name="OwnerNationality" className="form-control" required />
        </div>

        {/* Address */}
        <div className="col-md-3">
          <label className="form-label">Address 1</label>
          <input type="text" name="OwnerAddress1" className="form-control" required />
        </div>
        <div className="col-md-3">
          <label className="form-label">Address 2</label>
          <input type="text" name="OwnerAddress2" className="form-control" />
        </div>
        <div className="col-md-3">
          <label className="form-label">City</label>
          <input type="text" name="OwnerCity" className="form-control" required />
        </div>
        <div className="col-md-3">
          <label className="form-label">State</label>
          <input type="text" name="OwnerState" className="form-control" required />
        </div>
        <div className="col-md-3">
          <label className="form-label">Country</label>
          <input type="text" name="OwnerCountry" className="form-control" required />
        </div>
        <div className="col-md-3">
          <label className="form-label">Zip Code</label>
          <input type="text" name="OwnerZipCode" className="form-control" required />
        </div>

        {/* Contact */}
        <div className="col-md-3">
          <label className="form-label">Mobile Number</label>
          <input type="text" name="OwnerMobileNumber" className="form-control" required />
        </div>
        <div className="col-md-3">
          <label className="form-label">Email</label>
          <input type="email" name="OwnerEmailId" className="form-control" required />
        </div>

        {/* Passport / DOB / Visa */}
        <div className="col-md-3">
          <label className="form-label">Passport Number</label>
          <input type="text" name="OwnerPassportNumber" className="form-control" required />
        </div>
        <div className="col-md-3">
          <label className="form-label">Emirates ID</label>
          <input type="text" name="OwnerEmiratesId" className="form-control" />
        </div>
        <div className="col-md-3">
          <label className="form-label">Date of Birth</label>
          <input type="date" name="OwnerDateOfBirth" className="form-control" required />
        </div>
        <div className="col-md-3">
          <label className="form-label">Visa Expiry Date</label>
          <input type="date" name="OwnerVisaExpiryDate" className="form-control" required />
        </div>
        <div className="col-md-3">
          <label className="form-label">Passport Expiry Date</label>
          <input type="date" name="OwnerPassportExpiryDate" className="form-control" required />
        </div>

        {/* File Uploads with Preview Grid */}
        <div className="col-12">
          <label className="form-label">Upload Documents</label>
          <div className="row g-3">
            {["OwnerEID", "OwnerPassportCopy", "OwnerResVisa"].map((field) => (
              <div className="col-md-4 text-center" key={field}>
                <input
                  type="file"
                  name={field}
                  className="form-control mb-2"
                  accept="image/*"
                  onChange={handleImageChange}
                  required
                />
                {imagePreviews[field] && (
                  <img
                    src={imagePreviews[field]}
                    alt={field}
                    className="img-fluid rounded border"
                    style={{ maxHeight: "150px" }}
                  />
                )}
              </div>
            ))}
          </div>
        </div>

        <div className="col-12 text-center mt-3">
          <button type="submit" className="btn btn-success px-5">Save Owner</button>
        </div>
      </form>

      {/* Modal */}
      <div
        className={`modal fade ${showModal ? "show d-block" : ""}`}
        tabIndex="-1"
        role="dialog"
        style={{ backgroundColor: showModal ? "rgba(0,0,0,0.5)" : "transparent" }}
      >
        <div className="modal-dialog modal-dialog-centered" role="document">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title">Message</h5>
              <button type="button" className="btn-close" onClick={closeModal}></button>
            </div>
            <div className="modal-body">
              <p>{modalMessage}</p>
            </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-primary" onClick={closeModal}>
                OK
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddOwner;
