// ListOwner.jsx
import React, { useEffect, useState } from "react";

const OwnersList = () => {
  const [owners, setOwners] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");
  const [previewSrc, setPreviewSrc] = useState(null);

  const [editingOwner, setEditingOwner] = useState(null);
  const [editForm, setEditForm] = useState({});
  const [editFiles, setEditFiles] = useState({});
  const [editLoading, setEditLoading] = useState(false);

  const imageFields = ["eid_image", "passport_copy", "res_visa"];

  useEffect(() => {
    fetchOwners();
  }, []);

  const fetchOwners = async () => {
    try {
      setLoading(true);
      const res = await fetch("http://localhost:5000/owners_list", {
        credentials: "include",
      });
      const data = await res.json();
      if (res.ok && data.success) {
        setOwners(data.owners || []);
      } else {
        alert(data.message || "Failed to fetch owners");
      }
    } catch (err) {
      console.error(err);
      alert("Error fetching owners");
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (owner_code) => {
    if (!window.confirm("Are you sure you want to delete this owner?")) return;

    try {
      const res = await fetch("http://localhost:5000/delete_owner", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ owner_code }),
      });
      const data = await res.json();
      if (data.success) {
        alert("‚úÖ Owner deleted successfully");
        fetchOwners();
      } else {
        alert(data.message || "Failed to delete owner");
      }
    } catch (err) {
      console.error(err);
      alert("Error deleting owner");
    }
  };

  const openEditModal = (owner) => {
    setEditingOwner(owner);
    const form = {
      first_name: owner.first_name || "",
      last_name: owner.last_name || "",
      email: owner.email || "",
      mobile_number: owner.mobile_number || "",
      nationality: owner.nationality || "",
      address1: owner.address1 || "",
      address2: owner.address2 || "",
      city: owner.city || "",
      state: owner.state || "",
      country: owner.country || "",
      date_of_birth: owner.date_of_birth ? owner.date_of_birth.slice(0, 10) : "",
      visa_expiry: owner.visa_expiry ? owner.visa_expiry.slice(0, 10) : "",
      passport_expiry: owner.passport_expiry ? owner.passport_expiry.slice(0, 10) : "",
    };
    setEditForm(form);
    setEditFiles({});
  };

  const closeEditModal = () => {
    setEditingOwner(null);
    setEditForm({});
    setEditFiles({});
  };

  const onEditChange = (e) => {
    const { name, value } = e.target;
    setEditForm((p) => ({ ...p, [name]: value }));
  };

  const onEditFileChange = (e, key) => {
    const file = e.target.files[0];
    if (!file) return;
    setEditFiles((p) => ({ ...p, [key]: file }));
  };

  const submitEdit = async (e) => {
    e.preventDefault();
    if (!editingOwner) return;

    try {
      setEditLoading(true);
      const data = new FormData();
      data.append("owner_code", editingOwner.owner_code);

      Object.entries(editForm).forEach(([k, v]) => {
        if (v !== undefined) data.append(k, v ?? "");
      });

      if (editFiles.eid_image) data.append("OwnerEID", editFiles.eid_image);
      if (editFiles.passport_copy) data.append("OwnerPassportCopy", editFiles.passport_copy);
      if (editFiles.res_visa) data.append("OwnerResVisa", editFiles.res_visa);

      const res = await fetch("http://localhost:5000/update_owner", {
        method: "POST",
        credentials: "include",
        body: data,
      });

      const result = await res.json();
      if (res.ok && result.success) {
        alert("‚úÖ Owner updated successfully");
        fetchOwners();
        closeEditModal();
      } else {
        alert(result.message || "Failed to update owner");
      }
    } catch (err) {
      console.error(err);
      alert("Error updating owner");
    } finally {
      setEditLoading(false);
    }
  };

  const filteredOwners = owners.filter((o) => {
    const q = searchTerm.toLowerCase();
    return (
      o.first_name?.toLowerCase().includes(q) ||
      o.last_name?.toLowerCase().includes(q) ||
      o.email?.toLowerCase().includes(q) ||
      o.mobile_number?.toLowerCase().includes(q) ||
      o.nationality?.toLowerCase().includes(q)
    );
  });

  const renderThumb = (o, key) => {
    const val = o[key];
    if (!val) return <div style={{ fontSize: 12, color: "#666" }}>‚Äî</div>;
    const src =
      val.startsWith("http") || val.startsWith("/uploads")
        ? `http://localhost:5000${val.replace("http://localhost:5000", "")}`
        : `data:image/jpeg;base64,${val}`;
    return (
      <img
        src={src}
        alt={key}
        style={{ width: 48, height: 48, objectFit: "cover", borderRadius: 4, border: "1px solid #ccc", cursor: "pointer" }}
        onClick={() => setPreviewSrc(src)}
      />
    );
  };

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: 20, background: "#f0f2f5" }}>
      <h2 style={{ textAlign: "center", background: "#2f3640", color: "white", padding: 15, margin: 0 }}>Owner List</h2>

      <div style={{ maxWidth: 1500, margin: "20px auto", background: "white", padding: 16, borderRadius: 8, boxShadow: "0 0 12px rgba(0,0,0,0.12)", overflowX: "auto" }}>
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: 12 }}>
          <div style={{ fontWeight: "bold" }}>{new Date().toLocaleDateString()} | Total Owners: {owners.length}</div>
          <input
            placeholder="Search owner..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            style={{ padding: "8px 12px", borderRadius: 6, border: "1px solid #ccc", width: 350 }}
          />
          <a href="/index" style={{ backgroundColor: "gray", textDecoration: "none", color: "#333", fontWeight: "bold", padding: "8px 12px", borderRadius: 6 }}>
            ‚Üê Go Back Home
          </a>
        </div>

        {loading ? (
          <p>Loading...</p>
        ) : (
          <table style={{ width: "100%", borderCollapse: "collapse" }}>
            <thead>
              <tr>
                <th style={{ padding: 8, border: "1px solid #ddd", background: "#273c75", color: "#fff" }}>Owner Code</th>
                <th style={{ padding: 8, border: "1px solid #ddd", background: "#273c75", color: "#fff" }}>First Name</th>
                <th style={{ padding: 8, border: "1px solid #ddd", background: "#273c75", color: "#fff" }}>Last Name</th>
                <th style={{ padding: 8, border: "1px solid #ddd", background: "#273c75", color: "#fff" }}>Email</th>
                <th style={{ padding: 8, border: "1px solid #ddd", background: "#273c75", color: "#fff" }}>Mobile</th>
                <th style={{ padding: 8, border: "1px solid #ddd", background: "#273c75", color: "#fff" }}>Nationality</th>
                <th style={{ padding: 8, border: "1px solid #ddd", background: "#273c75", color: "#fff" }}>EID</th>
                <th style={{ padding: 8, border: "1px solid #ddd", background: "#273c75", color: "#fff" }}>Passport</th>
                <th style={{ padding: 8, border: "1px solid #ddd", background: "#273c75", color: "#fff" }}>Visa</th>
                <th style={{ padding: 8, border: "1px solid #ddd", background: "#273c75", color: "#fff" }}>Actions</th>
              </tr>
            </thead>

            <tbody>
              {filteredOwners.map((o, i) => (
                <tr key={o.owner_code || i} style={{ background: i % 2 === 0 ? "#fafafa" : "#fff" }}>
                  <td style={{ padding: 6, border: "1px solid #eee", textAlign: "center" }}>{`OW${o.owner_code.toString().padStart(5, "0")}`}</td>
                  <td style={{ padding: 6, border: "1px solid #eee" }}>{o.first_name}</td>
                  <td style={{ padding: 6, border: "1px solid #eee" }}>{o.last_name}</td>
                  <td style={{ padding: 6, border: "1px solid #eee" }}>{o.email}</td>
                  <td style={{ padding: 6, border: "1px solid #eee" }}>{o.mobile_number}</td>
                  <td style={{ padding: 6, border: "1px solid #eee" }}>{o.nationality}</td>
                  <td style={{ textAlign: "center" }}>{renderThumb(o, "eid_image")}</td>
                  <td style={{ textAlign: "center" }}>{renderThumb(o, "passport_copy")}</td>
                  <td style={{ textAlign: "center" }}>{renderThumb(o, "res_visa")}</td>
                  <td style={{ display: "flex", justifyContent: "center", gap: 8, padding: 6 }}>
                    <button onClick={() => openEditModal(o)} style={{ padding: "6px 10px", borderRadius: 6, cursor: "pointer" }}>
                      ‚úèÔ∏è Edit
                    </button>
                    <button onClick={() => handleDelete(o.owner_code)} style={{ padding: "6px 10px", borderRadius: 6, background: "#c0392b", color: "#fff", cursor: "pointer" }}>
                      üóëÔ∏è Delete
                    </button>
                  </td>
                </tr>
              ))}

              {filteredOwners.length === 0 && (
                <tr>
                  <td colSpan="10" style={{ padding: 16, textAlign: "center" }}>No owners found</td>
                </tr>
              )}
            </tbody>
          </table>
        )}
      </div>

      {/* Image Preview Modal */}
      {previewSrc && (
        <div
          onClick={() => setPreviewSrc(null)}
          style={{ position: "fixed", inset: 0, backgroundColor: "rgba(0,0,0,0.8)", display: "flex", justifyContent: "center", alignItems: "center", zIndex: 2000 }}
        >
          <img src={previewSrc} alt="preview" style={{ maxWidth: "90%", maxHeight: "90%", borderRadius: 6 }} />
        </div>
      )}

      {/* Edit Modal */}
      {editingOwner && (
        <div style={{ position: "fixed", inset: 0, background: "rgba(0,0,0,0.5)", display: "flex", alignItems: "center", justifyContent: "center", zIndex: 2000 }}>
          <div style={{ width: 800, maxHeight: "90vh", overflowY: "auto", background: "#fff", borderRadius: 8, padding: 16 }}>
            <h3>Edit Owner ‚Äî {`OW${editingOwner.owner_code.toString().padStart(5, "0")}`}</h3>
            <form onSubmit={submitEdit}>
              <div style={{ display: "grid", gridTemplateColumns: "repeat(3, 1fr)", gap: 12 }}>
                {Object.keys(editForm).map((key) => (
                  <div key={key}>
                    <label style={{ display: "block", fontWeight: "bold", marginBottom: 6 }}>{key.replace(/_/g, " ").toUpperCase()}</label>
                    {key.includes("expiry") || key.includes("date") ? (
                      <input type="date" name={key} value={editForm[key] || ""} onChange={onEditChange} style={{ width: "100%", padding: 8, borderRadius: 6, border: "1px solid #ccc" }} />
                    ) : (
                      <input type="text" name={key} value={editForm[key] || ""} onChange={onEditChange} style={{ width: "100%", padding: 8, borderRadius: 6, border: "1px solid #ccc" }} />
                    )}
                  </div>
                ))}
              </div>

              <hr style={{ margin: "16px 0" }} />

              <div style={{ display: "grid", gridTemplateColumns: "repeat(3,1fr)", gap: 12 }}>
                {imageFields.map((imgKey) => (
                  <div key={imgKey} style={{ textAlign: "center" }}>
                    <label style={{ display: "block", fontWeight: "bold", marginBottom: 6 }}>{imgKey.toUpperCase()}</label>
                    {editingOwner[imgKey] ? (
                      <img
                        src={editingOwner[imgKey].startsWith("data:")
                          ? editingOwner[imgKey]
                          : `data:image/jpeg;base64,${editingOwner[imgKey]}`
                        }
                        alt={imgKey}
                        style={{ width: 80, height: 80, objectFit: "cover", borderRadius: 6, border: "1px solid #ccc", marginBottom: 8 }}
                      />
                    ) : (
                      <div style={{ width: 80, height: 80, background: "#f3f3f3", borderRadius: 6, margin: "0 auto 8px" }} />
                    )}
                    <input type="file" onChange={(e) => onEditFileChange(e, imgKey)} />
                  </div>
                ))}
              </div>

              <div style={{ display: "flex", gap: 8, justifyContent: "flex-end", marginTop: 16 }}>
                <button type="button" onClick={closeEditModal} style={{ padding: "8px 12px", borderRadius: 6 }}>
                  Cancel
                </button>
                <button type="submit" disabled={editLoading} style={{ padding: "8px 12px", borderRadius: 6, background: "#2ecc71", color: "#fff" }}>
                  {editLoading ? "Saving..." : "Save Changes"}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default ListOwner;
