import React, { useEffect, useState } from "react";

const ListProperty = () => {
  const [properties, setProperties] = useState([]);
  const [currentDate, setCurrentDate] = useState("");
  const [editingProperty, setEditingProperty] = useState(null);
  const [editForm, setEditForm] = useState({});
  const [editLoading, setEditLoading] = useState(false);

  const photoFields = [
    "property_photo",
  ];

  useEffect(() => {
    fetchProperties();
    setCurrentDate(new Date().toLocaleDateString());
  }, []);

  const fetchProperties = async () => {
    try {
      const response = await fetch("http://localhost:5000/list_property_api"); // <-- API returning JSON
      const data = await response.json();
      setProperties(data);
    } catch (error) {
      console.error("Error fetching properties:", error);
      alert("Error fetching properties");
    }
  };

  const sortTable = (n) => {
    const sorted = [...properties].sort((a, b) => {
      const valA = Object.values(a)[n] ?? "";
      const valB = Object.values(b)[n] ?? "";
      if (!isNaN(valA) && !isNaN(valB)) return valA - valB;
      return valA.toString().localeCompare(valB.toString());
    });
    setProperties(sorted);
  };

  const openEditModal = (property) => {
    setEditingProperty(property);
    setEditForm({ ...property });
  };

  const closeEditModal = () => {
    setEditingProperty(null);
    setEditForm({});
  };

  const onEditChange = (e) => {
    const { name, value } = e.target;
    setEditForm((prev) => ({ ...prev, [name]: value }));
  };

  const onEditFileChange = (e, field) => {
    const file = e.target.files[0];
    if (!file) return;
    const reader = new FileReader();
    reader.onloadend = () => {
      setEditForm((prev) => ({ ...prev, [field]: reader.result.split(",")[1] }));
    };
    reader.readAsDataURL(file);
  };

  const submitEdit = async (e) => {
    e.preventDefault();
    setEditLoading(true);
    try {
      const res = await fetch(`http://localhost:5000/property_edit_api/${editingProperty.property_code}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(editForm),
      });
      const data = await res.json();
      if (res.ok && data.success) {
        alert("Property updated successfully");
        fetchProperties();
        closeEditModal();
      } else {
        alert(data.message || "Failed to update property");
      }
    } catch (err) {
      console.error(err);
      alert("Error updating property");
    } finally {
      setEditLoading(false);
    }
  };

  const deleteProperty = async (propertyCode) => {
    if (!window.confirm("Are you sure you want to delete this property?")) return;
    try {
      const res = await fetch(`http://localhost:5000/property_delete_api/${propertyCode}`, {
        method: "DELETE",
      });
      const data = await res.json();
      if (res.ok) {
        alert(data.message);
        fetchProperties();
      } else {
        alert(data.message || "Failed to delete property");
      }
    } catch (err) {
      console.error(err);
      alert("Error deleting property");
    }
  };

  const labelize = (s) =>
    s.replace(/_/g, " ").replace(/\b\w/g, (ch) => ch.toUpperCase());

  return (
    <div style={{ fontFamily: "Arial, sans-serif", background: "#f5f6fa", padding: "20px" }}>
      <h2 style={{ textAlign: "center", background: "#2f3640", color: "white", padding: "15px 0" }}>
        All Properties
      </h2>

      <div className="header-row" style={{ display: "flex", justifyContent: "space-between", margin: "15px" }}>
        <div>
          <span>{currentDate}</span> | <span>Total Properties: {properties.length}</span>
        </div>
        <div>
          <a href="/index" style={{ textDecoration: "none", color: "#333" }}>← Go Back Home</a>
        </div>
      </div>

      <div className="table-container" style={{ overflowX: "auto", background: "white", padding: "20px", borderRadius: "10px" }}>
        <table style={{ width: "100%", borderCollapse: "collapse" }}>
          <thead>
            <tr>
              {properties[0] && Object.keys(properties[0]).map((col, index) => (
                <th
                  key={index}
                  onClick={() => sortTable(index)}
                  style={{ cursor: "pointer", backgroundColor: "#2f3640", color: "white", padding: "8px" }}
                >
                  {labelize(col)}
                </th>
              ))}
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {properties.map((p, idx) => (
              <tr key={idx} style={{ backgroundColor: idx % 2 === 0 ? "#f0f0f0" : "white" }}>
                {Object.keys(p).map((key, i) => (
                  <td key={i} style={{ padding: 8, border: "1px solid #ccc", textAlign: "center" }}>
                    {photoFields.includes(key) && p[key] ? (
                      <img src={`data:image/jpeg;base64,${p[key]}`} alt={key} style={{ width: 60, height: 60, borderRadius: 6, border: "1px solid #ccc" }} />
                    ) : (
                      p[key]
                    )}
                  </td>
                ))}
                <td>
                  <button onClick={() => openEditModal(p)} style={{ marginRight: 6 }}>Edit</button>
                  <button onClick={() => deleteProperty(p.property_code)} style={{ background: "#c0392b", color: "#fff" }}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Edit Modal */}
      {editingProperty && (
        <div style={{
          position: "fixed", inset: 0, backgroundColor: "rgba(0,0,0,0.5)",
          display: "flex", alignItems: "center", justifyContent: "center", zIndex: 2000
        }}>
          <div style={{ width: 900, maxHeight: "90vh", overflowY: "auto", background: "#fff", borderRadius: 8, padding: 16 }}>
            <h3 style={{ marginTop: 0 }}>Edit Property — {editingProperty.property_code}</h3>

            <form onSubmit={submitEdit}>
              <div style={{ display: "grid", gridTemplateColumns: "repeat(3, 1fr)", gap: 12 }}>
                {Object.keys(editForm).map((key) => (
                  !photoFields.includes(key) && (
                    <div key={key}>
                      <label style={{ display: "block", fontWeight: "bold", marginBottom: 6 }}>{labelize(key)}</label>
                      <input
                        type={key.toLowerCase().includes("date") ? "date" : "text"}
                        name={key}
                        value={editForm[key] ?? ""}
                        onChange={onEditChange}
                        style={{ width: "100%", padding: 8, borderRadius: 6, border: "1px solid #ccc" }}
                      />
                    </div>
                  )
                ))}
              </div>

              <hr style={{ margin: "16px 0" }} />

              <div style={{ display: "grid", gridTemplateColumns: `repeat(${photoFields.length},1fr)`, gap: 12 }}>
                {photoFields.map((pf) => (
                  <div key={pf} style={{ textAlign: "center" }}>
                    <label style={{ display: "block", fontWeight: "bold", marginBottom: 6 }}>{labelize(pf)}</label>
                    {editingProperty[pf] ? (
                      <img
                        src={editingProperty[pf].startsWith("data:") ? editingProperty[pf] : `data:image/jpeg;base64,${editingProperty[pf]}`}
                        alt={pf}
                        style={{ width: 80, height: 80, objectFit: "cover", borderRadius: 6, border: "1px solid #ccc", display: "block", margin: "0 auto 8px" }}
                      />
                    ) : (
                      <div style={{ width: 80, height: 80, background: "#f3f3f3", borderRadius: 6, display: "inline-block", marginBottom: 8 }} />
                    )}
                    <input type="file" name={pf} onChange={(e) => onEditFileChange(e, pf)} />
                  </div>
                ))}
              </div>

              <div style={{ display: "flex", gap: 8, justifyContent: "flex-end", marginTop: 16 }}>
                <button
                  type="button"
                  onClick={closeEditModal}
                  style={{ padding: "8px 12px", borderRadius: 6, border: "1px solid #ccc" }}
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  disabled={editLoading}
                  style={{ padding: "8px 12px", borderRadius: 6, background: "#2ecc71", color: "#fff", border: "none" }}
                >
                  {editLoading ? "Saving..." : "Save Changes"}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default ListProperty;
