import React, { useState } from "react";

const AddContract = ({ sessionUser }) => {
  const [propertyCode, setPropertyCode] = useState("");
  const [tenantCode, setTenantCode] = useState("");
  const [property, setProperty] = useState(null);
  const [tenant, setTenant] = useState(null);
  const [formData, setFormData] = useState({});

  // ------------------- Search Property -------------------
  const handleSearchProperty = async (e) => {
    e.preventDefault();
    if (!propertyCode) return;

    try {
      const res = await fetch(`http://localhost:5000/property/${propertyCode}`, {
        method: "GET",
        credentials: "include",
      });
      const data = await res.json();
      if (res.ok && data.success) setProperty(data.property);
      else alert(data.message || "Property not found");
    } catch (err) {
      console.error(err);
      alert("Error fetching property details");
    }
  };

  // ------------------- Search Tenant -------------------
  const handleSearchTenant = async (e) => {
    e.preventDefault();
    if (!tenantCode) return;

    try {
      const res = await fetch(`http://localhost:5000/tenancycontracts/${tenantCode}`, {
        method: "GET",
        credentials: "include",
      });
      const data = await res.json();
      if (res.ok && data.success) setTenant(data.contract);
      else alert(data.message || "Tenant not found");
    } catch (err) {
      console.error(err);
      alert("Error fetching tenant details");
    }
  };

  // ------------------- Input Change -------------------
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // ------------------- Submit Contract -------------------
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!property || !tenant)
      return alert("Please search and select both Property and Tenant first");

    const data = new FormData();
    data.append("property_id", property.property_code);
    data.append("tenant_id", tenant.TenancyContractNumber);
    data.append("tenant_name", tenant.TenantName);

    Object.keys(formData).forEach((key) => {
      data.append(key, formData[key]);
    });

    if (!formData.created_by) data.append("created_by", sessionUser || "admin");
    if (!formData.creation_date)
      data.append("creation_date", new Date().toISOString().split("T")[0]);

    try {
      const res = await fetch("http://localhost:5000/add_contract", {
        method: "POST",
        credentials: "include",
        body: data,
      });

      const result = await res.json();
      if (res.ok && result.success) {
        alert(`‚úÖ ${result.message}`);
      } else {
        alert(`‚ùå ${result.message || "Failed to add contract"}`);
      }
    } catch (err) {
      console.error("FETCH FAILED:", err);
      alert("‚ùå Error submitting contract");
    }
  };

  // ------------------- Contract Fields -------------------
  const contractFields = [
    ["contract_start_date", "contract_end_date", "contract_termination_date", "move_in_date", "move_out_date"],
    ["first_time_move_in_date", "contract_renewal_date", "rent_amount", "deposit_amount", "notice_period"],
    ["rent_amount_description", "rent_payment_mode", "rent_payment_type", "deposit_payment_type", "new_or_old_tenant"],
    ["created_by", "creation_date"]
  ];

  // ------------------- Enum Options -------------------
  const paymentModes = ["cash", "cheque", "online", "card"];
  const rentTypes = ["monthly", "yearly", "halfyearly"];

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: "20px" }}>
      <h2 style={{ textAlign: "center", background: "#2f3640", color: "white", padding: "15px 0" }}>
        Add Contract
      </h2>

      {/* Property Search */}
      <form onSubmit={handleSearchProperty,h} style={{ textAlign: "center", margin: "20px 0" }}>
        <label>Enter Property Code:</label>
        <input
          type="text"
          value={propertyCode}
          onChange={(e) => setPropertyCode(e.target.value)}
          placeholder="Ex: 101"
          required
          style={{ margin: "0 10px", padding: "5px", width: 100 }}
        />
        <button type="submit" style={{ padding: "5px 10px", cursor: "pointer", width: 100 }}>
          Search
        </button>
      </form>

      {/* Tenant Search */}
      <form onSubmit={handleSearchTenant} style={{ textAlign: "center", margin: "20px 0" }}>
        <label>Enter Tenant Contract No:</label>
        <input
          type="text"
          value={tenantCode}
          onChange={(e) => setTenantCode(e.target.value)}
          placeholder="Ex: 2001"
          required
          style={{ margin: "0 10px", padding: "5px", width: 120 }}
        />
        <button type="submit" style={{ padding: "5px 10px", cursor: "pointer", width: 100 }}>
          Search
        </button>
      </form>

      {property && tenant && (
        <>
          <p style={{ textAlign: "center", fontWeight: "bold", marginBottom: "20px" }}>
            <span>üè† Property:</span> {property.name} | Code: {property.property_code}
            <br />
            <span>üë§ Tenant:</span> {tenant.TenantName} | Email: {tenant.TenantEmailID}
          </p>

          <form onSubmit={handleSubmit}>
            {contractFields.map((row, i) => (
              <div
                key={i}
                style={{
                  display: "grid",
                  gridTemplateColumns: "repeat(5, 1fr)",
                  gap: "10px",
                  marginBottom: "10px",
                }}
              >
                {row.map((field) => {
                  const value =
                    field === "created_by"
                      ? sessionUser || "admin"
                      : field === "creation_date"
                      ? new Date().toISOString().split("T")[0]
                      : formData[field] || "";

                  const isDateField = field.includes("date") || field.includes("Date");

                  if (field === "rent_payment_mode" || field === "deposit_payment_type") {
                    return (
                      <div key={field}>
                        <label style={{ fontWeight: "bold" }}>
                          {field.replace(/_/g, " ").toUpperCase()}
                        </label>
                        <select name={field} value={value} onChange={handleChange}>
                          <option value="">Select</option>
                          {paymentModes.map((mode) => (
                            <option key={mode} value={mode}>
                              {mode}
                            </option>
                          ))}
                        </select>
                      </div>
                    );
                  }

                  if (field === "rent_payment_type") {
                    return (
                      <div key={field}>
                        <label style={{ fontWeight: "bold" }}>
                          {field.replace(/_/g, " ").toUpperCase()}
                        </label>
                        <select name={field} value={value} onChange={handleChange}>
                          <option value="">Select</option>
                          {rentTypes.map((type) => (
                            <option key={type} value={type}>
                              {type}
                            </option>
                          ))}
                        </select>
                      </div>
                    );
                  }

                  if (field === "new_or_old_tenant") {
                    return (
                      <div key={field}>
                        <label style={{ fontWeight: "bold" }}>
                          {field.replace(/_/g, " ").toUpperCase()}
                        </label>
                        <div>
                          <label>
                            <input
                              type="radio"
                              name={field}
                              value="new"
                              checked={value === "new"}
                              onChange={handleChange}
                            />
                            New
                          </label>
                          <label style={{ marginLeft: "10px" }}>
                            <input
                              type="radio"
                              name={field}
                              value="old"
                              checked={value === "old"}
                              onChange={handleChange}
                            />
                            Old
                          </label>
                        </div>
                      </div>
                    );
                  }

                  return (
                    <div key={field}>
                      <label style={{ fontWeight: "bold" }}>
                        {field.replace(/_/g, " ").toUpperCase()}
                      </label>
                      <input
                        type={isDateField ? "date" : "text"}
                        name={field}
                        value={value}
                        onChange={handleChange}
                      />
                    </div>
                  );
                })}
              </div>
            ))}

            <div style={{ textAlign: "center", marginTop: "30px" }}>
              <a
                href="/index"
                style={{
                  ...styles.button,
                  backgroundColor: "#492bc0ff",
                  marginRight: "20px",
                  textDecoration: "none",
                  display: "inline-block",
                }}
              >
                ‚Üê Go Back Home
              </a>
              <button type="submit" style={styles.submitButton}>
                Add Contract
              </button>
            </div>
          </form>
        </>
      )}
    </div>
  );
};

const styles = {
  button: {
    width: 200,
    padding: "10px 20px",
    backgroundColor: "#0097e6",
    color: "white",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer",
    fontSize: "16px",
  },
  submitButton: {
    width: 200,
    padding: "10px 20px",
    backgroundColor: "#27ae60",
    color: "white",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer",
    fontSize: "16px",
  },
};

export default AddContract;
