import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";

const AddFinTrans = () => {
  const [contractId, setContractId] = useState("");
  const [formData, setFormData] = useState({
    mode_of_payment: "",
    tr_date: "",
    tr_amount: "",
    reference_number: "",
    cheque_date: "",
    cheque_number: "",
  });
  const [details, setDetails] = useState(null);
  const location = useLocation();
  const navigate = useNavigate();

  // get contract_id from query param
  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const id = params.get("contract_id");
    if (id) {
      setContractId(id);
      fetchContract(id);
    }
  }, [location]);

  // fetch contract details
  const fetchContract = async (id) => {
    try {
      const res = await fetch(`http://localhost:5000/contract/${id}`);
      const data = await res.json();
      if (data.success && data.contract) {
        setDetails(data.contract);
      } else alert("Contract not found!");
    } catch (err) {
      console.error("Error fetching contract:", err);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!contractId) return alert("Contract ID missing!");

    const payload = {
      contract_id: contractId,
      ...formData,
    };

    try {
      const res = await fetch("http://localhost:5000/add_fintrans", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });

      const data = await res.json();

      if (res.ok && data.success) {
        alert("✅ Transaction saved successfully!");
        navigate("/addcontract"); // redirect back
      } else {
        alert(`❌ ${data.message || "Error saving transaction"}`);
      }
    } catch (err) {
      console.error(err);
      alert("Error submitting transaction!");
    }
  };

  if (!details) return <p>Loading contract details...</p>;

  const { tenant_id, tenant_name, property_id } = details;

  return (
    <div style={{ maxWidth: 800, margin: "0 auto", fontFamily: "Arial" }}>
      <h2 style={{ textAlign: "center", background: "#2f3640", color: "white", padding: 15 }}>
        Add Financial Transaction
      </h2>

      <form onSubmit={handleSubmit} style={{ padding: 20, background: "white", borderRadius: 10, boxShadow: "0 0 10px #ccc" }}>
        <div style={{ marginBottom: 15 }}>
          <label>Contract ID:</label>
          <input type="text" value={contractId} readOnly />
        </div>

        <div style={{ marginBottom: 15 }}>
          <label>Tenant ID:</label>
          <input type="text" value={tenant_id} readOnly />
        </div>

        <div style={{ marginBottom: 15 }}>
          <label>Tenant Name:</label>
          <input type="text" value={tenant_name} readOnly />
        </div>

        <div style={{ marginBottom: 15 }}>
          <label>Property ID:</label>
          <input type="text" value={property_id} readOnly />
        </div>

        <div style={{ marginBottom: 15 }}>
          <label>Mode of Payment:</label>
          <select name="mode_of_payment" value={formData.mode_of_payment} onChange={handleChange}>
            <option value="">Select</option>
            <option value="Cash">Cash</option>
            <option value="Cheque">Cheque</option>
            <option value="Online">Online</option>
          </select>
        </div>

        {/* conditional fields */}
        {formData.mode_of_payment === "Cash" && (
          <>
            <label>Transaction Date:</label>
            <input type="date" name="tr_date" value={formData.tr_date} onChange={handleChange} />
            <label>Amount:</label>
            <input type="number" name="tr_amount" value={formData.tr_amount} onChange={handleChange} />
          </>
        )}

        {formData.mode_of_payment === "Cheque" && (
          <>
            <label>Cheque Number:</label>
            <input type="text" name="cheque_number" value={formData.cheque_number} onChange={handleChange} />
            <label>Cheque Date:</label>
            <input type="date" name="cheque_date" value={formData.cheque_date} onChange={handleChange} />
            <label>Amount:</label>
            <input type="number" name="tr_amount" value={formData.tr_amount} onChange={handleChange} />
          </>
        )}

        {formData.mode_of_payment === "Online" && (
          <>
            <label>Reference Number:</label>
            <input type="text" name="reference_number" value={formData.reference_number} onChange={handleChange} />
            <label>Transaction Date:</label>
            <input type="date" name="tr_date" value={formData.tr_date} onChange={handleChange} />
            <label>Amount:</label>
            <input type="number" name="tr_amount" value={formData.tr_amount} onChange={handleChange} />
          </>
        )}

        <div style={{ textAlign: "center", marginTop: 20 }}>
          <button type="submit" style={{ padding: "10px 20px", background: "#27ae60", color: "white", border: "none", borderRadius: 5 }}>
            Save Transaction
          </button>
        </div>
      </form>
    </div>
  );
};

export default AddFinTrans;
