import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const AddFinTrans = () => {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    PropertyCode: "",
    TenancyContractNumber: "",
    ReceiptPayment: "Receipt",
    ReceiptPaymentReason: "",
    ModeOfPayment: "",
    TrDate: new Date().toISOString().split("T")[0],
    TrAmount: "",
    ReferenceNumber: "",
    ChequeDate: "",
  });

  // ✅ Load pending contract data
  useEffect(() => {
    const savedContract = localStorage.getItem("pending_contract");
    if (!savedContract) {
      alert("No contract found. Redirecting back...");
      navigate("/addcontract");
      return;
    }

    const contract = JSON.parse(savedContract);
    setFormData((prev) => ({
      ...prev,
      PropertyCode: contract.property_code,
      TenancyContractNumber: contract.contract_id,
    }));
  }, [navigate]);

  // ✅ Handle form input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // ✅ Submit financial transaction
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://localhost:5000/add_fintrans", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData),
      });

      const result = await response.json();

      if (response.ok && result.success) {
        alert(`✅ Financial Transaction #${result.FinTransRefNo} added successfully!`);

        // Clear temp contract after success
        localStorage.removeItem("pending_contract");

        // Redirect back to AddContract page
        navigate("/addcontract");
      } else {
        alert("❌ Failed to add financial transaction!");
      }
    } catch (err) {
      console.error("Error adding transaction:", err);
      alert("⚠️ Error while adding transaction!");
    }
  };

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: "20px" }}>
      <h2 style={{ textAlign: "center", background: "#2f3640", color: "white", padding: "15px 0" }}>
        Add Financial Transaction
      </h2>

      <form onSubmit={handleSubmit} style={{ maxWidth: "700px", margin: "0 auto" }}>
        <div style={{ display: "grid", gridTemplateColumns: "repeat(2, 1fr)", gap: "15px" }}>
          <div>
            <label>Property Code:</label>
            <input type="text" name="PropertyCode" value={formData.PropertyCode} readOnly style={{ background: "#eee" }} />
          </div>

          <div>
            <label>Tenancy Contract #:</label>
            <input
              type="text"
              name="TenancyContractNumber"
              value={formData.TenancyContractNumber}
              readOnly
              style={{ background: "#eee" }}
            />
          </div>

          <div>
            <label>Receipt / Payment:</label>
            <select name="ReceiptPayment" value={formData.ReceiptPayment} onChange={handleChange}>
              <option value="Receipt">Receipt</option>
              <option value="Payment">Payment</option>
            </select>
          </div>

          <div>
            <label>Reason:</label>
            <input
              type="text"
              name="ReceiptPaymentReason"
              value={formData.ReceiptPaymentReason}
              onChange={handleChange}
              placeholder="e.g. Rent for Jan"
            />
          </div>

          <div>
            <label>Mode of Payment:</label>
            <select name="ModeOfPayment" value={formData.ModeOfPayment} onChange={handleChange}>
              <option value="">Select</option>
              <option value="Cash">Cash</option>
              <option value="Check">Check</option>
              <option value="Online">Online</option>
            </select>
          </div>

          <div>
            <label>Transaction Date:</label>
            <input type="date" name="TrDate" value={formData.TrDate} onChange={handleChange} />
          </div>

          <div>
            <label>Transaction Amount:</label>
            <input
              type="number"
              name="TrAmount"
              value={formData.TrAmount}
              onChange={handleChange}
              placeholder="e.g. 1500"
            />
          </div>

          <div>
            <label>Reference Number:</label>
            <input
              type="text"
              name="ReferenceNumber"
              value={formData.ReferenceNumber}
              onChange={handleChange}
              placeholder="e.g. CHQ1234"
            />
          </div>

          <div>
            <label>Cheque Date:</label>
            <input type="date" name="ChequeDate" value={formData.ChequeDate} onChange={handleChange} />
          </div>
        </div>

        <div style={{ textAlign: "center", marginTop: "30px" }}>
          <button
            type="submit"
            style={{
              width: 200,
              padding: "10px 20px",
              backgroundColor: "#27ae60",
              color: "white",
              border: "none",
              borderRadius: "6px",
              cursor: "pointer",
              fontSize: "16px",
            }}
          >
            Submit Transaction
          </button>
        </div>
      </form>
    </div>
  );
};

export default AddFinTrans;
