import React, { useState } from "react";

const DeleteContract = () => {
  const [searchId, setSearchId] = useState("");
  const [contract, setContract] = useState(null);
  const [message, setMessage] = useState("");

  // -------- Search Contract --------
  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchId) return;

    try {
      const res = await fetch(`http://localhost:5000/contract/${searchId}`);
      const data = await res.json();

      if (data.success && data.contract) {
        setContract(data.contract);
        setMessage("Contract found");
      } else {
        setContract(null);
        setMessage(data.message || "No contract found");
      }
    } catch (err) {
      console.error(err);
      setMessage("Error fetching contract");
      setContract(null);
    }
  };

  // -------- Delete Contract --------
  const handleDelete = async (e) => {
    e.preventDefault();
    if (!contract) return;

    if (!window.confirm(`Are you sure you want to delete contract ${contract.contract_id}?`)) return;

    const formData = new FormData();
    formData.append("contract_id", contract.contract_id);

    try {
      const res = await fetch("http://localhost:5000/delete_contract", {
        method: "POST",
        credentials: "include",
        body: formData,
      });
      const data = await res.json();

      if (data.success) {
        setMessage(`Contract ${contract.contract_id} deleted successfully`);
        setContract(null);
        setSearchId("");
      } else {
        setMessage(data.message || "Failed to delete contract");
      }
    } catch (err) {
      console.error(err);
      setMessage("Error deleting contract");
    }
  };

  // -------- Render Contract Fields --------
  const renderFields = () => {
    if (!contract) return null;

    const fields = Object.keys(contract);
    return fields.map((field, idx) => {
      let value = contract[field] ?? "";
      if (field.includes("date") && value) value = value.split("T")[0];

      return (
        <div key={idx} style={{ display: "flex", flexDirection: "column", minWidth: "180px", marginBottom: "15px" }}>
          <label style={{ fontWeight: "bold", marginBottom: "3px" }}>{field.replace(/_/g," ").toUpperCase()}</label>
          <input
            type={field.includes("date") ? "date" : "text"}
            value={value}
            readOnly
            style={{
              padding: "6px",
              borderRadius: "5px",
              border: "1px solid #ccc",
              background: "#f0f0f0",
              width: "100%"
            }}
          />
        </div>
      );
    });
  };

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: "20px", background: "#f5f6fa" }}>
      <h2 style={{ textAlign: "center", background: "#2f3640", color: "white", padding: "15px 0" }}>Delete Contract</h2>

      {/* Search */}
      <div style={{ textAlign: "center", margin: "20px 0" }}>
        <form onSubmit={handleSearch}>
          <label style={{ fontWeight: "bold", marginRight: "10px" }}>Enter Contract ID:</label>
          <input
            type="text"
            value={searchId}
            onChange={(e) => setSearchId(e.target.value)}
            style={{ width: "120px", padding: "6px", borderRadius: "5px", border: "1px solid #aaa" }}
          />
          <button
            type="submit"
            style={{ width:100, padding: "6px 12px", background: "#c0392b", color: "white", border: "none", borderRadius: "5px", marginLeft: "10px", cursor: "pointer" }}
          >
            Search
          </button>
        </form>
        {message && <p style={{ marginTop: "10px", fontWeight: "bold" }}>{message}</p>}
      </div>

      {/* Contract Details */}
      {contract && (
        <form onSubmit={handleDelete} style={{
          background: "white",
          width: "100%",
          maxWidth: "1200px",
          margin: "0 auto",
          padding: "20px",
          borderRadius: "10px",
          boxShadow: "0 0 10px #ccc"
        }}>
          <div style={{ display: "flex", flexWrap: "wrap", gap: "15px" }}>
            {renderFields()}
          </div>

          {/* Buttons */}
          <div style={{ textAlign: "center", marginTop: "30px" }}>
            <a
              href="/index"
              style={{
                width:200,
                padding: "10px 20px",
                backgroundColor: "#492bc0ff",
                color: "white",
                border: "none",
                borderRadius: "6px",
                cursor: "pointer",
                fontSize: "16px",
                marginRight: "20px",
                textDecoration: "none",
                display: "inline-block"
              }}
            >
              ‚Üê Go Back Home
            </a>
            <button
              type="submit"
              style={{
                width:200,
                padding: "10px 20px",
                backgroundColor: "#c0392b",
                color: "white",
                border: "none",
                borderRadius: "6px",
                cursor: "pointer",
                fontSize: "16px"
              }}
            >
              Delete Contract
            </button>
          </div>
        </form>
      )}
    </div>
  );
};

export default DeleteContract;
