import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const AddFinTrans = () => {
  const navigate = useNavigate();
  const [contract, setContract] = useState(null);
  const [formData, setFormData] = useState({
    mode_of_payment: "",
    tr_date: "",
    tr_amount: "",
    reference_number: "",
    cheque_date: "",
  });

  const contractId = localStorage.getItem("latest_contract_id");

  useEffect(() => {
    if (!contractId) {
      alert("No contract found. Redirecting back...");
      navigate("/addcontract");
      return;
    }

    // Fetch contract details
    fetch(`http://localhost:5000/api/get_contract/${contractId}`)
      .then((res) => res.json())
      .then((data) => {
        if (data.success) setContract(data.contract);
        else alert("Contract not found");
      })
      .catch(() => alert("Error fetching contract details"));
  }, [contractId, navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSave = async () => {
    if (!formData.mode_of_payment) return alert("Select payment mode");

    try {
      const res = await fetch("http://localhost:5000/add_fintrans", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          contract_id: contractId,
          ...formData,
        }),
      });

      const data = await res.json();
      if (data.success) {
        alert("✅ Transaction saved and confirmation email sent!");
        localStorage.removeItem("latest_contract_id");
        navigate("/addcontract");
      } else {
        alert("❌ Failed to save transaction");
      }
    } catch (err) {
      alert("Error saving transaction");
    }
  };

  if (!contract) return <p>Loading contract details...</p>;

  return (
    <div style={{ padding: "20px" }}>
      <h2 style={{ textAlign: "center", background: "#2f3640", color: "white", padding: "10px" }}>
        Add Financial Transaction
      </h2>

      {/* Contract Info */}
      <div style={{ marginBottom: "20px" }}>
        <h3>Contract ID: {contractId}</h3>
        <p>
          <strong>Tenant:</strong> {contract.tenant_name} (ID: {contract.tenant_id})<br />
          <strong>Property:</strong> {contract.property_id}<br />
          <strong>Rent Amount:</strong> {contract.rent_amount}
        </p>
      </div>

      {/* Payment Form */}
      <div>
        <label>Mode of Payment:</label>
        <select name="mode_of_payment" value={formData.mode_of_payment} onChange={handleChange}>
          <option value="">Select</option>
          <option value="Cash">Cash</option>
          <option value="Cheque">Cheque</option>
          <option value="Online">Online</option>
        </select>

        {/* Conditional fields */}
        {formData.mode_of_payment === "Cash" && (
          <>
            <label>Transaction Date:</label>
            <input type="date" name="tr_date" onChange={handleChange} />
            <label>Amount:</label>
            <input type="number" name="tr_amount" onChange={handleChange} />
          </>
        )}

        {formData.mode_of_payment === "Cheque" && (
          <>
            <label>Cheque Date:</label>
            <input type="date" name="cheque_date" onChange={handleChange} />
            <label>Amount:</label>
            <input type="number" name="tr_amount" onChange={handleChange} />
          </>
        )}

        {formData.mode_of_payment === "Online" && (
          <>
            <label>Reference Number:</label>
            <input type="text" name="reference_number" onChange={handleChange} />
            <label>Amount:</label>
            <input type="number" name="tr_amount" onChange={handleChange} />
          </>
        )}

        <div style={{ marginTop: "20px" }}>
          <button
            onClick={handleSave}
            style={{
              padding: "10px 20px",
              background: "#27ae60",
              color: "white",
              border: "none",
              borderRadius: 6,
              cursor: "pointer",
            }}
          >
            Save Transaction
          </button>
        </div>
      </div>
    </div>
  );
};

export default AddFinTrans;
