// OwnersList.jsx
import React, { useEffect, useState } from "react";

const OwnersList = () => {
  const [owners, setOwners] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [sortField, setSortField] = useState("owner_code");
  const [sortOrder, setSortOrder] = useState("ASC");

  // Fetch owners from Flask API
  const fetchOwners = async () => {
    setLoading(true);
    setError("");
    try {
      const res = await fetch(
        `http://localhost:5000/api/owners?sort=${sortField}&order=${sortOrder}`,
        {
          method: "GET",
          credentials: "include", // send session cookie
          headers: { "Accept": "application/json" },
        }
      );

      const data = await res.json();

      if (res.status === 401) {
        setError("❌ You must log in first.");
        setOwners([]);
      } else if (data.success) {
        setOwners(data.owners);
        setError("");
      } else {
        setError(data.message || "Error fetching owners.");
      }
    } catch (err) {
      setError("Error connecting to server.");
      setOwners([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchOwners();
  }, [sortField, sortOrder]);

  // Toggle sorting
  const handleSort = (field) => {
    if (field === sortField) {
      setSortOrder(sortOrder === "ASC" ? "DESC" : "ASC");
    } else {
      setSortField(field);
      setSortOrder("ASC");
    }
  };

  if (loading) return <p>Loading owners...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;
  if (!owners.length) return <p>No owners found.</p>;

  return (
    <div>
      <h2>Owners List</h2>
      <table border="1" cellPadding="5" style={{ borderCollapse: "collapse" }}>
        <thead>
          <tr>
            <th onClick={() => handleSort("owner_code")}>Owner Code</th>
            <th onClick={() => handleSort("first_name")}>First Name</th>
            <th onClick={() => handleSort("last_name")}>Last Name</th>
            <th onClick={() => handleSort("email")}>Email</th>
            <th onClick={() => handleSort("mobile_number")}>Mobile</th>
            <th onClick={() => handleSort("nationality")}>Nationality</th>
          </tr>
        </thead>
        <tbody>
          {owners.map((owner) => (
            <tr key={owner.owner_code}>
              <td>{owner.owner_code}</td>
              <td>{owner.first_name}</td>
              <td>{owner.last_name}</td>
              <td>{owner.email}</td>
              <td>{owner.mobile_number}</td>
              <td>{owner.nationality}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default OwnersList;
