import React, { useState } from "react";

const EditFinTrans = ({ sessionUser }) => {
  const [finTransRefNo, setFinTransRefNo] = useState("");
  const [transaction, setTransaction] = useState(null);
  const [formData, setFormData] = useState({});
  const [loading, setLoading] = useState(false);

  const parseDate = (val) => {
    if (!val) return "";
    const d = new Date(val);
    if (isNaN(d)) return "";
    const month = (d.getMonth() + 1).toString().padStart(2, "0");
    const day = d.getDate().toString().padStart(2, "0");
    return `${d.getFullYear()}-${month}-${day}`;
  };

  const labelize = (s) =>
    s.replace(/_/g, " ").replace(/\b\w/g, (ch) => ch.toUpperCase());

  const fields = [
    "FinTransRefNo",
    "PropertyCode",
    "TenantID",
    "TenantName",
    "TenancyContractNumber",
    "ReceiptPayment",
    "ReceiptPaymentReason",
    "ModeOfPayment",
    "TrDate",
    "TrAmount",
    "ReferenceNumber",
    "ChequeDate",
    "BankName",
    "BankCity",
    "IFSCCode",
    "ChequeStatus",
    "CreatedBy",
    "CreationDate",
  ];

  const modeOptions = ["Cash", "Check", "Online"];
  const receiptOptions = ["Receipt", "Payment"];
  const chequeStatuses = ["Pending", "Cleared", "Bounced", "Cancelled"];

  // ------------------- Search Transaction -------------------
const handleSearch = async (e) => {
  e?.preventDefault();
  if (!finTransRefNo) return alert("Please enter Transaction Reference Number");

  try {
    setLoading(true);
    const res = await fetch(`http://localhost:5000/fintrans/${finTransRefNo}`);
    const data = await res.json();

    if (res.ok && data.success && data.transaction) {
      setTransaction(data.transaction);
      // populate your form fields
    } else {
      alert(data.message || "Transaction not found");
    }
  } catch (err) {
    console.error("Error fetching transaction:", err);
    alert("Error fetching transaction");
  } finally {
    setLoading(false);
  }
};

  // ------------------- Input Change -------------------
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // ------------------- Update Transaction -------------------
  const handleUpdate = async (e) => {
    e.preventDefault();
    if (!transaction) return alert("Search a transaction first");

    const data = new FormData();
    data.append("FinTransRefNo", transaction.FinTransRefNo || finTransRefNo);

    Object.entries(formData).forEach(([key, val]) => {
      if (val != null) data.append(key, val);
    });

    try {
      setLoading(true);
      const res = await fetch("http://localhost:5000/modify_fintrans", {
        method: "POST",
        credentials: "include",
        body: data,
      });
      const result = await res.json();

      if (res.ok && result.success) {
        alert(`✅ ${result.message}`);
        await handleSearch(); // Refresh updated details
      } else alert(`❌ ${result.message || "Failed to update transaction"}`);
    } catch (err) {
      console.error(err);
      alert("❌ Error updating transaction");
    } finally {
      setLoading(false);
    }
  };

  const getValue = (key) => formData[key] ?? transaction?.[key] ?? "";
  const readOnlyFields = ["FinTransRefNo", "PropertyCode", "TenantID", "TenantName"];

  // ------------------- Render -------------------
  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: 20 }}>
      <h2
        style={{
          textAlign: "center",
          background: "#2f3640",
          color: "white",
          padding: 15,
          margin: 0,
        }}
      >
        Edit Financial Transaction
      </h2>

      {/* Search */}
      <form
        onSubmit={handleSearch}
        style={{ textAlign: "center", margin: "20px 0" }}
      >
        <label style={{ fontWeight: "bold", marginRight: 8 }}>
          Enter FinTransRefNo:
        </label>
        <input
          type="text"
          value={finTransRefNo}
          onChange={(e) => setFinTransRefNo(e.target.value)}
          placeholder="Ex: 1001"
          required
          style={{
            width: 150,
            padding: 6,
            borderRadius: 5,
            border: "1px solid #aaa",
          }}
        />
        <button
          type="submit"
          style={{
            marginLeft: 8,
            padding: "6px 12px",
            background: "#273c75",
            color: "white",
            border: "none",
            borderRadius: 5,
            cursor: "pointer",
            width: 200,
          }}
        >
          {loading ? "Searching..." : "Search"}
        </button>
      </form>

      {/* Transaction Form */}
      {transaction && (
        <form
          onSubmit={handleUpdate}
          encType="multipart/form-data"
          style={{
            maxWidth: 1200,
            margin: "0 auto",
            background: "white",
            padding: 20,
            borderRadius: 10,
            boxShadow: "0 0 10px #ccc",
          }}
        >
          {fields
            .reduce((rows, field, i) => {
              if (i % 4 === 0) rows.push([]);
              rows[rows.length - 1].push(field);
              return rows;
            }, [])
            .map((cols, i) => (
              <div
                key={i}
                style={{
                  display: "grid",
                  gridTemplateColumns: `repeat(${cols.length}, 1fr)`,
                  gap: 15,
                  marginBottom: 15,
                }}
              >
                {cols.map((field) => {
                  const isDate =
                    field.toLowerCase().includes("date") && field !== "ChequeStatus";
                  const isReadOnly = readOnlyFields.includes(field);

                  if (field === "ModeOfPayment") {
                    return (
                      <div key={field}>
                        <label
                          style={{
                            fontWeight: "bold",
                            display: "block",
                            marginBottom: 6,
                          }}
                        >
                          {labelize(field)}
                        </label>
                        <select
                          name={field}
                          value={getValue(field)}
                          onChange={handleChange}
                        >
                          <option value="">Select</option>
                          {modeOptions.map((opt) => (
                            <option key={opt} value={opt}>
                              {opt}
                            </option>
                          ))}
                        </select>
                      </div>
                    );
                  }

                  if (field === "ReceiptPayment") {
                    return (
                      <div key={field}>
                        <label
                          style={{
                            fontWeight: "bold",
                            display: "block",
                            marginBottom: 6,
                          }}
                        >
                          {labelize(field)}
                        </label>
                        <select
                          name={field}
                          value={getValue(field)}
                          onChange={handleChange}
                        >
                          <option value="">Select</option>
                          {receiptOptions.map((opt) => (
                            <option key={opt} value={opt}>
                              {opt}
                            </option>
                          ))}
                        </select>
                      </div>
                    );
                  }

                  if (field === "ChequeStatus") {
                    return (
                      <div key={field}>
                        <label
                          style={{
                            fontWeight: "bold",
                            display: "block",
                            marginBottom: 6,
                          }}
                        >
                          {labelize(field)}
                        </label>
                        <select
                          name={field}
                          value={getValue(field)}
                          onChange={handleChange}
                        >
                          <option value="">Select</option>
                          {chequeStatuses.map((status) => (
                            <option key={status} value={status}>
                              {status}
                            </option>
                          ))}
                        </select>
                      </div>
                    );
                  }

                  return (
                    <div key={field}>
                      <label
                        style={{
                          fontWeight: "bold",
                          display: "block",
                          marginBottom: 6,
                        }}
                      >
                        {labelize(field)}
                      </label>
                      <input
                        type={isDate ? "date" : "text"}
                        name={field}
                        value={getValue(field)}
                        onChange={isReadOnly ? undefined : handleChange}
                        readOnly={isReadOnly}
                        style={{
                          width: "100%",
                          padding: 7,
                          borderRadius: 6,
                          border: "1px solid #ccc",
                          backgroundColor: isReadOnly ? "#f0f0f0" : "white",
                          cursor: isReadOnly ? "not-allowed" : "text",
                        }}
                      />
                    </div>
                  );
                })}
              </div>
            ))}

          <div style={{ textAlign: "center", marginTop: 30 }}>
            <a
              href="/index"
              style={{
                ...styles.button,
                backgroundColor: "#492bc0ff",
                marginRight: 20,
                textDecoration: "none",
                display: "inline-block",
              }}
            >
              ← Go Back Home
            </a>
            <button type="submit" style={styles.updateButton}>
              Update Transaction
            </button>
          </div>
        </form>
      )}
    </div>
  );
};

const styles = {
  button: {
    width: 200,
    padding: "10px 20px",
    backgroundColor: "#0097e6",
    color: "white",
    border: "none",
    borderRadius: 6,
    cursor: "pointer",
    fontSize: 16,
  },
  updateButton: {
    width: 200,
    padding: "10px 20px",
    backgroundColor: "#27ae60",
    color: "white",
    border: "none",
    borderRadius: 6,
    cursor: "pointer",
    fontSize: 16,
  },
};

export default EditFinTrans;
