import React, { useState } from "react";

const AddFinTrans = () => {
  const [contractId, setContractId] = useState("");
  const [contract, setContract] = useState(null);
  const [formData, setFormData] = useState({
    receipt_payment: "Receipt",
    receipt_payment_reason: "",
    mode_of_payment: "Cash",
    tr_date: "",
    tr_amount: "",
    reference_number: "",
    cheque_number: "",
    cheque_date: "",
    bank_name: "",
  });
  const [loading, setLoading] = useState(false);

  const labelize = (s) =>
    s.replace(/_/g, " ").replace(/\b\w/g, (ch) => ch.toUpperCase());

  const paymentModes = ["Cash", "Cheque", "Online"];
  const receiptOptions = ["Receipt", "Payment"];
  const readOnlyFields = ["contract_id", "property_code", "tenant_id", "tenant_name"];

  // ------------------- Search Contract -------------------
const handleSearch = async (e) => {
  e?.preventDefault();
  if (!contractID) return alert("Please enter Contract Number");

  try {
    setLoading(true);
    const res = await fetch(`http://localhost:5000/contract/${contractNumber}`);
    const data = await res.json();

    if (res.ok && data.success && data.contract) {
      const contractData = data.contract;
      setContract(contractData);

      // Convert Flask datetime strings ("Tue, 09 Jun 2026 00:00:00 GMT") to yyyy-mm-dd
      const initial = {};
      Object.keys(contractData).forEach((key) => {
        let value = contractData[key];
        if (value && typeof value === "string" && value.includes("GMT")) {
          const dateObj = new Date(value);
          value = isNaN(dateObj) ? "" : dateObj.toISOString().split("T")[0];
        }
        initial[key] = value ?? "";
      });

      setFormData(initial);
    } else {
      alert(data.message || "Contract not found");
      setContract(null);
      setFormData({});
    }
  } catch (err) {
    console.error("‚ùå Error fetching contract:", err);
    alert("Error fetching contract");
  } finally {
    setLoading(false);
  }
};

  // ------------------- Input Change -------------------
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // ------------------- Save Transaction -------------------
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!contract) return alert("Search a contract first");

    const { mode_of_payment } = formData;

    let payload = {
      contract_id: formData.contract_id,
      property_code: formData.property_code,
      tenant_id: formData.tenant_id,
      tenant_name: formData.tenant_name,
      receipt_payment: formData.receipt_payment,
      receipt_payment_reason: formData.receipt_payment_reason,
      mode_of_payment,
      tr_date: formData.tr_date,
      tr_amount: formData.tr_amount,
    };

    if (mode_of_payment === "Cheque") {
      payload = {
        ...payload,
        cheque_number: formData.cheque_number,
        cheque_date: formData.cheque_date,
        bank_name: formData.bank_name,
      };
    } else if (mode_of_payment === "Online") {
      payload = { ...payload, reference_number: formData.reference_number };
    }

    try {
      setLoading(true);
      const res = await fetch("http://localhost:5000/api/fintrans", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });
      const result = await res.json();

      if (res.ok) {
        alert("‚úÖ Financial transaction saved successfully!");
        window.location.href = "/addtrans";
      } else alert(result.message || "‚ùå Failed to save transaction");
    } catch (err) {
      console.error(err);
      alert("Error saving transaction");
    } finally {
      setLoading(false);
    }
  };

  const getValue = (key) => formData[key] ?? contract?.[key] ?? "";

  // ------------------- Render -------------------
  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: 20 }}>
      <h2
        style={{
          textAlign: "center",
          background: "#2f3640",
          color: "white",
          padding: 15,
          margin: 0,
        }}
      >
        Add Financial Transaction
      </h2>

      {/* Search */}
      <form
        onSubmit={handleSearch}
        style={{ textAlign: "center", margin: "20px 0" }}
      >
        <label style={{ fontWeight: "bold", marginRight: 8 }}>
          Enter Contract ID:
        </label>
        <input
          type="text"
          value={contractId}
          onChange={(e) => setContractId(e.target.value)}
          placeholder="Ex: 1001"
          required
          style={{
            width: 150,
            padding: 6,
            borderRadius: 5,
            border: "1px solid #aaa",
          }}
        />
        <button
          type="submit"
          style={{
            marginLeft: 8,
            padding: "6px 12px",
            background: "#273c75",
            color: "white",
            border: "none",
            borderRadius: 5,
            cursor: "pointer",
            width: 200,
          }}
        >
          {loading ? "Searching..." : "Search"}
        </button>
      </form>

      {/* Form */}
      {contract && (
        <form
          onSubmit={handleSubmit}
          style={{
            maxWidth: 1200,
            margin: "0 auto",
            background: "white",
            padding: 20,
            borderRadius: 10,
            boxShadow: "0 0 10px #ccc",
          }}
        >
          {/* Basic Info */}
          <div
            style={{
              display: "grid",
              gridTemplateColumns: "repeat(4, 1fr)",
              gap: 15,
              marginBottom: 20,
            }}
          >
            {readOnlyFields.map((field) => (
              <div key={field}>
                <label
                  style={{
                    fontWeight: "bold",
                    display: "block",
                    marginBottom: 6,
                  }}
                >
                  {labelize(field)}
                </label>
                <input
                  name={field}
                  value={getValue(field)}
                  readOnly
                  style={{
                    width: "100%",
                    padding: 7,
                    borderRadius: 6,
                    border: "1px solid #ccc",
                    backgroundColor: "#f0f0f0",
                    cursor: "not-allowed",
                  }}
                />
              </div>
            ))}
          </div>

          {/* Payment Details */}
          <div
            style={{
              display: "grid",
              gridTemplateColumns: "repeat(4, 1fr)",
              gap: 15,
            }}
          >
            {/* Receipt/Payment */}
            <div>
              <label style={{ fontWeight: "bold", display: "block", marginBottom: 6 }}>
                Receipt / Payment
              </label>
              <select
                name="receipt_payment"
                value={formData.receipt_payment}
                onChange={handleChange}
              >
                {receiptOptions.map((opt) => (
                  <option key={opt} value={opt}>
                    {opt}
                  </option>
                ))}
              </select>
            </div>

            {/* Reason */}
            <div>
              <label style={{ fontWeight: "bold", display: "block", marginBottom: 6 }}>
                Reason
              </label>
              <input
                type="text"
                name="receipt_payment_reason"
                value={formData.receipt_payment_reason}
                onChange={handleChange}
              />
            </div>

            {/* Mode of Payment */}
            <div>
              <label style={{ fontWeight: "bold", display: "block", marginBottom: 6 }}>
                Mode of Payment
              </label>
              <select
                name="mode_of_payment"
                value={formData.mode_of_payment}
                onChange={handleChange}
              >
                {paymentModes.map((mode) => (
                  <option key={mode} value={mode}>
                    {mode}
                  </option>
                ))}
              </select>
            </div>

            {/* Transaction Date */}
            <div>
              <label style={{ fontWeight: "bold", display: "block", marginBottom: 6 }}>
                Transaction Date
              </label>
              <input
                type="date"
                name="tr_date"
                value={formData.tr_date}
                onChange={handleChange}
              />
            </div>

            {/* Amount */}
            <div>
              <label style={{ fontWeight: "bold", display: "block", marginBottom: 6 }}>
                Transaction Amount
              </label>
              <input
                type="number"
                name="tr_amount"
                value={formData.tr_amount}
                onChange={handleChange}
              />
            </div>

            {/* Conditional Fields */}
            {formData.mode_of_payment === "Cheque" && (
              <>
                <div>
                  <label style={{ fontWeight: "bold" }}>Cheque Number</label>
                  <input
                    type="text"
                    name="cheque_number"
                    value={formData.cheque_number}
                    onChange={handleChange}
                  />
                </div>
                <div>
                  <label style={{ fontWeight: "bold" }}>Cheque Date</label>
                  <input
                    type="date"
                    name="cheque_date"
                    value={formData.cheque_date}
                    onChange={handleChange}
                  />
                </div>
                <div>
                  <label style={{ fontWeight: "bold" }}>Bank Name</label>
                  <input
                    type="text"
                    name="bank_name"
                    value={formData.bank_name}
                    onChange={handleChange}
                  />
                </div>
              </>
            )}

            {formData.mode_of_payment === "Online" && (
              <div>
                <label style={{ fontWeight: "bold" }}>Reference Number</label>
                <input
                  type="text"
                  name="reference_number"
                  value={formData.reference_number}
                  onChange={handleChange}
                />
              </div>
            )}
          </div>

          {/* Buttons */}
          <div style={{ textAlign: "center", marginTop: 30 }}>
            <a
              href="/addtrans"
              style={{
                ...styles.button,
                backgroundColor: "#492bc0ff",
                marginRight: 20,
                textDecoration: "none",
                display: "inline-block",
              }}
            >
              ‚Üê Back
            </a>
            <button type="submit" style={styles.saveButton}>
              üíæ Save Transaction
            </button>
          </div>
        </form>
      )}
    </div>
  );
};

const styles = {
  button: {
    width: 200,
    padding: "10px 20px",
    backgroundColor: "#0097e6",
    color: "white",
    border: "none",
    borderRadius: 6,
    cursor: "pointer",
    fontSize: 16,
  },
  saveButton: {
    width: 200,
    padding: "10px 20px",
    backgroundColor: "#27ae60",
    color: "white",
    border: "none",
    borderRadius: 6,
    cursor: "pointer",
    fontSize: 16,
  },
};

export default AddFinTrans;
