import React, { useEffect, useState } from "react";
import EditProperty from "./EditProperty";

const PropertyList = () => {
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentDate, setCurrentDate] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [editingPropertyCode, setEditingPropertyCode] = useState(null);

  const photoFields = ["property_photo"];

  useEffect(() => {
    fetchProperties();
    setCurrentDate(new Date().toLocaleDateString());
  }, []);

  const fetchProperties = async () => {
    try {
      setLoading(true);
      const res = await fetch("http://localhost:5000/list_property_api", { credentials: "include" });
      const data = await res.json();
      setProperties(data);
    } catch (err) {
      console.error(err);
      alert("Error fetching properties");
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (propertyCode) => {
    if (!window.confirm("Are you sure you want to delete this property?")) return;
    try {
      const res = await fetch(`http://localhost:5000/property_delete_api/${propertyCode}`, { method: "DELETE" });
      const data = await res.json();
      if (res.ok) {
        alert(data.message || "Property deleted");
        fetchProperties();
      } else {
        alert(data.message || "Failed to delete property");
      }
    } catch (err) {
      console.error(err);
      alert("Error deleting property");
    }
  };

  const filteredProperties = properties.filter(p =>
    p.property_code?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    p.name?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const sortTable = (n) => {
    const sorted = [...properties].sort((a, b) => {
      const valA = Object.values(a)[n] ?? "";
      const valB = Object.values(b)[n] ?? "";
      if (!isNaN(valA) && !isNaN(valB)) return valA - valB;
      return valA.toString().localeCompare(valB.toString());
    });
    setProperties(sorted);
  };

  const handleEditUpdate = () => {
    fetchProperties();
    setEditingPropertyCode(null);
  };

  const dbColumnsOrder = [
    "property_code","primary_owner_code","developer_name","building_name","property_type",
    "name","address1","address2","city","state","country","zip_code","municipality_number",
    "electricity_bill_number","water_bill_number","ownership_type","furnishing_status",
    "land_area","carpet_area","builtup_area","bedrooms","bathrooms","washrooms","facing",
    "car_parkings","servant_rooms","balconies","has_dishwasher","has_washing_machine",
    "property_value","year_of_construction","second_owner_code","expected_rent_value","status",
    "mtd_income","ytd_income","mtd_expense","ytd_expense","purchase_value","construction_cost",
    "renovation_cost","registration","transfer_fees","misc_expense","created_by","creation_date",
    "property_photo"
  ];

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: 20, background: "#f0f2f5" }}>
      <h2 style={{ textAlign: "center", background: "#2f3640", color: "white", padding: 15, margin: 0 }}>
        Property List
      </h2>

      <div style={{ display: "flex", justifyContent: "space-between", margin: "20px 0" }}>
        <div>
          <span>{currentDate}</span> | Total Properties: {properties.length}
        </div>
        <div>
          <input
            type="text"
            placeholder="Search by Property Code or Name"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            style={{ padding: 6, borderRadius: 5, border: "1px solid #ccc" }}
          />
        </div>
      </div>

      <div style={{ overflowX: "auto", background: "white", padding: 20, borderRadius: 10 }}>
        {loading ? <p>Loading properties...</p> : (
          <table style={{ width: "100%", borderCollapse: "collapse" }}>
            <thead>
              <tr>
                {dbColumnsOrder.map((col, idx) => (
                  <th
                    key={col}
                    style={{ padding: 8, border: "1px solid #ccc", background: "#273c75", color: "white", cursor: "pointer" }}
                    onClick={() => sortTable(idx)}
                  >
                    {col.replace(/_/g, " ").replace(/\b\w/g, ch => ch.toUpperCase())}
                  </th>
                ))}
                <th style={{ padding: 8, border: "1px solid #ccc", background: "#273c75", color: "white" }}>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredProperties.map((p, idx) => (
                <tr key={p.property_code} style={{ backgroundColor: idx % 2 === 0 ? "#f2f2f2" : "#fff" }}>
                  {dbColumnsOrder.map((col) => (
                    <td key={col} style={{ padding: 8, border: "1px solid #ccc", textAlign: "center" }}>
                      {photoFields.includes(col) && p[col] ? (
                        <img src={`data:image/jpeg;base64,${p[col]}`} alt={col} style={{ width: 60, height: 60, borderRadius: 4, border: "1px solid #ccc" }} />
                      ) : p[col]}
                    </td>
                  ))}
                  <td style={{ padding: 8, textAlign: "center" }}>
                    <button onClick={() => setEditingPropertyCode(p.property_code)} style={{ marginRight: 6, padding: "4px 8px" }}>Edit</button>
                    <button onClick={() => handleDelete(p.property_code)} style={{ padding: "4px 8px", background: "#c0392b", color: "#fff" }}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>

      {/* Edit Modal */}
      {editingPropertyCode && (
        <EditProperty
          propertyCode={editingPropertyCode}
          onClose={() => setEditingPropertyCode(null)}
          onUpdate={handleEditUpdate}
        />
      )}
    </div>
  );
};

export default PropertyList;
