import React, { useState } from "react";

const AddServTrans = ({ sessionUser }) => {
  const [propertyCode, setPropertyCode] = useState("");
  const [tenantCode, setTenantCode] = useState("");
  const [property, setProperty] = useState(null);
  const [tenant, setTenant] = useState(null);
  const [formData, setFormData] = useState({});

  // ------------------- Unified Search -------------------
  const handleSearch = async (e) => {
    e.preventDefault();
    try {
      const [propertyRes, tenantRes] = await Promise.all([
        fetch(`http://localhost:5000/property/${propertyCode}`),
        fetch(`http://localhost:5000/tenant/${tenantCode}`),
      ]);

      const propertyData = await propertyRes.json();
      const tenantData = await tenantRes.json();

      if (!propertyData.success) return alert(propertyData.message || "Property not found");
      if (!tenantData.success) return alert(tenantData.message || "Tenant not found");

      setProperty(propertyData.property);
      setTenant(tenantData.tenant);

      setFormData({
        property_code: propertyData.property.property_code || "",
        tenancy_contract_number: tenantData.tenant.TenancyContractNumber || "",
        tenant_name: tenantData.tenant.TenantName || "",
        created_by: sessionUser || "admin",
        creation_date: new Date().toISOString().split("T")[0],
      });
    } catch (err) {
      console.error("Error fetching data:", err);
      alert("Error fetching property or tenant data!");
    }
  };

  // ------------------- Handle Change -------------------
  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (files) {
      setFormData((prev) => ({ ...prev, [name]: files[0] }));
    } else {
      setFormData((prev) => ({ ...prev, [name]: value }));
    }
  };

  // ------------------- Submit Form -------------------
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!property || !tenant)
      return alert("Please search and select both Property and Tenant first");

    const data = new FormData();
    Object.keys(formData).forEach((key) => {
      data.append(key, formData[key]);
    });

    try {
      const res = await fetch("http://localhost:5000/add_servtrans", {
        method: "POST",
        credentials: "include",
        body: data,
      });

      const result = await res.json();

      if (res.ok && result.success) {
        alert(`‚úÖ ${result.message}`);
        setProperty(null);
        setTenant(null);
        setFormData({});
        setPropertyCode("");
        setTenantCode("");
      } else {
        alert(`‚ùå ${result.message || "Failed to add service transaction"}`);
      }
    } catch (err) {
      console.error("Error submitting form:", err);
      alert("‚ùå Error submitting service transaction");
    }
  };

  // ------------------- Dropdown Options -------------------
  const serviceTypes = [
    "Plumbing",
    "Electrical",
    "AC Maintenance",
    "Cleaning",
    "Painting",
    "Pest Control",
    "Other",
  ];

  const serviceStatus = ["Pending", "In Progress", "Completed", "Cancelled"];
  const paymentModes = ["Cash", "Check", "Online"];

  // ------------------- UI -------------------
  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: "20px" }}>
      <h2 style={{ textAlign: "center", background: "#2f3640", color: "white", padding: "15px 0" }}>
        Add Service Transaction
      </h2>

      {/* Unified Search */}
      <form onSubmit={handleSearch} style={{ textAlign: "center", margin: "20px 0" }}>
        <label>Enter Property Code:</label>
        <input
          type="text"
          value={propertyCode}
          onChange={(e) => setPropertyCode(e.target.value)}
          placeholder="Ex: 101"
          required
          style={{ margin: "0 10px", padding: "5px", width: 100 }}
        />

        <label>Enter Tenant ID:</label>
        <input
          type="text"
          value={tenantCode}
          onChange={(e) => setTenantCode(e.target.value)}
          placeholder="Ex: 2001"
          required
          style={{ margin: "0 10px", padding: "5px", width: 120 }}
        />

        <button type="submit" style={{ padding: "5px 10px", cursor: "pointer", width: 100 }}>
          Search
        </button>
      </form>

      {/* Display Property + Tenant Info */}
      {property && tenant && (
        <p style={{ textAlign: "center", fontWeight: "bold", marginBottom: "20px" }}>
          <span>üè¢ Property:</span> {property.building_name || property.building_type} |{" "}
          <span>Tenant:</span> {tenant.TenantName}
        </p>
      )}

      {/* Form */}
      {property && tenant && (
        <form onSubmit={handleSubmit}>
          <div
            style={{
              display: "grid",
              gridTemplateColumns: "repeat(4, 1fr)",
              gap: "10px",
              marginBottom: "15px",
            }}
          >
            {/* Readonly fields */}
            <div>
              <label style={{ fontWeight: "bold" }}>PROPERTY CODE</label>
              <input type="text" name="property_code" value={formData.property_code || ""} readOnly style={{ backgroundColor: "#f0f0f0" }} />
            </div>

            <div>
              <label style={{ fontWeight: "bold" }}>TENANCY ID</label>
              <input type="text" name="tenancy_contract_number" value={formData.tenancy_contract_number || ""} readOnly style={{ backgroundColor: "#f0f0f0" }} />
            </div>

            <div>
              <label style={{ fontWeight: "bold" }}>TENANT NAME</label>
              <input type="text" name="tenant_name" value={formData.tenant_name || ""} readOnly style={{ backgroundColor: "#f0f0f0" }} />
            </div>

            {/* Service Fields */}
            <div>
              <label style={{ fontWeight: "bold" }}>SERVICE TYPE</label>
              <select name="service_type" value={formData.service_type || ""} onChange={handleChange}>
                <option value="">Select</option>
                {serviceTypes.map((s) => (
                  <option key={s} value={s}>
                    {s}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label style={{ fontWeight: "bold" }}>SERVICE DATE</label>
              <input type="date" name="service_date" value={formData.service_date || ""} onChange={handleChange} />
            </div>

            <div>
              <label style={{ fontWeight: "bold" }}>SERVICE COST</label>
              <input type="number" step="0.01" name="service_cost" value={formData.service_cost || ""} onChange={handleChange} />
            </div>

            <div>
              <label style={{ fontWeight: "bold" }}>SERVICE STATUS</label>
              <select name="service_status" value={formData.service_status || ""} onChange={handleChange}>
                <option value="">Select</option>
                {serviceStatus.map((s) => (
                  <option key={s} value={s}>
                    {s}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label style={{ fontWeight: "bold" }}>PAYMENT MODE</label>
              <select name="payment_mode" value={formData.payment_mode || ""} onChange={handleChange}>
                <option value="">Select</option>
                {paymentModes.map((m) => (
                  <option key={m} value={m}>
                    {m}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label style={{ fontWeight: "bold" }}>DESCRIPTION</label>
              <textarea
                name="service_description"
                rows="2"
                value={formData.service_description || ""}
                onChange={handleChange}
                placeholder="Details about the service..."
              />
            </div>

            <div>
              <label style={{ fontWeight: "bold" }}>ATTACH INVOICE</label>
              <input type="file" name="invoice_copy" accept="application/pdf,image/*" onChange={handleChange} />
            </div>
          </div>

          {/* Buttons */}
          <div style={{ textAlign: "center", marginTop: "30px" }}>
            <a
              href="/index"
              style={{
                width: 200,
                padding: "10px 20px",
                backgroundColor: "#492bc0ff",
                color: "white",
                borderRadius: "6px",
                textDecoration: "none",
                marginRight: "20px",
                display: "inline-block",
              }}
            >
              ‚Üê Go Back Home
            </a>
            <button
              type="submit"
              style={{
                width: 200,
                padding: "10px 20px",
                backgroundColor: "#27ae60",
                color: "white",
                border: "none",
                borderRadius: "6px",
                cursor: "pointer",
                fontSize: "16px",
              }}
            >
              Add Service Transaction
            </button>
          </div>
        </form>
      )}
    </div>
  );
};

export default AddServTrans;
