import React, { useEffect, useState } from "react";

const ListProperty = () => {
  const [properties, setProperties] = useState([]);
  const [filteredProperties, setFilteredProperties] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [loading, setLoading] = useState(true);

  const propertyPhotoField = "property_photo";

  useEffect(() => {
    fetchProperties();
  }, []);

  // ---------------- Fetch Properties ----------------
  const fetchProperties = async () => {
    try {
      setLoading(true);
      const res = await fetch("http://localhost:5000/list_property_api", {
        method: "GET",
        credentials: "include",
      });
      const data = await res.json();
      setProperties(data);
      setFilteredProperties(data);
    } catch (err) {
      console.error(err);
      alert("❌ Error fetching properties");
    } finally {
      setLoading(false);
    }
  };

  // ---------------- Search / Filter ----------------
  const handleSearch = (e) => {
    const term = e.target.value;
    setSearchTerm(term);
    if (!term) {
      setFilteredProperties(properties);
    } else {
      const filtered = properties.filter((p) =>
        (p.property_code ?? "").toString().toLowerCase().includes(term.toLowerCase()) ||
        (p.name ?? "").toString().toLowerCase().includes(term.toLowerCase())
      );
      setFilteredProperties(filtered);
    }
  };

  // ---------------- Delete Property ----------------
  const handleDelete = async (propertyCode) => {
    if (!window.confirm(`Are you sure you want to delete property ${propertyCode}?`)) return;
    try {
      const res = await fetch(`http://localhost:5000/property_delete_api/${propertyCode}`, {
        method: "DELETE",
        credentials: "include",
      });
      const data = await res.json();
      if (res.ok) {
        alert(`✅ ${data.message}`);
        fetchProperties(); // Refresh list
      } else {
        alert(`❌ ${data.message || "Failed to delete property"}`);
      }
    } catch (err) {
      console.error(err);
      alert("❌ Error deleting property");
    }
  };

  // ---------------- Sort Table ----------------
  const handleSort = (key) => {
    const sorted = [...filteredProperties].sort((a, b) => {
      const valA = a[key] ?? "";
      const valB = b[key] ?? "";
      if (!isNaN(valA) && !isNaN(valB)) return valA - valB;
      return valA.toString().localeCompare(valB.toString());
    });
    setFilteredProperties(sorted);
  };

  const labelize = (s) =>
    s.replace(/([A-Z])/g, " $1")
     .replace(/_/g, " ")
     .replace(/\b\w/g, (ch) => ch.toUpperCase())
     .trim();

  const columns = [
    "property_code","primary_owner_code","developer_name","building_name","property_type",
    "name","address1","address2","city","state","country","zip_code","municipality_number",
    "electricity_bill_number","water_bill_number","ownership_type","furnishing_status",
    "land_area","carpet_area","builtup_area","bedrooms","bathrooms","washrooms","facing",
    "car_parkings","servant_rooms","balconies","has_dishwasher","has_washing_machine",
    "property_value","year_of_construction","second_owner_code","expected_rent_value",
    "status","mtd_income","ytd_income","mtd_expense","ytd_expense","purchase_value",
    "construction_cost","renovation_cost","registration","transfer_fees","misc_expense",
    "created_by","creation_date", propertyPhotoField
  ];

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: 20, background: "#f5f6fa" }}>
      <h2 style={{ textAlign: "center", background: "#2f3640", color: "white", padding: 15, margin: 0, borderRadius: "8px 8px 0 0" }}>
        Property List
      </h2>

      <div style={{ maxWidth: 1500, margin: "20px auto", background: "white", padding: 20, borderRadius: 10, boxShadow: "0 0 15px rgba(0,0,0,0.2)", overflowX: "auto" }}>
        <div style={{ display: "flex", justifyContent: "space-between", marginBottom: 15 }}>
          <div>
            <input
              type="text"
              placeholder="Search by Property Code or Name"
              value={searchTerm}
              onChange={handleSearch}
              style={{ padding: 6, borderRadius: 5, border: "1px solid #aaa", width: 250 }}
            />
          </div>
          <div>
            <a href="/index" style={{ textDecoration: "none", fontWeight: "bold", color: "#333" }}>← Go Back Home</a>
          </div>
        </div>

        {loading ? <p>Loading properties...</p> : (
          <table style={{ width: "100%", borderCollapse: "collapse" }}>
            <thead>
              <tr>
                {columns.map((col) => (
                  <th
                    key={col}
                    onClick={() => handleSort(col)}
                    style={{ padding: 8, border: "1px solid #ccc", background: "#273c75", color: "white", cursor: "pointer" }}
                  >
                    {labelize(col)}
                  </th>
                ))}
                <th style={{ padding: 8, border: "1px solid #ccc", background: "#273c75", color: "white" }}>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredProperties.map((p, idx) => (
                <tr key={idx} style={{ backgroundColor: idx % 2 === 0 ? "#f2f2f2" : "#fff" }}>
                  {columns.map((key) => (
                    <td key={key} style={{ padding: 8, border: "1px solid #ccc", textAlign: "center" }}>
                      {key === propertyPhotoField && p[key] ? (
                        <img src={`data:image/jpeg;base64,${p[key]}`} alt="property" style={{ width: 60, height: 60, borderRadius: 6, border: "1px solid #ccc" }} />
                      ) : p[key] ?? ""}
                    </td>
                  ))}
                  <td style={{ padding: 8, textAlign: "center" }}>
                    <a
                      href={`/edit_property/${p.property_code}`}
                      style={{ marginRight: 10, padding: "4px 8px", background: "#f39c12", color: "white", borderRadius: 4, textDecoration: "none" }}
                    >
                      Edit
                    </a>
                    <button
                      onClick={() => handleDelete(p.property_code)}
                      style={{ padding: "4px 8px", background: "#c0392b", color: "white", borderRadius: 4, border: "none", cursor: "pointer" }}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
   
  );
};

      {/* Edit Modal */}
      {showModal && editProperty && (
        <div style={{
          position: "fixed", top: 0, left: 0, width: "100%", height: "100%",
          background: "rgba(0,0,0,0.5)", display: "flex", justifyContent: "center", alignItems: "center"
        }}>
          <div style={{ background: "white", padding: 20, borderRadius: 10, width: "80%", maxHeight: "90%", overflowY: "auto" }}>
            <h3>Edit Property {editProperty.property_code}</h3>
            <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: 15 }}>
              {Object.keys(editProperty).map((key) => (
                key !== "property_photo" ? (
                  <div key={key}>
                    <label style={{ fontWeight: "bold" }}>{labelize(key)}</label>
                    <input
                      type="text"
                      value={editProperty[key] ?? ""}
                      onChange={(e) => handleEditChange(key, e.target.value)}
                      style={{ width: "100%", padding: 5, marginTop: 4 }}
                    />
                  </div>
                ) : (
                  <div key={key}>
                    <label style={{ fontWeight: "bold" }}>{labelize(key)}</label>
                    {editProperty[key] ? (
                      <img src={`data:image/jpeg;base64,${editProperty[key]}`} alt={key} style={{ width: 100, height: 100, borderRadius: 6 }} />
                    ) : <p>No photo</p>}
                  </div>
                )
              ))}
            </div>
            <div style={{ marginTop: 20, textAlign: "right" }}>
              <button onClick={() => setShowModal(false)} style={{ marginRight: 10, padding: "8px 15px" }}>Cancel</button>
              <button onClick={handleEditSave} style={{ padding: "8px 15px", background: "#27ae60", color: "white", border: "none", borderRadius: 4 }}>Save</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ListProperty;
