import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // ‚úÖ add for navigation

const AddContract = ({ sessionUser }) => {
  const navigate = useNavigate();
  const [propertyCode, setPropertyCode] = useState("");
  const [tenantCode, setTenantCode] = useState("");
  const [property, setProperty] = useState(null);
  const [tenant, setTenant] = useState(null);
  const [formData, setFormData] = useState({});

  // ------------------- Unified Search -------------------
  const handleSearch = async (e) => {
    e.preventDefault();

    try {
      const [propertyRes, tenantRes] = await Promise.all([
        fetch(`http://localhost:5000/property/${propertyCode}`),
        fetch(`http://localhost:5000/tenant/${tenantCode}`),
      ]);

      const propertyData = await propertyRes.json();
      const tenantData = await tenantRes.json();

      if (!propertyData.success) return alert(propertyData.message || "Property not found");
      if (!tenantData.success) return alert(tenantData.message || "Tenant not found");

      if (propertyData.property.status?.toLowerCase() !== "vacant") {
        alert("‚ùå Property is currently occupied! Cannot create a new contract.");
        setProperty(null);
        setTenant(null);
        return;
      }

      setProperty(propertyData.property);
      setTenant(tenantData.tenant);

      setFormData({
        property_code: propertyData.property.property_code || "",
        tenant_id: tenantData.tenant.TenancyContractNumber || "",
        tenant_name: tenantData.tenant.TenantName || "",
        tenant_email: tenantData.tenant.TenantEmailID || "",
        created_by: sessionUser || "admin",
        creation_date: new Date().toISOString().split("T")[0],
      });
    } catch (err) {
      console.error("Error fetching data:", err);
      alert("Error fetching property or tenant data!");
    }
  };

  // ------------------- Input Change -------------------
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // ------------------- Submit Contract -------------------
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!property || !tenant)
      return alert("Please search and select both Property and Tenant first");

    // ‚ö†Ô∏è Prevent entering rent/deposit before FinTrans
    if (!formData.rent_amount || !formData.deposit_amount) {
      alert("‚ö†Ô∏è Please complete the Financial Transaction before entering Rent and Deposit.");
      navigate(`/add_fintrans?property=${propertyCode}&tenant=${tenantCode}`);
      return;
    }

    const data = new FormData();
    data.append("property_id", formData.property_code);
    data.append("tenant_id", formData.tenant_id);

    if (tenant?.TenantEmailID) data.append("tenant_email", tenant.TenantEmailID);

    Object.keys(formData).forEach((key) => {
      if (!["property_code", "tenant_id"].includes(key)) {
        data.append(key, formData[key]);
      }
    });

    if (!formData.created_by) data.append("created_by", sessionUser || "admin");
    if (!formData.creation_date)
      data.append("creation_date", new Date().toISOString().split("T")[0]);

    try {
      const res = await fetch("http://localhost:5000/add_contract", {
        method: "POST",
        credentials: "include",
        body: data,
      });

      const result = await res.json();

      if (res.ok && result.success) {
        alert(`‚úÖ ${result.message}\nüìß ${result.email_status || "Email not sent"}`);

        // ‚úÖ Redirect to FinTrans page before entering rent/deposit
        navigate(`/add_fintrans?property=${propertyCode}&tenant=${tenantCode}`);
      } else {
        alert(`‚ùå ${result.message || "Failed to add contract"}`);
      }
    } catch (err) {
      console.error("FETCH FAILED:", err);
      alert("‚ùå Error submitting contract");
    }
  };

  // ------------------- Contract Fields -------------------
  const contractFields = [
    ["property_code", "tenant_id", "tenant_name", "contract_start_date", "contract_end_date"],
    ["contract_termination_date", "move_in_date", "move_out_date", "first_time_move_in_date", "contract_renewal_date"],
    ["rent_amount", "deposit_amount", "notice_period", "rent_amount_description", "rent_payment_mode"],
    ["rent_payment_type", "deposit_payment_type", "new_or_old_tenant"],
  ];

  const paymentModes = ["cash", "cheque", "online", "card"];
  const rentTypes = ["monthly", "yearly", "halfyearly"];

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: "20px" }}>
      <h2 style={{ textAlign: "center", background: "#2f3640", color: "white", padding: "15px 0" }}>
        Add Contract
      </h2>

      {/* Unified Search */}
      <form onSubmit={handleSearch} style={{ textAlign: "center", margin: "20px 0" }}>
        <label>Enter Property Code:</label>
        <input
          type="text"
          value={propertyCode}
          onChange={(e) => setPropertyCode(e.target.value)}
          placeholder="Ex: 101"
          required
          style={{ margin: "0 10px", padding: "5px", width: 100 }}
        />

        <label>Enter Tenant ID:</label>
        <input
          type="text"
          value={tenantCode}
          onChange={(e) => setTenantCode(e.target.value)}
          placeholder="Ex: 2001"
          required
          style={{ margin: "0 10px", padding: "5px", width: 120 }}
        />

        <button type="submit" style={{ padding: "5px 10px", cursor: "pointer", width: 100 }}>
          Search
        </button>
      </form>

      {property && tenant && (
        <p style={{ textAlign: "center", fontWeight: "bold", marginBottom: "20px" }}>
          <span>Developer Name: </span> {property.developer_name} |{" "}
          <span>Property Type: </span> {property.property_type || "-"}
          <br />
          <span>üë§ Tenant:</span> {tenant.TenantName} |{" "}
          <span>Email:</span> {tenant.TenantEmailID || "-"}
        </p>
      )}

      {property && tenant && (
        <form onSubmit={handleSubmit}>
          {contractFields.map((row, i) => (
            <div
              key={i}
              style={{
                display: "grid",
                gridTemplateColumns: "repeat(5, 1fr)",
                gap: "10px",
                marginBottom: "10px",
              }}
            >
              {row.map((field) => {
                const value = formData[field] || "";
                const isDateField = field.includes("date") || field.includes("Date");

                // Read-only fields
                if (["property_code", "tenant_id", "tenant_name"].includes(field)) {
                  return (
                    <div key={field}>
                      <label style={{ fontWeight: "bold" }}>{field.replace(/_/g, " ").toUpperCase()}</label>
                      <input type="text" name={field} value={value} readOnly style={{ backgroundColor: "#f0f0f0" }} />
                    </div>
                  );
                }

                // Selects
                if (field === "rent_payment_mode" || field === "deposit_payment_type") {
                  return (
                    <div key={field}>
                      <label style={{ fontWeight: "bold" }}>{field.replace(/_/g, " ").toUpperCase()}</label>
                      <select name={field} value={value} onChange={handleChange}>
                        <option value="">Select</option>
                        {paymentModes.map((mode) => (
                          <option key={mode} value={mode}>
                            {mode}
                          </option>
                        ))}
                      </select>
                    </div>
                  );
                }

                if (field === "rent_payment_type") {
                  return (
                    <div key={field}>
                      <label style={{ fontWeight: "bold" }}>{field.replace(/_/g, " ").toUpperCase()}</label>
                      <select name={field} value={value} onChange={handleChange}>
                        <option value="">Select</option>
                        {rentTypes.map((type) => (
                          <option key={type} value={type}>
                            {type}
                          </option>
                        ))}
                      </select>
                    </div>
                  );
                }

                if (field === "new_or_old_tenant") {
                  return (
                    <div key={field}>
                      <label style={{ fontWeight: "bold" }}>{field.replace(/_/g, " ").toUpperCase()}</label>
                      <div>
                        <label>
                          <input type="radio" name={field} value="new" checked={value === "new"} onChange={handleChange} />
                          New
                        </label>
                        <label style={{ marginLeft: "10px" }}>
                          <input type="radio" name={field} value="old" checked={value === "old"} onChange={handleChange} />
                          Old
                        </label>
                      </div>
                    </div>
                  );
                }

                // Default
                return (
                  <div key={field}>
                    <label style={{ fontWeight: "bold" }}>{field.replace(/_/g, " ").toUpperCase()}</label>
                    <input type={isDateField ? "date" : "text"} name={field} value={value} onChange={handleChange} />
                  </div>
                );
              })}
            </div>
          ))}

          <div style={{ textAlign: "center", marginTop: "30px" }}>
            <a
              href="/index"
              style={{
                width: 200,
                padding: "10px 20px",
                backgroundColor: "#492bc0ff",
                color: "white",
                borderRadius: "6px",
                textDecoration: "none",
                marginRight: "20px",
                display: "inline-block",
              }}
            >
              ‚Üê Go Back Home
            </a>
            <button
              type="submit"
              style={{
                width: 200,
                padding: "10px 20px",
                backgroundColor: "#27ae60",
                color: "white",
                border: "none",
                borderRadius: "6px",
                cursor: "pointer",
                fontSize: "16px",
              }}
            >
              Add Contract
            </button>
          </div>
        </form>
      )}
    </div>
  );
};

export default AddContract;
