import React, { useState } from "react";

const ModifyContract = ({ sessionUser }) => {
  const [contractNumber, setContractNumber] = useState("");
  const [contract, setContract] = useState(null);
  const [formData, setFormData] = useState({});
  const [loading, setLoading] = useState(false);

  const parseDate = (val) => {
    if (!val) return "";
    const d = new Date(val);
    if (isNaN(d)) return "";
    const month = (d.getMonth() + 1).toString().padStart(2, "0");
    const day = d.getDate().toString().padStart(2, "0");
    return `${d.getFullYear()}-${month}-${day}`;
  };

  const labelize = (s) => s.replace(/_/g, " ").replace(/\b\w/g, (ch) => ch.toUpperCase());

  // Fields to display
  const fields = [
    "contract_id",
    "property_id",
    "tenant_id",
    "tenant_name",
    "contract_start_date",
    "contract_end_date",
    "contract_termination_date",
    "move_in_date",
    "move_out_date",
    "rent_amount",
    "deposit_amount",
    "notice_period",
    "rent_payment_mode",
    "rent_payment_type",
    "deposit_payment_type",
    "new_or_old_tenant",
  ];

  // ------------------- Search Contract -------------------
  const handleSearch = async (e) => {
    e?.preventDefault();
    if (!contractNumber) return alert("Please enter Contract Number");

    try {
      setLoading(true);
      const res = await fetch(`http://localhost:5000/contract/${contractNumber}`);
      const data = await res.json();

      if (res.ok && data.success && data.contract) {
        setContract(data.contract);

        // Populate formData
        const initial = {};
        Object.keys(data.contract).forEach((k) => {
          initial[k] = ["creation_date","contract_start_date","contract_end_date","contract_termination_date","move_in_date","move_out_date"].includes(k)
            ? parseDate(data.contract[k])
            : data.contract[k] ?? "";
        });
        setFormData(initial);
      } else {
        alert(data.message || "Contract not found");
        setContract(null);
        setFormData({});
      }
    } catch (err) {
      console.error(err);
      alert("Error fetching contract");
    } finally {
      setLoading(false);
    }
  };

  // ------------------- Input Change -------------------
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // ------------------- Update Contract -------------------
  const handleUpdate = async (e) => {
    e.preventDefault();
    if (!contract) return alert("Search a contract first");

    const data = new FormData();
    data.append("contract_id", contract.contract_id || contractNumber);

    Object.entries(formData).forEach(([key, val]) => {
      if (val != null) data.append(key, val);
    });

    try {
      setLoading(true);
      const res = await fetch("http://localhost:5000/modify_contract", {
        method: "POST",
        credentials: "include",
        body: data,
      });
      const result = await res.json();

      if (res.ok && result.success) {
        alert(`✅ ${result.message}`);
        await handleSearch(); // Refresh updated details
      } else alert(`❌ ${result.message || "Failed to update contract"}`);
    } catch (err) {
      console.error(err);
      alert("❌ Error updating contract");
    } finally {
      setLoading(false);
    }
  };

  const getValue = (key) => formData[key] ?? contract?.[key] ?? "";

  // ------------------- Render -------------------
  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: 20 }}>
      <h2 style={{ textAlign: "center", background: "#2f3640", color: "white", padding: 15, margin: 0 }}>
        Modify Contract
      </h2>

      {/* Search */}
      <form onSubmit={handleSearch} style={{ textAlign: "center", margin: "20px 0" }}>
        <label style={{ fontWeight: "bold", marginRight: 8 }}>Enter Contract ID:</label>
        <input
          type="text"
          value={contractNumber}
          onChange={(e) => setContractNumber(e.target.value)}
          placeholder="Ex: 1001"
          required
          style={{ width: 150, padding: 6, borderRadius: 5, border: "1px solid #aaa" }}
        />
        <button
          type="submit"
          style={{
            marginLeft: 8,
            padding: "6px 12px",
            background: "#273c75",
            color: "white",
            border: "none",
            borderRadius: 5,
            cursor: "pointer",
            width: 200,
          }}
        >
          {loading ? "Searching..." : "Search"}
        </button>
      </form>

      {/* Contract Form */}
      {contract && (
        <form
          onSubmit={handleUpdate}
          encType="multipart/form-data"
          style={{
            maxWidth: 1200,
            margin: "0 auto",
            background: "white",
            padding: 20,
            borderRadius: 10,
            boxShadow: "0 0 10px #ccc",
          }}
        >
          {fields.reduce((rows, field, i) => {
            if (i % 4 === 0) rows.push([]);
            rows[rows.length - 1].push(field);
            return rows;
          }, []).map((cols, i) => (
            <div key={i} style={{ display: "grid", gridTemplateColumns: `repeat(${cols.length}, 1fr)`, gap: 15, marginBottom: 15 }}>
              {cols.map((field) => {
                const isDate = field.includes("date");
                const isReadOnly = ["contract_id","property_id","tenant_id","tenant_name"].includes(field);
                return (
                  <div key={field}>
                    <label style={{ fontWeight: "bold", display: "block", marginBottom: 6 }}>{labelize(field)}</label>
                    <input
                      type={isDate ? "date" : "text"}
                      name={field}
                      value={getValue(field)}
                      onChange={isReadOnly ? undefined : handleChange}
                      readOnly={isReadOnly}
                      style={{
                        width: "100%",
                        padding: 7,
                        borderRadius: 6,
                        border: "1px solid #ccc",
                        backgroundColor: isReadOnly ? "#f0f0f0" : "white",
                        cursor: isReadOnly ? "not-allowed" : "text"
                      }}
                    />
                  </div>
                );
              })}
            </div>
          ))}

          {/* Buttons */}
          <div style={{ textAlign: "center", marginTop: "30px" }}>
            <a href="/index" style={{ ...styles.button, backgroundColor: "#492bc0ff", marginRight: 20, textDecoration: "none", display: "inline-block" }}>← Go Back Home</a>
            <button type="submit" style={styles.updateButton}>Modify Contract</button>
          </div>
        </form>
      )}
    </div>
  );
};

const styles = {
  button: { width: 200, padding: "10px 20px", backgroundColor: "#0097e6", color: "white", border: "none", borderRadius: 6, cursor: "pointer", fontSize: 16 },
  updateButton: { width: 200, padding: "10px 20px", backgroundColor: "#27ae60", color: "white", border: "none", borderRadius: 6, cursor: "pointer", fontSize: 16 },
};

export default ModifyContract;
