import React, { useState } from "react";
import "./ModifyOwner.css";

// Reusable input component
const InputField = ({ label, name, type = "text", value }) => (
  <div className="col">
    <label>{label}</label>
    <input
      name={name}
      type={type}
      defaultValue={value}
      style={{
        width: "100%",
        padding: "8px",
        border: "1px solid #ccc",
        borderRadius: "4px",
        fontSize: "0.9rem",
      }}
    />
  </div>
);

// Reusable file upload with preview
const FileUpload = ({ label, name, preview, onChange }) => (
  <div className="col">
    <label>{label}</label>
    <input type="file" name={name} onChange={onChange} />
    {preview && (
      <img
        src={preview}
        alt={`${label} Preview`}
        style={{
          display: "block",
          marginTop: "5px",
          maxWidth: "100px",
          maxHeight: "80px",
          border: "1px solid #ccc",
          borderRadius: "4px",
        }}
      />
    )}
  </div>
);

const ModifyOwner = () => {
  const [ownerCode, setOwnerCode] = useState("");
  const [ownerData, setOwnerData] = useState(null);
  const [message, setMessage] = useState("");
  const [eidPreview, setEidPreview] = useState(null);
  const [passportPreview, setPassportPreview] = useState(null);
  const [visaPreview, setVisaPreview] = useState(null);

  const handleSearch = async (e) => {
    e.preventDefault();
    setMessage("");
    setOwnerData(null);

    try {
      const res = await fetch(`http://localhost:5000/owners/${ownerCode}`, {
        method: "GET",
        credentials: "include",
      });
      const data = await res.json();
      if (res.ok) {
        setOwnerData(data);
        setEidPreview(data.eid_image ? `http://localhost:5000/owner_image/${data.owner_code}/eid` : null);
        setPassportPreview(data.passport_copy ? `http://localhost:5000/owner_image/${data.owner_code}/passport` : null);
        setVisaPreview(data.res_visa ? `http://localhost:5000/owner_image/${data.owner_code}/visa` : null);
      } else {
        setMessage(data.error || "Owner not found");
      }
    } catch (err) {
      console.error(err);
      setMessage("Error fetching owner");
    }
  };

  const handleFileChange = (e, type) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (event) => {
      if (type === "eid") setEidPreview(event.target.result);
      if (type === "passport") setPassportPreview(event.target.result);
      if (type === "visa") setVisaPreview(event.target.result);
    };
    reader.readAsDataURL(file);
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);

    try {
      const res = await fetch("http://localhost:5000/update_owner", {
        method: "POST",
        credentials: "include",
        body: formData,
      });
      if (res.ok) {
        setMessage("✅ Owner updated successfully");
      } else {
        setMessage("❌ Error updating owner");
      }
    } catch (err) {
      console.error(err);
      setMessage("❌ Error updating owner");
    }
  };

  return (
    <div className="container" style={{ maxWidth: "1500px", margin: "auto", padding: "20px", background: "#fff", borderRadius: "8px", boxShadow: "0 0 10px rgba(0,0,0,0.1)" }}>
      <h2 style={{ textAlign: "center", color: "black", marginBottom: "20px" }}>Modify Owner</h2>

      {message && <p className="message" style={{ textAlign: "center", fontWeight: "bold", marginBottom: "15px", color: message.includes("✅") ? "green" : "red" }}>{message}</p>}

      {/* Search */}
      <form onSubmit={handleSearch} style={{ textAlign: "center", marginBottom: "20px" }}>
        <input
          type="text"
          value={ownerCode}
          onChange={(e) => setOwnerCode(e.target.value)}
          placeholder="Owner Code"
          required
          style={{ width: "120px", padding: "6px", marginRight: "5px" }}
        />
        <button type="submit" style={{ padding: "6px 12px", background: "#007BFF", color: "#fff", border: "none", borderRadius: "4px" }}>Search</button>
      </form>

      <a href="/" style={{ display: "block", textAlign: "center", marginBottom: "20px", fontWeight: "bold" }}>← Go Back Home</a>

      {ownerData && (
        <form onSubmit={handleUpdate} encType="multipart/form-data">
          <input type="hidden" name="owner_code" value={ownerData.owner_code} />

          {/* Owner info fields */}
          {[
            ["Salutation", "salutation"],
            ["First Name", "first_name"],
            ["Last Name", "last_name"],
            ["Nationality", "nationality"],
            ["Address 1", "address1"],
            ["Address 2", "address2"],
            ["City", "city"],
            ["State", "state"],
            ["Country", "country"],
            ["Zip Code", "zip_code"],
            ["Mobile Number", "mobile_number", "tel"],
            ["Email", "email", "email"],
            ["Passport Number", "passport_number"],
            ["Emirates ID", "emirates_id"],
            ["Date of Birth", "date_of_birth", "date"],
            ["Visa Expiry Date", "visa_expiry_date", "date"],
            ["Passport Expiry Date", "passport_expiry_date", "date"]
          ].map(([label, name, type], i) => (
            <InputField
              key={i}
              label={label}
              name={name}
              type={type || "text"}
              value={ownerData[name]}
            />
          ))}

          {/* File uploads */}
          <div className="row" style={{ display: "flex", flexWrap: "wrap", marginTop: "15px" }}>
            <FileUpload label="Emirates ID" name="OwnerEID" preview={eidPreview} onChange={(e) => handleFileChange(e, "eid")} />
            <FileUpload label="Passport Copy" name="OwnerPassportCopy" preview={passportPreview} onChange={(e) => handleFileChange(e, "passport")} />
            <FileUpload label="Residence Visa" name="OwnerResVisa" preview={visaPreview} onChange={(e) => handleFileChange(e, "visa")} />
          </div>

          <div style={{ textAlign: "center", marginTop: "15px" }}>
            <button type="submit" style={{ padding: "8px 16px", background: "#007BFF", color: "#fff", border: "none", borderRadius: "4px" }}>Update Owner</button>
          </div>
        </form>
      )}
    </div>
  );
};

export default ModifyOwner;
