import React, { useState, useEffect } from "react";

const ListOwners = () => {
  const [owners, setOwners] = useState([]);
  const [sortField, setSortField] = useState("");
  const [sortOrder, setSortOrder] = useState("asc");
  const [currentDate, setCurrentDate] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    setCurrentDate(new Date().toLocaleDateString());
    fetchOwners();
  }, [sortField, sortOrder]);

  const fetchOwners = async () => {
    setLoading(true);
    setError("");
    try {
      const url = `http://localhost:5000/owners?sort=${sortField}&order=${sortOrder}`;
      const res = await fetch(url, { credentials: "include" });
      if (!res.ok) throw new Error("Failed to fetch owners from server.");
      const data = await res.json();
      setOwners(data);
    } catch (err) {
      console.error(err);
      setError("Error fetching owners from server.");
    } finally {
      setLoading(false);
    }
  };

  const handleSort = (field) => {
    if (sortField === field) {
      setSortOrder(sortOrder === "asc" ? "desc" : "asc");
    } else {
      setSortField(field);
      setSortOrder("asc");
    }
  };

  const getImageSrc = (owner, key) => {
    if (!owner[key]) return null;
    const type = key === "eid_image" ? "eid" : key === "passport_copy" ? "passport" : "visa";
    return `http://localhost:5000/owner_image/${owner.owner_code}/${type}`;
  };

  return (
    <div className="container" style={{ maxWidth: "1600px", margin: "auto", padding: "20px", overflowX: "auto" }}>
      {/* Header */}
      <div className="header-row" style={{ display: "flex", alignItems: "center", justifyContent: "space-between", marginBottom: "15px", flexWrap: "wrap" }}>
        <div className="header-left" style={{ display: "flex", gap: "10px", fontWeight: "bold" }}>
          <span>{currentDate}</span> | <span>Total Owners: {owners.length}</span>
        </div>
        <h2 style={{ margin: "0 auto", textAlign: "center", color: "black" }}>List of Owners</h2>
        <div className="header-right" style={{ fontWeight: "bold" }}>
          <a href="/" style={{ textDecoration: "none", color: "#333" }}>‚Üê Go Back Home</a>
        </div>
      </div>

      {/* Loading & Error */}
      {loading && <p style={{ textAlign: "center" }}>Loading owners...</p>}
      {error && <p style={{ textAlign: "center", color: "red", fontWeight: "bold" }}>{error}</p>}

      {/* Table */}
      {!loading && owners.length > 0 && (
        <table style={{ width: "100%", borderCollapse: "collapse", background: "white", borderRadius: "8px", overflow: "hidden" }}>
          <thead>
            <tr>
              {Object.keys(owners[0]).map((col) => {
                const isImageCol = ["eid_image", "passport_copy", "res_visa"].includes(col);
                return (
                  <th
                    key={col}
                    style={{ padding: "10px 12px", borderBottom: "1px solid #ddd", textAlign: "left", fontSize: "0.9rem", background: "#007BFF", color: "white", cursor: isImageCol ? "default" : "pointer" }}
                    onClick={() => !isImageCol && handleSort(col)}
                  >
                    {col.replace("_", " ").toUpperCase()} {!isImageCol && sortField === col && `(${sortOrder})`}
                  </th>
                );
              })}
            </tr>
          </thead>
          <tbody>
            {owners.map((owner) => (
              <tr key={owner.owner_code} style={{ backgroundColor: "#fff" }}>
                {Object.entries(owner).map(([key, val]) => {
                  if (["eid_image", "passport_copy", "res_visa"].includes(key)) {
                    const src = getImageSrc(owner, key);
                    return (
                      <td key={key} style={{ padding: "10px 12px", borderBottom: "1px solid #ddd" }}>
                        {src ? <img src={src} alt={key} style={{ width: "30px", height: "30px", objectFit: "cover", borderRadius: "4px", border: "1px solid #ccc" }} /> : "N/A"}
                      </td>
                    );
                  } else {
                    return (
                      <td key={key} style={{ padding: "10px 12px", borderBottom: "1px solid #ddd", fontSize: "0.9rem" }}>
                        {val}
                      </td>
                    );
                  }
                })}
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {/* No owners */}
      {!loading && owners.length === 0 && !error && <p style={{ textAlign: "center", fontWeight: "bold", marginTop: "20px" }}>No owners found</p>}
    </div>
  );
};

export default ListOwners;
