import React, { useState, useEffect, useCallback } from "react";

const OwnersList = () => {
  const [owners, setOwners] = useState([]);
  const [loading, setLoading] = useState(true);
  const [search, setSearch] = useState("");

  const fetchOwners = useCallback(async () => {
    try {
      const res = await fetch("http://localhost:5000/owners_list", {
        credentials: "include",
      });
      const data = await res.json();
      if (data.success) setOwners(data.owners);
    } catch (err) {
      console.error("Error fetching owners:", err);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchOwners();
  }, [fetchOwners]);

  const filteredOwners = owners.filter(owner =>
    Object.values(owner).some(val =>
      String(val).toLowerCase().includes(search.toLowerCase())
    )
  );

  const handleEdit = (owner_code) => {
    console.log("Edit owner:", owner_code);
    // Redirect or open edit modal
  };

  const handleDelete = async (owner_code) => {
    if (!window.confirm("Are you sure you want to delete this owner?")) return;
    console.log("Delete owner:", owner_code);
    // Call delete API here
  };

  return (
    <div>
      <div className="header-left">
        <span>{new Date().toLocaleDateString()}</span> | 
        <span>Total Owners: {owners.length}</span>
      </div>
      <h2>List of Owners</h2>
      <div className="header-right">
        <a href="/" className="back-link">← Go Back Home</a>
      </div>

      {/* ✅ Search Bar */}
      <input
        type="text"
        placeholder="Search owners..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        style={{ marginBottom: "10px", padding: "5px", width: "200px" }}
      />

      {loading ? (
        <p>Loading...</p>
      ) : (
        <table border="1" cellPadding="5">
          <thead>
            <tr>
              <th>Owner Code</th>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Email</th>
              <th>Mobile</th>
              <th>Nationality</th>
              <th>Image</th>
              <th>Actions</th> {/* ✅ New Column */}
            </tr>
          </thead>
          <tbody>
            {filteredOwners.map((owner) => (
              <tr key={owner.owner_code}>
                <td>{owner.owner_code}</td>
                <td>{owner.first_name}</td>
                <td>{owner.last_name}</td>
                <td>{owner.email}</td>
                <td>{owner.mobile_number}</td>
                <td>{owner.nationality}</td>
                <td>
                  {owner.eid_image ? (
                    <img
                      src={`data:image/jpeg;base64,${owner.eid_image}`}
                      alt="EID"
                      width="50"
                      height="50"
                    />
                  ) : "No Image"}
                </td>
                <td>
                  <button onClick={() => handleEdit(owner.owner_code)}>Edit</button>
                  <button onClick={() => handleDelete(owner.owner_code)} style={{ marginLeft: "5px", color: "red" }}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};
export default OwnersList;
