import React, { useEffect, useState } from "react";

const ListProperty = () => {
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editProperty, setEditProperty] = useState(null); // Property to edit
  const [showModal, setShowModal] = useState(false);

  const photoFields = [
    "property_photo"
  ];

  useEffect(() => {
    fetchProperties();
  }, []);

  const fetchProperties = async () => {
    try {
      setLoading(true);
      const res = await fetch("http://localhost:5000/list_property_api", { method: "GET", credentials: "include" });
      const data = await res.json();
      setProperties(data);
    } catch (err) {
      console.error(err);
      alert("Error fetching properties");
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (property_code) => {
    if (!window.confirm(`Are you sure you want to delete property ${property_code}?`)) return;
    try {
      const res = await fetch(`http://localhost:5000/property_delete_api/${property_code}`, { method: "DELETE", credentials: "include" });
      const data = await res.json();
      alert(data.message);
      fetchProperties();
    } catch (err) {
      console.error(err);
      alert("Error deleting property");
    }
  };

  const handleEditOpen = (property) => {
    setEditProperty(property);
    setShowModal(true);
  };

  const handleEditChange = (field, value) => {
    setEditProperty({ ...editProperty, [field]: value });
  };

  const handleEditSave = async () => {
    try {
      const res = await fetch(`http://localhost:5000/property_edit_api/${editProperty.property_code}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify(editProperty),
      });
      const data = await res.json();
      alert(data.message);
      setShowModal(false);
      fetchProperties();
    } catch (err) {
      console.error(err);
      alert("Error updating property");
    }
  };

  const labelize = (s) => s.replace(/_/g, " ").replace(/\b\w/g, (ch) => ch.toUpperCase());

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: 20, background: "#f5f6fa" }}>
      <h2 style={{ textAlign: "center", background: "#2f3640", color: "white", padding: 15, margin: 0 }}>Property List</h2>

      <div style={{ margin: "15px 0", display: "flex", justifyContent: "space-between" }}>
        <span>Total Properties: {properties.length}</span>
        <a href="/index" style={{ textDecoration: "none", color: "#333" }}>‚Üê Go Back Home</a>
      </div>

      {loading ? <p>Loading properties...</p> :
        <div style={{ overflowX: "auto", background: "white", padding: 20, borderRadius: 10 }}>
          <table style={{ width: "100%", borderCollapse: "collapse" }}>
            <thead>
              <tr>
                {properties[0] && Object.keys(properties[0]).map((col, idx) => (
                  <th key={idx} style={{ padding: 8, border: "1px solid #ccc", background: "#273c75", color: "white" }}>{labelize(col)}</th>
                ))}
                <th style={{ padding: 8, border: "1px solid #ccc", background: "#273c75", color: "white" }}>Actions</th>
              </tr>
            </thead>
            <tbody>
              {properties.map((p, idx) => (
                <tr key={idx} style={{ backgroundColor: idx % 2 === 0 ? "#f2f2f2" : "#fff" }}>
                  {Object.keys(p).map((key) => (
                    <td key={key} style={{ padding: 8, textAlign: "center", border: "1px solid #ccc" }}>
                      {photoFields.includes(key) && p[key] ? (
                        <img src={`data:image/jpeg;base64,${p[key]}`} alt={key} style={{ width: 60, height: 60, borderRadius: 4 }} />
                      ) : p[key]}
                    </td>
                  ))}
                  <td style={{ textAlign: "center" }}>
                    <button onClick={() => handleEditOpen(p)} style={{ marginRight: 10, padding: "5px 10px", background: "#2980b9", color: "white", border: "none", borderRadius: 4 }}>Edit</button>
                    <button onClick={() => handleDelete(p.property_code)} style={{ padding: "5px 10px", background: "#c0392b", color: "white", border: "none", borderRadius: 4 }}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      }

      {/* Edit Modal */}
      {showModal && editProperty && (
        <div style={{
          position: "fixed", top: 0, left: 0, width: "100%", height: "100%",
          background: "rgba(0,0,0,0.5)", display: "flex", justifyContent: "center", alignItems: "center"
        }}>
          <div style={{ background: "white", padding: 20, borderRadius: 10, width: "80%", maxHeight: "90%", overflowY: "auto" }}>
            <h3>Edit Property {editProperty.property_code}</h3>
            <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: 15 }}>
              {Object.keys(editProperty).map((key) => (
                key !== "property_photo" ? (
                  <div key={key}>
                    <label style={{ fontWeight: "bold" }}>{labelize(key)}</label>
                    <input
                      type="text"
                      value={editProperty[key] ?? ""}
                      onChange={(e) => handleEditChange(key, e.target.value)}
                      style={{ width: "100%", padding: 5, marginTop: 4 }}
                    />
                  </div>
                ) : (
                  <div key={key}>
                    <label style={{ fontWeight: "bold" }}>{labelize(key)}</label>
                    {editProperty[key] ? (
                      <img src={`data:image/jpeg;base64,${editProperty[key]}`} alt={key} style={{ width: 100, height: 100, borderRadius: 6 }} />
                    ) : <p>No photo</p>}
                  </div>
                )
              ))}
            </div>
            <div style={{ marginTop: 20, textAlign: "right" }}>
              <button onClick={() => setShowModal(false)} style={{ marginRight: 10, padding: "8px 15px" }}>Cancel</button>
              <button onClick={handleEditSave} style={{ padding: "8px 15px", background: "#27ae60", color: "white", border: "none", borderRadius: 4 }}>Save</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ListProperty;
