import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

const EditServTrans = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [form, setForm] = useState({});

  useEffect(() => {
    fetch(`http://localhost:5000/get_servtrans/${id}`)
      .then((res) => res.json())
      .then((data) => {
        if (data.success) setForm(data.data);
        else alert(data.message);
      })
      .catch((err) => console.error("Error fetching record:", err));
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    Object.keys(form).forEach((key) => formData.append(key, form[key]));

    try {
      const res = await fetch(`http://localhost:5000/update_servtrans/${id}`, {
        method: "POST",
        body: formData,
      });
      const result = await res.json();
      if (result.success) {
        alert(result.message);
        navigate("/list-servtrans");
      } else {
        alert("❌ " + result.message);
      }
    } catch (err) {
      console.error("Error updating record:", err);
    }
  };

  const serviceTypes = ["PL", "PA", "CL", "DA", "EL", "OT"];
  const statuses = ["OP", "IP", "CL"];

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "flex-start",
        minHeight: "100vh",
        backgroundColor: "#f5f6fa",
        paddingTop: "50px",
        fontFamily: "'Poppins', sans-serif",
      }}
    >
      <div
        style={{
          width: "600px",
          background: "white",
          borderRadius: "12px",
          boxShadow: "0 4px 20px rgba(0,0,0,0.1)",
          overflow: "hidden",
        }}
      >
        <div
          style={{
            background: "#273c75",
            color: "white",
            textAlign: "center",
            padding: "20px 0",
            fontSize: "22px",
            fontWeight: "bold",
          }}
        >
          Edit Service Transaction #{id}
        </div>

        <form onSubmit={handleSubmit} style={{ padding: "30px" }}>
          <div style={{ display: "flex", flexDirection: "column", gap: "18px" }}>
            <div>
              <label style={{ fontWeight: "500" }}>Service Type</label>
              <select
                name="ServiceRequestType"
                value={form.ServiceRequestType || ""}
                onChange={handleChange}
                style={selectStyle}
              >
                <option value="">Select Type</option>
                {serviceTypes.map((t) => (
                  <option key={t} value={t}>
                    {t}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label style={{ fontWeight: "500" }}>Description</label>
              <input
                name="ServiceRequestDescription"
                value={form.ServiceRequestDescription || ""}
                onChange={handleChange}
                style={inputStyle}
              />
            </div>

            <div>
              <label style={{ fontWeight: "500" }}>Attended By</label>
              <input
                name="ServiceRequestAttendedBy"
                value={form.ServiceRequestAttendedBy || ""}
                onChange={handleChange}
                style={inputStyle}
              />
            </div>

            <div>
              <label style={{ fontWeight: "500" }}>Estimated Cost</label>
              <input
                type="number"
                name="ServiceRequestEstimatedCost"
                value={form.ServiceRequestEstimatedCost || ""}
                onChange={handleChange}
                style={inputStyle}
              />
            </div>

            <div>
              <label style={{ fontWeight: "500" }}>Actual Cost</label>
              <input
                type="number"
                name="ServiceRequestActualCost"
                value={form.ServiceRequestActualCost || ""}
                onChange={handleChange}
                style={inputStyle}
              />
            </div>

            <div>
              <label style={{ fontWeight: "500" }}>Status</label>
              <select
                name="StatusOfServiceRequest"
                value={form.StatusOfServiceRequest || ""}
                onChange={handleChange}
                style={selectStyle}
              >
                <option value="">Select Status</option>
                {statuses.map((s) => (
                  <option key={s} value={s}>
                    {s}
                  </option>
                ))}
              </select>
            </div>
          </div>

          <div style={{ textAlign: "center", marginTop: "30px" }}>
            <button
              type="button"
              onClick={() => navigate("/list-servtrans")}
              style={{
                ...buttonStyle,
                backgroundColor: "#718093",
                marginRight: "15px",
              }}
            >
              Cancel
            </button>
            <button
              type="submit"
              style={{ ...buttonStyle, backgroundColor: "#27ae60" }}
            >
              Update
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

const inputStyle = {
  width: "100%",
  padding: "10px",
  border: "1px solid #ccc",
  borderRadius: "8px",
  fontSize: "14px",
  marginTop: "5px",
};

const selectStyle = {
  ...inputStyle,
  backgroundColor: "white",
};

const buttonStyle = {
  width: "130px",
  padding: "10px",
  color: "white",
  border: "none",
  borderRadius: "6px",
  fontSize: "15px",
  cursor: "pointer",
  transition: "0.3s",
};

export default EditServTrans;
