import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const AddFinTrans = () => {
  const [contractId, setContractId] = useState("");
  const [contractData, setContractData] = useState(null);
  const [formData, setFormData] = useState({
    ReceiptPayment: "Receipt",
    ReceiptPaymentReason: "",
    ModeOfPayment: "",
    TrDate: "",
    TrAmount: "",
    ReferenceNumber: "",
    ChequeDate: "",
  });

  const navigate = useNavigate();

  // ------------------- Search Contract -------------------
  const handleSearch = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch(`http://localhost:5000/contract/${contractId}`);
      const data = await res.json();

      if (!data.success) {
        alert(data.message || "Contract not found!");
        setContractData(null);
        return;
      }

      setContractData(data.contract);
    } catch (error) {
      console.error("Error fetching contract:", error);
      alert("Error fetching contract details!");
    }
  };

  // ------------------- Input Change -------------------
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // ------------------- Save FinTrans -------------------
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!contractData) {
      alert("Please search and select a valid contract first!");
      return;
    }

    const payload = {
      ...formData,
      PropertyCode: contractData.property_code,
      TenancyContractNumber: contractData.contract_id,
      TenantName: contractData.tenant_name,
    };

    try {
      const res = await fetch("http://localhost:5000/addfintrans", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });

      const result = await res.json();

      if (result.success) {
        alert("‚úÖ Financial Transaction saved successfully!");
        localStorage.removeItem("pending_contract"); // clear saved contract
        navigate("/addcontract"); // go back after success
      } else {
        alert(result.message || "Failed to save financial transaction.");
      }
    } catch (error) {
      console.error("Error submitting financial transaction:", error);
      alert("Error submitting financial transaction!");
    }
  };

  // ------------------- Options -------------------
  const paymentModes = ["Cash", "Cheque", "Online", "Card"];
  const receiptReasons = ["Rent", "Deposit", "Maintenance", "Other"];

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: "20px" }}>
      <h2
        style={{
          textAlign: "center",
          background: "#2f3640",
          color: "white",
          padding: "15px 0",
        }}
      >
        Add Financial Transaction
      </h2>

      {/* Search Contract */}
      <form
        onSubmit={handleSearch}
        style={{ textAlign: "center", margin: "20px 0" }}
      >
        <label>Enter Contract ID:</label>
        <input
          type="text"
          value={contractId}
          onChange={(e) => setContractId(e.target.value)}
          placeholder="Ex: 1001"
          required
          style={{ margin: "0 10px", padding: "5px", width: 120 }}
        />
        <button
          type="submit"
          style={{
            padding: "5px 10px",
            cursor: "pointer",
            width: 100,
          }}
        >
          Search
        </button>
      </form>

      {contractData && (
        <>
          {/* Readonly Contract Info */}
          <div
            style={{
              display: "grid",
              gridTemplateColumns: "repeat(3, 1fr)",
              gap: "10px",
              marginBottom: "20px",
            }}
          >
            <div>
              <label style={{ fontWeight: "bold" }}>Property Code</label>
              <input
                type="text"
                value={contractData.property_code}
                readOnly
                style={{ backgroundColor: "#f0f0f0", cursor: "not-allowed" }}
              />
            </div>

            <div>
              <label style={{ fontWeight: "bold" }}>Tenant ID</label>
              <input
                type="text"
                value={contractData.tenant_id}
                readOnly
                style={{ backgroundColor: "#f0f0f0", cursor: "not-allowed" }}
              />
            </div>

            <div>
              <label style={{ fontWeight: "bold" }}>Tenant Name</label>
              <input
                type="text"
                value={contractData.tenant_name}
                readOnly
                style={{ backgroundColor: "#f0f0f0", cursor: "not-allowed" }}
              />
            </div>
          </div>

          {/* Financial Transaction Form */}
          <form onSubmit={handleSubmit}>
            <div
              style={{
                display: "grid",
                gridTemplateColumns: "repeat(3, 1fr)",
                gap: "10px",
              }}
            >
              <div>
                <label style={{ fontWeight: "bold" }}>Receipt / Payment</label>
                <select
                  name="ReceiptPayment"
                  value={formData.ReceiptPayment}
                  onChange={handleChange}
                  required
                >
                  <option value="Receipt">Receipt</option>
                  <option value="Payment">Payment</option>
                </select>
              </div>

              <div>
                <label style={{ fontWeight: "bold" }}>
                  Receipt / Payment Reason
                </label>
                <select
                  name="ReceiptPaymentReason"
                  value={formData.ReceiptPaymentReason}
                  onChange={handleChange}
                  required
                >
                  <option value="">Select</option>
                  {receiptReasons.map((r) => (
                    <option key={r} value={r}>
                      {r}
                    </option>
                  ))}
                </select>
              </div>

              <div>
                <label style={{ fontWeight: "bold" }}>Mode of Payment</label>
                <select
                  name="ModeOfPayment"
                  value={formData.ModeOfPayment}
                  onChange={handleChange}
                  required
                >
                  <option value="">Select</option>
                  {paymentModes.map((mode) => (
                    <option key={mode} value={mode}>
                      {mode}
                    </option>
                  ))}
                </select>
              </div>

              <div>
                <label style={{ fontWeight: "bold" }}>Transaction Date</label>
                <input
                  type="date"
                  name="TrDate"
                  value={formData.TrDate}
                  onChange={handleChange}
                  required
                />
              </div>

              <div>
                <label style={{ fontWeight: "bold" }}>Transaction Amount</label>
                <input
                  type="number"
                  name="TrAmount"
                  value={formData.TrAmount}
                  onChange={handleChange}
                  placeholder="Enter Amount"
                  required
                />
              </div>

              <div>
                <label style={{ fontWeight: "bold" }}>Reference Number</label>
                <input
                  type="text"
                  name="ReferenceNumber"
                  value={formData.ReferenceNumber}
                  onChange={handleChange}
                  placeholder="Optional"
                />
              </div>

              <div>
                <label style={{ fontWeight: "bold" }}>Cheque Date</label>
                <input
                  type="date"
                  name="ChequeDate"
                  value={formData.ChequeDate}
                  onChange={handleChange}
                />
              </div>
            </div>

            {/* Buttons */}
            <div style={{ textAlign: "center", marginTop: "30px" }}>
              <button
                type="button"
                onClick={() => navigate("/addcontract")}
                style={{
                  width: 200,
                  padding: "10px 20px",
                  backgroundColor: "#492bc0ff",
                  color: "white",
                  borderRadius: "6px",
                  border: "none",
                  cursor: "pointer",
                  marginRight: "20px",
                }}
              >
                ‚Üê Back to Add Contract
              </button>

              <button
                type="submit"
                style={{
                  width: 200,
                  padding: "10px 20px",
                  backgroundColor: "#27ae60",
                  color: "white",
                  border: "none",
                  borderRadius: "6px",
                  cursor: "pointer",
                }}
              >
                üíæ Save Transaction
              </button>
            </div>
          </form>
        </>
      )}
    </div>
  );
};

export default AddFinTrans;
