import React, { useState } from "react";

const ModifyProperty = () => {
  const [searchCode, setSearchCode] = useState("");
  const [property, setProperty] = useState(null);
  const [formData, setFormData] = useState({});
  const [photoPreview, setPhotoPreview] = useState(null);

  // -------- Search Property --------
  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchCode) return;

    try {
      const res = await fetch("http://localhost:5000/modify_property", {
        method: "POST",
        credentials: "include",
        body: new URLSearchParams({ search_property_code: searchCode }),
      });
      const data = await res.json();

      if (data.success) {
        setProperty(data.property);
        if (data.property.property_photo) {
          setPhotoPreview(`data:image/jpeg;base64,${data.property.property_photo}`);
        }
      } else {
        alert(data.message || "Property not found");
        setProperty(null);
        setPhotoPreview(null);
      }
    } catch (err) {
      console.error(err);
      alert("Error fetching property");
    }
  };

  // -------- Input Change --------
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    setFormData((prev) => ({ ...prev, property_photo: file }));

    if (file.type.startsWith("image/")) {
      const reader = new FileReader();
      reader.onload = (ev) => setPhotoPreview(ev.target.result);
      reader.readAsDataURL(file);
    } else {
      setPhotoPreview(null);
    }
  };

  // -------- Update Property --------
  const handleUpdate = async (e) => {
    e.preventDefault();
    if (!property) return alert("Search a property first");

    const data = new FormData();
    data.append("property_code", property.property_code);

    Object.keys(formData).forEach((key) => {
      if (formData[key] !== undefined) data.append(key, formData[key]);
    });

    try {
      const res = await fetch("http://localhost:5000/modify_property", {
        method: "POST",
        credentials: "include",
        body: data,
      });

      const result = await res.json();
      if (result.success) {
        alert(result.message);
        setProperty(null);
        setFormData({});
        setPhotoPreview(null);
        setSearchCode("");
      } else {
        alert(result.message || "Failed to update property");
      }
    } catch (err) {
      console.error(err);
      alert("Error updating property");
    }
  };

  // -------- Render Input Rows (5 per row) --------
  const renderFields = (fields) => {
    const rows = [];
    for (let i = 0; i < fields.length; i += 5) {
      const rowFields = fields.slice(i, i + 5);
      rows.push(
        <div key={i} style={{ display: "grid", gridTemplateColumns: "repeat(5, 1fr)", gap: "15px", marginBottom: "15px" }}>
          {rowFields.map((field) => (
            <div key={field}>
              <label style={{ fontWeight: "bold", display: "block", marginBottom: "5px" }}>
                {field.replace(/_/g, " ").toUpperCase()}
              </label>
              <input
                type={field.includes("date") ? "date" : "text"}
                name={field}
                defaultValue={property[field] || ""}
                onChange={handleChange}
                style={{ width: "90%", padding: "6px", borderRadius: "5px", border: "1px solid #ccc" }}
              />
            </div>
          ))}
        </div>
      );
    }
    return rows;
  };

  const allFields = [
    "primary_owner_code","developer_name","building_name","property_type","name",
    "address1","address2","city","state","country",
    "zip_code","municipality_number","electricity_bill_number","water_bill_number","ownership_type",
    "furnishing_status","land_area","carpet_area","builtup_area","bedrooms",
    "bathrooms","washrooms","facing","car_parkings","servant_rooms",
    "balconies","has_dishwasher","has_washing_machine","property_value","year_of_construction",
    "expected_rent_value","status","created_by","creation_date"
  ];

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: "20px", background: "#f5f6fa" }}>
      <h2 style={{ textAlign: "center", background: "#2f3640", color: "white", padding: "15px 0" }}>Modify Property</h2>

      {/* Search Form */}
      <form onSubmit={handleSearch} style={{ textAlign: "center", margin: "20px 0" }}>
        <label>Enter Property Code:</label>
        <input
          type="text"
          value={searchCode}
          onChange={(e) => setSearchCode(e.target.value)}
          placeholder="Ex: P001"
          required
          style={{ width: "120px", padding: "6px", margin: "0 10px", borderRadius: "5px", border: "1px solid #aaa" }}
        />
        <button type="submit" style={{ padding: "6px 12px", background: "#273c75", color: "white", border: "none", borderRadius: "5px", cursor: "pointer",width: 200 }}>Search</button>
      </form>

      {/* Property Form */}
      {property && (
        <form onSubmit={handleUpdate} encType="multipart/form-data" style={{ background: "white", padding: "25px", borderRadius: "10px", maxWidth: "1500px", margin: "0 auto 30px", boxShadow: "0 0 10px #ccc" }}>
          {renderFields(allFields)}

          {/* Photo Section */}
          <div style={{ textAlign: "center", marginTop: "30px"}}>
            <label style={{ fontWeight: "bold" }}>Property Photo:</label><br />
            {photoPreview ? (
              <img src={photoPreview} alt="Property" style={{ maxWidth: "80px", borderRadius: "10px", border: "1px solid #ccc", marginTop: "10px" }} />
            ) : (
              <p>No photo available</p>
            )}
            <br />
            <input type="file" name="property_photo" onChange={handleFileChange} style={{ marginTop: "10px" }} />
          </div>

          <div style={{ textAlign: "center", marginTop: "30px" }}>
            <a href="/index" style={{ ...styles.button, backgroundColor: "#492bc0ff", marginRight: "20px", textDecoration: "none", display: "inline-block" }}>‚Üê Go Back Home</a>
            <button type="submit" style={styles.deleteButton}>Modify Property</button>
          </div>
        </form>
      )}

    </div>
  );
};
const styles={
    button: { width:200, padding: "10px 20px", backgroundColor: "#0097e6", color: "white", border: "none", borderRadius: "6px", cursor: "pointer", fontSize: "16px" },
  deleteButton: { width:200, padding: "10px 20px", backgroundColor: "#c0392b", color: "white", border: "none", borderRadius: "6px", cursor: "pointer", fontSize: "16px" },

};
export default ModifyProperty;
