import React, { useState } from "react";

const AddFinTrans = ({ sessionUser }) => {
  const [formData, setFormData] = useState({
    property_code: "",
    tenant_id: "",
    tenant_name: "",
    receipt_payment: "Receipt",
    receipt_payment_reason: "",
    mode_of_payment: "Cash",
    tr_date: "",
    tr_amount: "",
    reference_number: "",
    cheque_date: "",
    bank_name: "",
    bank_city: "",
    ifsc_code: "",
    cheque_status: "",
    created_by: sessionUser || "admin",
    creation_date: new Date().toISOString().split("T")[0],
  });

  const [chequeImage, setChequeImage] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    setChequeImage(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const data = new FormData();
    Object.entries(formData).forEach(([key, value]) => data.append(key, value));
    if (chequeImage) data.append("cheque_image", chequeImage);

    try {
      const res = await fetch("http://localhost:5000/add_fintrans", {
        method: "POST",
        body: data,
      });
      const result = await res.json();

      if (result.success) {
        alert(result.message);
        setFormData({
          property_code: "",
          tenant_id: "",
          tenant_name: "",
          receipt_payment: "Receipt",
          receipt_payment_reason: "",
          mode_of_payment: "Cash",
          tr_date: "",
          tr_amount: "",
          reference_number: "",
          cheque_date: "",
          bank_name: "",
          bank_city: "",
          ifsc_code: "",
          cheque_status: "",
          created_by: sessionUser || "admin",
          creation_date: new Date().toISOString().split("T")[0],
        });
        setChequeImage(null);
      } else {
        alert(result.message || "❌ Failed to add transaction");
      }
    } catch (err) {
      console.error("❌ Error adding FinTrans:", err);
      alert("Error connecting to server.");
    }
  };

  return (
    <div className="add-fintrans">
      <h2>Add Financial Transaction</h2>
      <form onSubmit={handleSubmit}>
        <label>Property Code:</label>
        <input
          type="number"
          name="property_code"
          value={formData.property_code}
          onChange={handleChange}
          required
        />

        <label>Tenancy Contract Number:</label>
        <input
          type="number"
          name="tenant_id"
          value={formData.tenant_id}
          onChange={handleChange}
        />

        <label>Tenant Name (if ID not known):</label>
        <input
          type="text"
          name="tenant_name"
          value={formData.tenant_name}
          onChange={handleChange}
        />

        <label>Receipt / Payment:</label>
        <select name="receipt_payment" value={formData.receipt_payment} onChange={handleChange}>
          <option value="Receipt">Receipt</option>
          <option value="Payment">Payment</option>
        </select>

        <label>Reason:</label>
        <input
          type="text"
          name="receipt_payment_reason"
          value={formData.receipt_payment_reason}
          onChange={handleChange}
        />

        <label>Mode of Payment:</label>
        <select name="mode_of_payment" value={formData.mode_of_payment} onChange={handleChange}>
          <option value="Cash">Cash</option>
          <option value="Check">Check</option>
          <option value="Online">Online</option>
        </select>

        <label>Transaction Date:</label>
        <input type="date" name="tr_date" value={formData.tr_date} onChange={handleChange} />

        <label>Amount:</label>
        <input type="number" name="tr_amount" value={formData.tr_amount} onChange={handleChange} />

        <label>Reference Number:</label>
        <input type="text" name="reference_number" value={formData.reference_number} onChange={handleChange} />

        <label>Cheque Date:</label>
        <input type="date" name="cheque_date" value={formData.cheque_date} onChange={handleChange} />

        <label>Bank Name:</label>
        <input type="text" name="bank_name" value={formData.bank_name} onChange={handleChange} />

        <label>Bank City:</label>
        <input type="text" name="bank_city" value={formData.bank_city} onChange={handleChange} />

        <label>IFSC Code:</label>
        <input type="text" name="ifsc_code" value={formData.ifsc_code} onChange={handleChange} />

        <label>Cheque Status:</label>
        <select name="cheque_status" value={formData.cheque_status} onChange={handleChange}>
          <option value="">Select</option>
          <option value="Cleared">Cleared</option>
          <option value="Bounced">Bounced</option>
        </select>

        <label>Cheque Image:</label>
        <input type="file" name="cheque_image" onChange={handleFileChange} />

        <button type="submit">Add Transaction</button>
      </form>
    </div>
  );
};

export default AddFinTrans;
