import React, { useState, useEffect } from "react";

const ListOwners = () => {
  const [owners, setOwners] = useState([]);
  const [sortField, setSortField] = useState("");
  const [sortOrder, setSortOrder] = useState("asc");
  const [currentDate, setCurrentDate] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    setCurrentDate(new Date().toLocaleDateString());
    fetchOwners();
  }, [sortField, sortOrder]);

  const fetchOwners = async () => {
    setLoading(true);
    setError("");
    try {
      const url = `http://localhost:5000/owners?sort=${sortField}&order=${sortOrder}`;
      const res = await fetch(url, { credentials: "include" });
      const data = await res.json();

      console.log("Fetched owners:", data);

      // Support both array and object with owners key
      if (Array.isArray(data)) {
        setOwners(data);
      } else if (data.owners && Array.isArray(data.owners)) {
        setOwners(data.owners);
      } else {
        setOwners([]);
        setError("No owners found.");
      }
    } catch (err) {
      console.error(err);
      setOwners([]);
      setError("Error fetching owners from server.");
    } finally {
      setLoading(false);
    }
  };

  const handleSort = (field) => {
    if (sortField === field) {
      setSortOrder(sortOrder === "asc" ? "desc" : "asc");
    } else {
      setSortField(field);
      setSortOrder("asc");
    }
  };

  return (
    <div style={{ maxWidth: "1600px", margin: "auto", padding: "20px", overflowX: "auto" }}>
      {/* Header */}
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", flexWrap: "wrap", marginBottom: "15px" }}>
        <div style={{ fontWeight: "bold", gap: "10px", display: "flex" }}>
          <span>{currentDate}</span> | <span>Total Owners: {owners.length}</span>
        </div>
        <h2 style={{ margin: "0 auto", color: "black" }}>List of Owners</h2>
        <div style={{ fontWeight: "bold" }}>
          <a href="/" style={{ textDecoration: "none", color: "#333" }}>‚Üê Go Back Home</a>
        </div>
      </div>

      {loading && <p style={{ textAlign: "center" }}>Loading owners...</p>}
      {error && <p style={{ textAlign: "center", color: "red", fontWeight: "bold" }}>{error}</p>}

      {owners.length > 0 && (
        <table style={{ width: "100%", borderCollapse: "collapse", background: "white", borderRadius: "8px", overflow: "hidden" }}>
          <thead>
            <tr>
              {Object.keys(owners[0]).map((col) => {
                const isImageCol = ["eid_image", "passport_copy", "res_visa"].includes(col);
                return (
                  <th
                    key={col}
                    onClick={() => !isImageCol && handleSort(col)}
                    style={{
                      padding: "10px 12px",
                      borderBottom: "1px solid #ddd",
                      textAlign: "left",
                      fontSize: "0.9rem",
                      background: "#007BFF",
                      color: "white",
                      cursor: isImageCol ? "default" : "pointer"
                    }}
                  >
                    {col.replace("_", " ").toUpperCase()} {!isImageCol && sortField === col && `(${sortOrder})`}
                  </th>
                );
              })}
            </tr>
          </thead>
          <tbody>
            {owners.map((owner) => (
              <tr key={owner.owner_code} style={{ backgroundColor: "#fff" }}>
                {Object.entries(owner).map(([key, val]) => {
                  if (["eid_image", "passport_copy", "res_visa"].includes(key)) {
                    return (
                      <td key={key} style={{ padding: "10px 12px", borderBottom: "1px solid #ddd" }}>
                        {val ? (
                          <img
                            src={`http://localhost:5000/owner_image/${owner.owner_code}/${key === "eid_image" ? "eid" : key === "passport_copy" ? "passport" : "visa"}`}
                            alt={key}
                            style={{ width: "30px", height: "30px", objectFit: "cover", borderRadius: "4px", border: "1px solid #ccc" }}
                          />
                        ) : "N/A"}
                      </td>
                    );
                  } else {
                    return (
                      <td key={key} style={{ padding: "10px 12px", borderBottom: "1px solid #ddd", fontSize: "0.9rem" }}>
                        {val}
                      </td>
                    );
                  }
                })}
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default ListOwners;
