import React, { useState } from "react";

const ModifyProperty = ({ sessionUser }) => {
  const [searchCode, setSearchCode] = useState("");
  const [property, setProperty] = useState(null);
  const [formData, setFormData] = useState({});
  const [photoPreview, setPhotoPreview] = useState(null);

  // -------- Search Property --------
  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchCode) return;

    try {
      const res = await fetch(`http://localhost:5000/properties/${searchCode}`, {
        method: "GET",
        credentials: "include",
      });
      const data = await res.json();
      if (res.ok && data.success) {
        setProperty(data.property);

        // If photo exists
        if (data.property.property_photo) {
          setPhotoPreview(`data:image/jpeg;base64,${data.property.property_photo}`);
        }
      } else {
        alert(data.message || "Property not found");
        setProperty(null);
      }
    } catch (err) {
      console.error(err);
      alert("Error fetching property");
    }
  };

  // -------- Input Change --------
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    setFormData((prev) => ({ ...prev, property_photo: file }));

    if (file.type.startsWith("image/")) {
      const reader = new FileReader();
      reader.onload = (ev) => setPhotoPreview(ev.target.result);
      reader.readAsDataURL(file);
    } else {
      setPhotoPreview(null);
    }
  };

  // -------- Update Property --------
  const handleUpdate = async (e) => {
    e.preventDefault();
    if (!property) return alert("Search a property first");

    const data = new FormData();
    data.append("property_code", property.property_code);

    Object.keys(formData).forEach((key) => data.append(key, formData[key]));

    try {
      const res = await fetch("http://localhost:5000/modify_property", {
        method: "POST",
        credentials: "include",
        body: data,
      });
      const result = await res.json();
      if (res.ok && result.success) {
        alert(result.message);
        setProperty(null);
        setFormData({});
        setPhotoPreview(null);
        setSearchCode("");
      } else {
        alert(result.message || "Failed to update property");
      }
    } catch (err) {
      console.error(err);
      alert("Error updating property");
    }
  };

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: "20px" }}>
      <h2 style={{ textAlign: "center", background: "#2f3640", color: "white", padding: "15px 0" }}>
        Modify Property
      </h2>

      {/* Search */}
      <form onSubmit={handleSearch} style={{ textAlign: "center", margin: "20px 0" }}>
        <label>Enter Property Code:</label>
        <input
          type="text"
          value={searchCode}
          onChange={(e) => setSearchCode(e.target.value)}
          placeholder="Ex: P001"
          required
          style={{ margin: "0 10px", padding: "5px" }}
        />
        <button type="submit" style={{ padding: "5px 10px", cursor: "pointer" }}>Search</button>
      </form>

      {/* Property Form */}
      {property && (
        <form onSubmit={handleUpdate} encType="multipart/form-data" style={{ width: "90%", margin: "0 auto" }}>
          {/* Render all fields */}
          {[
            "primary_owner_code","developer_name","building_name","property_type","name","address1","address2",
            "city","state","country","zip_code","municipality_number","electricity_bill_number","water_bill_number",
            "ownership_type","furnishing_status","land_area","carpet_area","builtup_area","bedrooms","bathrooms",
            "washrooms","facing","car_parkings","servant_rooms","balconies","has_dishwasher","has_washing_machine",
            "property_value","year_of_construction","expected_rent_value","status","created_by","creation_date"
          ].map((field) => (
            <div key={field} style={{ marginBottom: "15px" }}>
              <label style={{ fontWeight: "bold" }}>{field.replace(/_/g," ").toUpperCase()}</label>
              <input
                type={field.includes("date") ? "date" : "text"}
                name={field}
                defaultValue={property[field] || ""}
                onChange={handleChange}
                style={{ width: "80%", padding: "5px", marginLeft: "10px" }}
              />
            </div>
          ))}

          {/* Photo */}
          <div style={{ marginTop: "20px" }}>
            <label style={{ fontWeight: "bold" }}>Property Photo:</label><br />
            {photoPreview ? (
              <img src={photoPreview} alt="Property" style={{ maxWidth: "200px", marginTop: "10px", border: "1px solid #ccc", borderRadius: "10px" }} />
            ) : (
              <p>No photo available</p>
            )}
            <br />
            <input type="file" name="property_photo" onChange={handleFileChange} />
          </div>

          <div style={{ textAlign: "center", marginTop: "20px" }}>
            <button type="submit" style={{ padding: "10px 20px", backgroundColor: "#0097e6", color: "white", border: "none", borderRadius: "6px", cursor: "pointer" }}>
              Update Property
            </button>
          </div>
        </form>
      )}

      <a href="/index" style={{ display: "block", textAlign: "center", marginTop: "20px", fontWeight: "bold", color: "#333" }}>
        ‚Üê Go Back Home
      </a>
    </div>
  );
};

export default ModifyProperty;
