import React, { useEffect, useState } from "react";

const ListProperty = () => {
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");

  const columns = [
    "property_code",
    "primary_owner_code",
    "developer_name",
    "building_name",
    "property_type",
    "name",
    "address1",
    "address2",
    "city",
    "state",
    "country",
    "zip_code",
    "municipality_number",
    "electricity_bill_number",
    "water_bill_number",
    "ownership_type",
    "furnishing_status",
    "land_area",
    "carpet_area",
    "builtup_area",
    "bedrooms",
    "bathrooms",
    "washrooms",
    "facing",
    "car_parkings",
    "servant_rooms",
    "balconies",
    "has_dishwasher",
    "has_washing_machine",
    "property_value",
    "year_of_construction",
    "second_owner_code",
    "expected_rent_value",
    "status",
    "mtd_income",
    "ytd_income",
    "mtd_expense",
    "ytd_expense",
    "purchase_value",
    "construction_cost",
    "renovation_cost",
    "registration",
    "transfer_fees",
    "misc_expense",
    "created_by",
    "creation_date",
    "property_photo",
  ];

  useEffect(() => {
    fetchProperties();
  }, []);

  const fetchProperties = async () => {
    try {
      setLoading(true);
      const res = await fetch("http://localhost:5000/list_property", {
        method: "GET",
        credentials: "include",
      });
      const data = await res.json();
      if (res.ok) {
        setProperties(data.properties);
      } else {
        alert(data.message || "Failed to fetch properties");
      }
    } catch (err) {
      console.error("Error fetching properties:", err);
      alert("Error fetching properties");
    } finally {
      setLoading(false);
    }
  };

  const handleSort = (col) => {
    const sorted = [...properties].sort((a, b) => {
      const valA = a[col] ?? "";
      const valB = b[col] ?? "";

      if (!isNaN(valA) && !isNaN(valB)) return valA - valB;
      return String(valA).localeCompare(String(valB));
    });
    setProperties(sorted);
  };

  const handleDelete = async (property_code) => {
    if (!window.confirm(`Are you sure you want to delete property ${property_code}?`)) return;

    try {
      const res = await fetch("http://localhost:5000/delete_property", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ property_code }),
        credentials: "include",
      });
      const data = await res.json();
      if (res.ok && data.success) {
        alert(`✅ ${data.message}`);
        setProperties(properties.filter((p) => p.property_code !== property_code));
      } else {
        alert(`❌ ${data.message || "Failed to delete property"}`);
      }
    } catch (err) {
      console.error(err);
      alert("❌ Error deleting property");
    }
  };

  // Filter by searchTerm (property_code, primary_owner_code, name, developer_name)
  const filteredProperties = properties.filter((p) =>
    ["property_code", "primary_owner_code", "name", "developer_name"].some((key) =>
      String(p[key] ?? "").toLowerCase().includes(searchTerm.toLowerCase())
    )
  );

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: 20, background: "#f5f6fa" }}>
      <h2 style={{ textAlign: "center", background: "#2f3640", color: "white", padding: 15, margin: 0, borderRadius: "8px 8px 0 0" }}>
        All Properties
      </h2>

      <div style={{ display: "flex", justifyContent: "space-between", margin: "15px 0" }}>
        <div>
          <span>{new Date().toLocaleDateString()}</span> | <span>Total Properties: {filteredProperties.length}</span>
        </div>
        <div>
          <a href="/index" style={{ textDecoration: "none", fontWeight: "bold", color: "#333" }}>← Go Back Home</a>
        </div>
      </div>

      <div style={{ overflowX: "auto", background: "white", padding: 20, borderRadius: 10, boxShadow: "0 0 15px rgba(0,0,0,0.2)" }}>
        <input
          type="text"
          placeholder="Search by property code, owner, name or developer..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ marginBottom: 10, padding: 6, width: "50%", borderRadius: 5, border: "1px solid #aaa" }}
        />

        {loading ? (
          <p>Loading properties...</p>
        ) : (
          <table style={{ width: "100%", borderCollapse: "collapse" }}>
            <thead>
              <tr>
                {columns.map((col) => (
                  <th
                    key={col}
                    onClick={() => handleSort(col)}
                    style={{ padding: 8, border: "1px solid #ccc", background: "#273c75", color: "white", cursor: "pointer" }}
                  >
                    {col.replace(/_/g, " ").replace(/\b\w/g, (ch) => ch.toUpperCase())}
                  </th>
                ))}
                <th style={{ padding: 8, border: "1px solid #ccc", background: "#273c75", color: "white" }}>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredProperties.map((p, idx) => (
                <tr key={idx} style={{ backgroundColor: idx % 2 === 0 ? "#f2f2f2" : "#fff" }}>
                  {columns.map((col) => (
                    <td key={col} style={{ padding: 8, border: "1px solid #ccc", textAlign: "center" }}>
                      {col === "property_photo" && p[col] ? (
                        <img src={`data:image/jpeg;base64,${p[col]}`} alt="Property" style={{ width: 60, height: 60, borderRadius: 6, border: "1px solid #ccc" }} />
                      ) : (
                        String(p[col] ?? "")
                      )}
                    </td>
                  ))}
                  <td style={{ padding: 8, border: "1px solid #ccc", textAlign: "center" }}>
                    <a
                      href={`/edit_property/${p.property_code}`}
                      style={{ marginRight: 10, color: "#0097e6", fontWeight: "bold", textDecoration: "none" }}
                    >
                      Edit
                    </a>
                    <button
                      onClick={() => handleDelete(p.property_code)}
                      style={{ backgroundColor: "#c0392b", color: "white", border: "none", padding: "5px 10px", borderRadius: 4, cursor: "pointer" }}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default ListProperty;
