import React, { useState } from "react";
import "./ModifyOwner.css";

const ModifyOwner = () => {
  const [ownerCode, setOwnerCode] = useState("");
  const [ownerData, setOwnerData] = useState(null);
  const [message, setMessage] = useState("");
  const [eidPreview, setEidPreview] = useState(null);
  const [passportPreview, setPassportPreview] = useState(null);
  const [visaPreview, setVisaPreview] = useState(null);

  // Search owner by code
  const handleSearch = async (e) => {
    e.preventDefault();
    setMessage("");
    setOwnerData(null);

    try {
      const res = await fetch(`http://localhost:5000/owners/${ownerCode}`, {
        method: "GET",
        credentials: "include",
      });
      const data = await res.json();
      if (res.ok) {
        setOwnerData(data);
        // Set initial previews
        setEidPreview(
          data.eid_image ? `http://localhost:5000/owner_image/${data.owner_code}/eid` : null
        );
        setPassportPreview(
          data.passport_copy
            ? `http://localhost:5000/owner_image/${data.owner_code}/passport`
            : null
        );
        setVisaPreview(
          data.res_visa ? `http://localhost:5000/owner_image/${data.owner_code}/visa` : null
        );
      } else {
        setMessage(data.error || "Owner not found");
      }
    } catch (err) {
      console.error(err);
      setMessage("Error fetching owner");
    }
  };

  // Handle live file preview
  const handleFileChange = (e, type) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (event) => {
      if (type === "eid") setEidPreview(event.target.result);
      if (type === "passport") setPassportPreview(event.target.result);
      if (type === "visa") setVisaPreview(event.target.result);
    };
    reader.readAsDataURL(file);
  };

  // Handle update owner
  const handleUpdate = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);

    try {
      const res = await fetch("http://localhost:5000/update_owner", {
        method: "POST",
        credentials: "include",
        body: formData,
      });
      if (res.ok) {
        setMessage("✅ Owner updated successfully");
      } else {
        setMessage("❌ Error updating owner");
      }
    } catch (err) {
      console.error(err);
      setMessage("❌ Error updating owner");
    }
  };

  return (
    <div className="container" style={{ maxWidth: "1500px", margin: "auto", padding: "20px", background: "#fff", borderRadius: "8px", boxShadow: "0 0 10px rgba(0,0,0,0.1)" }}>
      <h2 style={{ textAlign: "center", color: "black", marginBottom: "20px" }}>Modify Owner</h2>

      {message && <p className="message" style={{ textAlign: "center", fontWeight: "bold", marginBottom: "15px", color: message.includes("✅") ? "green" : "red" }}>{message}</p>}

      {/* Search Section */}
      <form onSubmit={handleSearch} style={{ textAlign: "center", marginBottom: "20px" }}>
        <label>Enter Owner Code:</label>
        <input
          type="text"
          value={ownerCode}
          onChange={(e) => setOwnerCode(e.target.value)}
          className="small-input"
          placeholder="Owner Code"
          required
          style={{ width: "120px", padding: "6px", fontSize: "0.85rem", marginRight: "5px" }}
        />
        <button type="submit" style={{ background: "#007BFF", color: "white", padding: "6px 12px", border: "none", borderRadius: "4px", cursor: "pointer" }}>Search</button>
      </form>

      <a href="/" style={{ display: "block", textAlign: "center", marginBottom: "20px", color: "#333", fontWeight: "bold", textDecoration: "none" }}>← Go Back Home</a>

      {ownerData && (
        <form onSubmit={handleUpdate} encType="multipart/form-data">
          <input type="hidden" name="owner_code" value={ownerData.owner_code} />

          {/* Row 1 */}
          <div className="row" style={{ display: "flex", flexWrap: "wrap", marginBottom: "15px" }}>
            {["salutation", "first_name", "last_name", "nationality"].map((field, i) => (
              <div key={i} className="col-md-3" style={{ width: "25%", padding: "0 10px", boxSizing: "border-box", marginBottom: "15px" }}>
                <label style={{ display: "block", marginBottom: "5px", fontWeight: "bold", fontSize: "0.9rem" }}>{field.replace("_", " ").replace(/\b\w/g, c => c.toUpperCase())}</label>
                <input name={field} defaultValue={ownerData[field]} style={{ width: "100%", padding: "8px", border: "1px solid #ccc", borderRadius: "4px", fontSize: "0.9rem" }} />
              </div>
            ))}
          </div>

          {/* Row 2 */}
          <div className="row" style={{ display: "flex", flexWrap: "wrap", marginBottom: "15px" }}>
            {["address1", "address2", "city", "state"].map((field, i) => (
              <div key={i} className="col-md-3" style={{ width: "25%", padding: "0 10px", boxSizing: "border-box", marginBottom: "15px" }}>
                <label style={{ display: "block", marginBottom: "5px", fontWeight: "bold", fontSize: "0.9rem" }}>{field.replace("_", " ").replace(/\b\w/g, c => c.toUpperCase())}</label>
                <input name={field} defaultValue={ownerData[field]} style={{ width: "100%", padding: "8px", border: "1px solid #ccc", borderRadius: "4px", fontSize: "0.9rem" }} />
              </div>
            ))}
          </div>

          {/* Row 3 */}
          <div className="row" style={{ display: "flex", flexWrap: "wrap", marginBottom: "15px" }}>
            {["country", "zip_code", "mobile_number", "email"].map((field, i) => (
              <div key={i} className="col-md-3" style={{ width: "25%", padding: "0 10px", boxSizing: "border-box", marginBottom: "15px" }}>
                <label style={{ display: "block", marginBottom: "5px", fontWeight: "bold", fontSize: "0.9rem" }}>{field.replace("_", " ").replace(/\b\w/g, c => c.toUpperCase())}</label>
                <input
                  name={field}
                  type={field.includes("email") ? "email" : field.includes("mobile") ? "tel" : "text"}
                  defaultValue={ownerData[field]}
                  style={{ width: "100%", padding: "8px", border: "1px solid #ccc", borderRadius: "4px", fontSize: "0.9rem" }}
                />
              </div>
            ))}
          </div>

          {/* Row 4 */}
          <div className="row" style={{ display: "flex", flexWrap: "wrap", marginBottom: "15px" }}>
            {["passport_number", "emirates_id", "date_of_birth", "visa_expiry_date"].map((field, i) => (
              <div key={i} className="col-md-3" style={{ width: "25%", padding: "0 10px", boxSizing: "border-box", marginBottom: "15px" }}>
                <label style={{ display: "block", marginBottom: "5px", fontWeight: "bold", fontSize: "0.9rem" }}>{field.replace("_", " ").replace(/\b\w/g, c => c.toUpperCase())}</label>
                <input
                  name={field}
                  type={field.includes("date") ? "date" : "text"}
                  defaultValue={ownerData[field]}
                  style={{ width: "100%", padding: "8px", border: "1px solid #ccc", borderRadius: "4px", fontSize: "0.9rem" }}
                />
              </div>
            ))}
          </div>

          {/* Row 5 - Passport Expiry & Files */}
          <div className="row" style={{ display: "flex", flexWrap: "wrap", marginBottom: "15px" }}>
            <div className="col-md-3" style={{ width: "25%", padding: "0 10px", boxSizing: "border-box", marginBottom: "15px" }}>
              <label style={{ display: "block", marginBottom: "5px", fontWeight: "bold", fontSize: "0.9rem" }}>Passport Expiry</label>
              <input
                name="passport_expiry_date"
                type="date"
                defaultValue={ownerData.passport_expiry_date}
                style={{ width: "100%", padding: "8px", border: "1px solid #ccc", borderRadius: "4px", fontSize: "0.9rem" }}
              />
            </div>

            {/* Emirates ID File */}
            <div className="col-md-3" style={{ width: "25%", padding: "0 10px", boxSizing: "border-box", marginBottom: "15px" }}>
              <label>Emirates ID File</label>
              <input type="file" name="OwnerEID" onChange={(e) => handleFileChange(e, "eid")} /><img src={`http://localhost:5000/owner_image/${owner_code}/eid`} alt="Owner EID" />
              {eidPreview && <img src={eidPreview} alt="EID Preview" style={{ display: "block", marginTop: "5px", maxWidth: "100px", maxHeight: "80px", border: "1px solid #ccc", borderRadius: "4px" }} />}
            </div>

            {/* Passport Copy File */}
            <div className="col-md-3" style={{ width: "25%", padding: "0 10px", boxSizing: "border-box", marginBottom: "15px" }}>
              <label>Passport Copy File</label>
              <input type="file" name="OwnerPassportCopy" onChange={(e) => handleFileChange(e, "passport")} />
              {passportPreview && <img src={passportPreview} alt="Passport Preview" style={{ display: "block", marginTop: "5px", maxWidth: "100px", maxHeight: "80px", border: "1px solid #ccc", borderRadius: "4px" }} />}
            </div>

            {/* Residence Visa File */}
            <div className="col-md-3" style={{ width: "25%", padding: "0 10px", boxSizing: "border-box", marginBottom: "15px" }}>
              <label>Residence Visa File</label><img src={`http://localhost:5000/owner_image/${owner_code}/passport`} alt="Passport" />
              <input type="file" name="OwnerResVisa" onChange={(e) => handleFileChange(e, "visa")} />
              {visaPreview && <img src={visaPreview} alt="Visa Preview" style={{ display: "block", marginTop: "5px", maxWidth: "100px", maxHeight: "80px", border: "1px solid #ccc", borderRadius: "4px" }} />}
            </div>
          </div>

          <div style={{ textAlign: "center" }}>
            <button type="submit" style={{ background: "#007BFF", color: "white", padding: "8px 16px", border: "none", borderRadius: "4px", cursor: "pointer", fontSize: "0.85rem", marginTop: "10px" }}>
              Update Owner
            </button>
          </div>
        </form>
      )}
    </div>
  );
};

export default ModifyOwner;
