import React, { useEffect, useState, useRef } from "react";

const OwnersTable = () => {
  const [owners, setOwners] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [searchQuery, setSearchQuery] = useState("");
  const [editOwner, setEditOwner] = useState(null);
  const editRowRef = useRef(null); // ‚úÖ Ref for smooth scroll

  useEffect(() => {
    fetchOwners();
  }, []);

  useEffect(() => {
    // Scroll to the edit row when editOwner changes
    if (editRowRef.current) {
      editRowRef.current.scrollIntoView({ behavior: "smooth", block: "center" });
    }
  }, [editOwner]);

  const fetchOwners = () => {
    setLoading(true);
    fetch("http://localhost:5000/owners_list", { credentials: "include" })
      .then((res) => res.json())
      .then((data) => {
        if (data.success) setOwners(data.owners);
        else setError(data.message || "Failed to fetch owners");
        setLoading(false);
      })
      .catch((err) => {
        console.error(err);
        setError("Error fetching owners");
        setLoading(false);
      });
  };

  const handleDelete = async (owner_code) => {
    if (!window.confirm("Are you sure you want to delete this owner?")) return;
    try {
      const res = await fetch(`http://localhost:5000/delete_owner`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ owner_code }),
      });
      const data = await res.json();
      if (data.success) {
        alert("Owner deleted successfully");
        fetchOwners();
      } else {
        alert(data.message || "Failed to delete owner");
      }
    } catch (err) {
      console.error(err);
      alert("Error deleting owner");
    }
  };

  const filteredOwners = owners.filter((owner) =>
    `${owner.first_name} ${owner.last_name} ${owner.email} ${owner.mobile_number} ${owner.nationality}`
      .toLowerCase()
      .includes(searchQuery.toLowerCase())
  );

  if (loading) return <p>Loading owners...</p>;
  if (error) return <p>{error}</p>;

  const currentDate = new Date().toLocaleDateString();

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-between align-items-center mb-3 flex-wrap">
        <div>
          <span>{currentDate}</span> | <span>Total Owners: {filteredOwners.length}</span>
        </div>
        <h3 className="text-center mb-0">List of Owners</h3>
        <a href="/index" className="btn btn-link">‚Üê Go Back Home</a>
      </div>

      <div className="mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Search owner..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
      </div>

      <div className="table-responsive">
        <table className="table table-bordered table-hover">
          <thead className="thead-dark">
            <tr>
              <th>Owner Code</th>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Email</th>
              <th>Mobile</th>
              <th>Nationality</th>
              <th>EID</th>
              <th>Passport</th>
              <th>Visa</th>
              <th>Edit</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody>
            {filteredOwners.map((owner) => (
              <React.Fragment key={owner.owner_code}>
                <tr
                  className={editOwner?.owner_code === owner.owner_code ? "table-warning" : ""}
                  ref={editOwner?.owner_code === owner.owner_code ? editRowRef : null} // ‚úÖ attach ref
                >
                  <td>{`OW${owner.owner_code.toString().padStart(5, "0")}`}</td>
                  <td>{owner.first_name}</td>
                  <td>{owner.last_name}</td>
                  <td>{owner.email}</td>
                  <td>{owner.mobile_number}</td>
                  <td>{owner.nationality}</td>
                  <td>{owner.eid_image ? <img src={`data:image/*;base64,${owner.eid_image}`} style={{ maxHeight: "60px" }} className="img-thumbnail" alt="EID" /> : "-"}</td>
                  <td>{owner.passport_copy ? <img src={`data:image/*;base64,${owner.passport_copy}`} style={{ maxHeight: "60px" }} className="img-thumbnail" alt="Passport" /> : "-"}</td>
                  <td>{owner.res_visa ? <img src={`data:image/*;base64,${owner.res_visa}`} style={{ maxHeight: "60px" }} className="img-thumbnail" alt="Visa" /> : "-"}</td>
                  <td><button className="btn btn-sm btn-primary" onClick={() => setEditOwner(owner)}>‚úè Edit</button></td>
                  <td><button className="btn btn-sm btn-danger" onClick={() => handleDelete(owner.owner_code)}>üóë Delete</button></td>
                </tr>

                {editOwner?.owner_code === owner.owner_code && (
                  <tr>
                    <td colSpan="11">
                      <EditOwnerForm owner={editOwner} onClose={() => setEditOwner(null)} onSave={fetchOwners} />
                    </td>
                  </tr>
                )}
              </React.Fragment>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default OwnersTable;
