import React, { useEffect, useState } from "react";

const OwnersTable = () => {
  const [owners, setOwners] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [searchQuery, setSearchQuery] = useState("");
  const [editOwner, setEditOwner] = useState(null);

  useEffect(() => {
    fetchOwners();
  }, []);

  const fetchOwners = () => {
    setLoading(true);
    fetch("http://localhost:5000/owners_list", { credentials: "include" })
      .then((res) => res.json())
      .then((data) => {
        if (data.success) setOwners(data.owners);
        else setError(data.message || "Failed to fetch owners");
        setLoading(false);
      })
      .catch((err) => {
        console.error(err);
        setError("Error fetching owners");
        setLoading(false);
      });
  };

  const handleDelete = async (owner_code) => {
    if (!window.confirm("Are you sure you want to delete this owner?")) return;
    try {
      const res = await fetch(`http://localhost:5000/delete_owner`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ owner_code }),
      });
      const data = await res.json();
      if (data.success) {
        alert("Owner deleted successfully");
        fetchOwners();
      } else {
        alert(data.message || "Failed to delete owner");
      }
    } catch (err) {
      console.error(err);
      alert("Error deleting owner");
    }
  };

  const filteredOwners = owners.filter((owner) =>
    `${owner.first_name} ${owner.last_name} ${owner.email} ${owner.mobile_number} ${owner.nationality}`
      .toLowerCase()
      .includes(searchQuery.toLowerCase())
  );

  if (loading) return <p>Loading owners...</p>;
  if (error) return <p>{error}</p>;

  const currentDate = new Date().toLocaleDateString();

  return (
<div className="container mt-4">
  {/* Header with Date, Total Owners, and Back button */}
  <div className="d-flex justify-content-between align-items-center mb-3 flex-wrap">
    <div>
      <span>{currentDate}</span> | <span>Total Owners: {filteredOwners.length}</span>
    </div>
    <h3 className="text-center mb-0">List of Owners</h3>
    <a href="/index" className="btn btn-link">‚Üê Go Back Home</a>
  </div>

  {/* Search Bar */}
  <div className="mb-3">
    <input
      type="text"
      className="form-control"
      placeholder="Search owner..."
      value={searchQuery}
      onChange={(e) => setSearchQuery(e.target.value)}
    />
  </div>

  {/* Owners Table */}
  <div className="table-responsive">
    <table className="table table-bordered table-hover">
      <thead className="thead-dark">
        <tr>
          <th>Owner Code</th>
          <th>First Name</th>
          <th>Last Name</th>
          <th>Email</th>
          <th>Mobile</th>
          <th>Nationality</th>
          <th>EID</th>
          <th>Passport</th>
          <th>Visa</th>
          <th>Edit</th>
          <th>Delete</th>
        </tr>
      </thead>
      <tbody>
        {filteredOwners.map((owner) => (
          <React.Fragment key={owner.owner_code}>
            <tr>
              <td>{`OW${owner.owner_code.toString().padStart(5, "0")}`}</td>
              <td>{owner.first_name}</td>
              <td>{owner.last_name}</td>
              <td>{owner.email}</td>
              <td>{owner.mobile_number}</td>
              <td>{owner.nationality}</td>
              <td>{owner.eid_image ? <img src={`data:image/*;base64,${owner.eid_image}`} style={{ maxHeight: "60px" }} className="img-thumbnail" alt="EID" /> : "-"}</td>
              <td>{owner.passport_copy ? <img src={`data:image/*;base64,${owner.passport_copy}`} style={{ maxHeight: "60px" }} className="img-thumbnail" alt="Passport" /> : "-"}</td>
              <td>{owner.res_visa ? <img src={`data:image/*;base64,${owner.res_visa}`} style={{ maxHeight: "60px" }} className="img-thumbnail" alt="Visa" /> : "-"}</td>
              <td><button className="btn btn-sm btn-primary" onClick={() => setEditOwner(owner)}>‚úè Edit</button></td>
              <td><button className="btn btn-sm btn-danger" onClick={() => handleDelete(owner.owner_code)}>üóë Delete</button></td>
            </tr>

            {editOwner?.owner_code === owner.owner_code && (
              <tr>
                <td colSpan="11">
                  <EditOwnerForm owner={editOwner} onClose={() => setEditOwner(null)} onSave={fetchOwners} />
                </td>
              </tr>
            )}
          </React.Fragment>
        ))}
      </tbody>
    </table>
  </div>
</div>
  );
};

// ------------------- Inline Edit Form (Bootstrap 4) -------------------
const EditOwnerForm = ({ owner, onClose, onSave }) => {
  const formatDate = (dateStr) => dateStr ? new Date(dateStr).toISOString().split("T")[0] : "";

  const [formData, setFormData] = useState({
    ...owner,
    date_of_birth: formatDate(owner.date_of_birth),
    visa_expiry: formatDate(owner.visa_expiry),
    passport_expiry: formatDate(owner.passport_expiry),
  });

  const [previews, setPreviews] = useState({
    eid_image: owner.eid_image || null,
    passport_copy: owner.passport_copy || null,
    res_visa: owner.res_visa || null,
  });

  const [files, setFiles] = useState({ eid_image: null, passport_copy: null, res_visa: null });

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (["date_of_birth", "visa_expiry", "passport_expiry"].includes(name)) {
      const iso = new Date(value).toISOString().split("T")[0];
      setFormData((prev) => ({ ...prev, [name]: iso }));
    } else setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e, key) => {
    const file = e.target.files[0];
    if (!file) return;
    setFiles((prev) => ({ ...prev, [key]: file }));

    const reader = new FileReader();
    reader.onload = (ev) => setPreviews((prev) => ({ ...prev, [key]: ev.target.result }));
    reader.readAsDataURL(file);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    for (let key in formData) data.append(key, formData[key]);
    if (files.eid_image) data.append("OwnerEID", files.eid_image);
    if (files.passport_copy) data.append("OwnerPassportCopy", files.passport_copy);
    if (files.res_visa) data.append("OwnerResVisa", files.res_visa);
    data.append("owner_code", owner.owner_code);

    try {
      const res = await fetch("http://localhost:5000/update_owner", {
        method: "POST",
        credentials: "include",
        body: data,
      });
      const result = await res.json();
      if (result.success) {
        alert("Owner updated successfully");
        onSave();
        onClose();
      } else alert(result.message || "Update failed");
    } catch (err) {
      console.error(err);
      alert("Error updating owner");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="border p-3 rounded bg-white">
      <div className="form-row">
        <div className="form-group col-md-3">
          <label>First Name</label>
          <input className="form-control" name="first_name" value={formData.first_name} onChange={handleChange} />
        </div>
        <div className="form-group col-md-3">
          <label>Last Name</label>
          <input className="form-control" name="last_name" value={formData.last_name} onChange={handleChange} />
        </div>
        <div className="form-group col-md-3">
          <label>Email</label>
          <input className="form-control" type="email" name="email" value={formData.email} onChange={handleChange} />
        </div>
        <div className="form-group col-md-3">
          <label>Mobile</label>
          <input className="form-control" name="mobile_number" value={formData.mobile_number} onChange={handleChange} />
        </div>
      </div>

      <div className="form-row">
        <div className="form-group col-md-3">
          <label>Nationality</label>
          <input className="form-control" name="nationality" value={formData.nationality} onChange={handleChange} />
        </div>
        <div className="form-group col-md-3">
          <label>Address1</label>
          <input className="form-control" name="address1" value={formData.address1 || ""} onChange={handleChange} />
        </div>
        <div className="form-group col-md-3">
          <label>Address2</label>
          <input className="form-control" name="address2" value={formData.address2 || ""} onChange={handleChange} />
        </div>
        <div className="form-group col-md-3">
          <label>City</label>
          <input className="form-control" name="city" value={formData.city || ""} onChange={handleChange} />
        </div>
      </div>

      <div className="form-row">
        <div className="form-group col-md-3">
          <label>State</label>
          <input className="form-control" name="state" value={formData.state || ""} onChange={handleChange} />
        </div>
        <div className="form-group col-md-3">
          <label>Country</label>
          <input className="form-control" name="country" value={formData.country || ""} onChange={handleChange} />
        </div>
        <div className="form-group col-md-3">
          <label>Date of Birth</label>
          <input className="form-control" type="date" name="date_of_birth" value={formData.date_of_birth} onChange={handleChange} />
        </div>
        <div className="form-group col-md-3">
          <label>Visa Expiry</label>
          <input className="form-control" type="date" name="visa_expiry" value={formData.visa_expiry} onChange={handleChange} />
        </div>
      </div>

      <div className="form-row">
        <div className="form-group col-md-3">
          <label>Passport Expiry</label>
          <input className="form-control" type="date" name="passport_expiry" value={formData.passport_expiry} onChange={handleChange} />
        </div>
        <div className="form-group col-md-3">
          <label>EID Image</label>
          <input type="file" className="form-control-file" onChange={(e) => handleFileChange(e, "eid_image")} />
          {previews.eid_image && <img src={previews.eid_image} className="img-thumbnail mt-1" style={{ maxHeight: "80px" }} alt="EID" />}
        </div>
        <div className="form-group col-md-3">
          <label>Passport Copy</label>
          <input type="file" className="form-control-file" onChange={(e) => handleFileChange(e, "passport_copy")} />
          {previews.passport_copy && <img src={previews.passport_copy} className="img-thumbnail mt-1" style={{ maxHeight: "80px" }} alt="Passport" />}
        </div>
        <div className="form-group col-md-3">
          <label>Residence Visa</label>
          <input type="file" className="form-control-file" onChange={(e) => handleFileChange(e, "res_visa")} />
          {previews.res_visa && <img src={previews.res_visa} className="img-thumbnail mt-1" style={{ maxHeight: "80px" }} alt="Visa" />}
        </div>
      </div>

      <div className="text-center mt-2">
        <button type="submit" className="btn btn-success mr-2">Save</button>
        <button type="button" className="btn btn-secondary" onClick={onClose}>Cancel</button>
      </div>
    </form>
  );
};

export default OwnersTable;
