import React, { useEffect, useState } from "react";

const OwnersTable = () => {
  const [owners, setOwners] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [searchQuery, setSearchQuery] = useState("");
  const [editOwner, setEditOwner] = useState(null);

  useEffect(() => {
    fetchOwners();
  }, []);

  const fetchOwners = () => {
    setLoading(true);
    fetch("http://localhost:5000/owners_list", { credentials: "include" })
      .then((res) => res.json())
      .then((data) => {
        if (data.success) setOwners(data.owners);
        else setError(data.message || "Failed to fetch owners");
        setLoading(false);
      })
      .catch((err) => {
        console.error(err);
        setError("Error fetching owners");
        setLoading(false);
      });
  };

  const handleDelete = async (owner_code) => {
    if (!window.confirm("Are you sure you want to delete this owner?")) return;

    try {
      const res = await fetch(`http://localhost:5000/delete_owner`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ owner_code }),
      });
      const data = await res.json();
      if (data.success) {
        alert("Owner deleted successfully");
        fetchOwners();
      } else {
        alert(data.message || "Failed to delete owner");
      }
    } catch (err) {
      console.error(err);
      alert("Error deleting owner");
    }
  };

  const filteredOwners = owners.filter((owner) =>
    `${owner.first_name} ${owner.last_name} ${owner.email} ${owner.mobile_number} ${owner.nationality}`
      .toLowerCase()
      .includes(searchQuery.toLowerCase())
  );

  if (loading) return <p>Loading owners...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div style={{ padding: "20px" }}>
      <input
        type="text"
        placeholder="Search owner..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        style={{ padding: "8px", width: "300px", marginBottom: "10px" }}
      />

      <table border="1" cellPadding="8" style={{ borderCollapse: "collapse", minWidth: "100%" }}>
        <thead>
          <tr>
            <th>Owner Code</th>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Email</th>
            <th>Mobile</th>
            <th>Nationality</th>
            <th>Edit</th>
            <th>Delete</th>
          </tr>
        </thead>
        <tbody>
          {filteredOwners.map((owner) => (
            <React.Fragment key={owner.owner_code}>
              <tr>
                <td>{`OW${owner.owner_code.toString().padStart(5, "0")}`}</td>
                <td>{owner.first_name}</td>
                <td>{owner.last_name}</td>
                <td>{owner.email}</td>
                <td>{owner.mobile_number}</td>
                <td>{owner.nationality}</td>

                <td>
                  <button
                    onClick={() => setEditOwner(owner)}
                    style={{ padding: "5px 10px", backgroundColor: "blue", color: "white", border: "none", cursor: "pointer" }}
                  >
                    ‚úè Edit
                  </button>
                </td>

                <td>
                  <button
                    onClick={() => handleDelete(owner.owner_code)}
                    style={{ padding: "5px 10px", backgroundColor: "red", color: "white", border: "none", cursor: "pointer" }}
                  >
                    üóë Delete
                  </button>
                </td>
              </tr>

              {/* Inline Edit Form */}
              {editOwner?.owner_code === owner.owner_code && (
                <tr>
                  <td colSpan="8">
                    <EditOwnerForm
                      owner={editOwner}
                      onClose={() => setEditOwner(null)}
                      onSave={fetchOwners}
                    />
                  </td>
                </tr>
              )}
            </React.Fragment>
          ))}
        </tbody>
      </table>
    </div>
  );
};

// ------------------- Inline Edit Form -------------------
const EditOwnerForm = ({ owner, onClose, onSave }) => {
  const [formData, setFormData] = useState({ ...owner });
  const [files, setFiles] = useState({ eid_image: null, passport_copy: null, res_visa: null });

const handleChange = (e) => {
  const { name, value } = e.target;

  // If it's a date input, convert to YYYY-MM-DD
  if (name === "date_of_birth" || name === "visa_expiry" || name === "passport_expiry") {
    const date = new Date(value);
    const isoDate = date.toISOString().split("T")[0]; // "YYYY-MM-DD"
    setFormData((prev) => ({ ...prev, [name]: isoDate }));
  } else {
    setFormData((prev) => ({ ...prev, [name]: value }));
  }
};

  const handleFileChange = (e) => {
    const { name, files: fileList } = e.target;
    setFiles((prev) => ({ ...prev, [name]: fileList[0] }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    for (let key in formData) data.append(key, formData[key]);
    for (let key in files) if (files[key]) data.append(key === "eid_image" ? "OwnerEID" : key === "passport_copy" ? "OwnerPassportCopy" : "OwnerResVisa", files[key]);
    data.append("owner_code", owner.owner_code);

    try {
      const res = await fetch("http://localhost:5000/update_owner", {
        method: "POST",
        credentials: "include",
        body: data,
      });
      const result = await res.json();
      if (result.success) {
        alert("Owner updated successfully");
        onSave();
        onClose();
      } else {
        alert(result.message || "Update failed");
      }
    } catch (err) {
      console.error(err);
      alert("Error updating owner");
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ background: "#f9f9f9", padding: "10px", borderRadius: "6px" }}>
      <div style={{ display: "flex", flexWrap: "wrap", gap: "10px" }}>
        <input name="first_name" value={formData.first_name} onChange={handleChange} placeholder="First Name" />
        <input name="last_name" value={formData.last_name} onChange={handleChange} placeholder="Last Name" />
        <input name="email" value={formData.email} onChange={handleChange} placeholder="Email" />
        <input name="mobile_number" value={formData.mobile_number} onChange={handleChange} placeholder="Mobile" />
        <input name="nationality" value={formData.nationality} onChange={handleChange} placeholder="Nationality" />
        <input name="address1" value={formData.address1 || ""} onChange={handleChange} placeholder="Address1" />
        <input name="address2" value={formData.address2 || ""} onChange={handleChange} placeholder="Address2" />
        <input name="city" value={formData.city || ""} onChange={handleChange} placeholder="City" />
        <input name="state" value={formData.state || ""} onChange={handleChange} placeholder="State" />
        <input name="country" value={formData.country || ""} onChange={handleChange} placeholder="Country" />

        <div>
          <label>EID Image:</label>
          <input type="file" name="eid_image" onChange={handleFileChange} />
        </div>
        <div>
          <label>Passport Copy:</label>
          <input type="file" name="passport_copy" onChange={handleFileChange} />
        </div>
        <div>
          <label>Residence Visa:</label>
          <input type="file" name="res_visa" onChange={handleFileChange} />
        </div>
      </div>

      <div style={{ marginTop: "10px" }}>
        <button type="submit" style={{ marginRight: "10px" }}>Save</button>
        <button type="button" onClick={onClose}>Cancel</button>
      </div>
    </form>
  );
};

export default OwnersTable;
